    border: none;
    color: #fff;
    font-size: 24px;
    margin-left: auto;
}

.primary-menu {
    margin-left: auto;
}

.primary-menu .menu {
    list-style: none;
    margin: 0;
    padding: 0;
    display: flex;
}

.primary-menu .menu > li {
    position: relative;
}

.primary-menu .menu > li > a {
    color: #fff;
    padding: 10px 15px;
    display: block;
}

.primary-menu .menu > li > a:hover {
    background-color: #444;
}

.primary-menu .menu li ul {
    display: none;
    position: absolute;
    background-color: #333;
    top: 100%;
    left: 0;
    list-style: none;
    padding: 0;
    margin: 0;
}

.primary-menu .menu li:hover > ul {
