    flex-direction: column;
}

.mobile-menu .menu {
    flex-direction: column;
}

.mobile-menu .menu > li {
    width: 100%;
}

.mobile-menu .menu li ul {
    position: static;
}

.mobile-menu-toggle[aria-expanded="true"] + .mobile-menu {
    display: flex;
}

@media (max-width: 768px) {
