import itertools

# Represent polynomials as list of coefficients mod3 [c0,c1,...]
def poly_mod(p):
    return [c%3 for c in p]

def poly_add(a,b):
    n=max(len(a),len(b))
    return poly_mod([(a[i] if i<len(a) else 0)+(b[i] if i<len(b) else 0) for i in range(n)])

def poly_mul(a,b):
    res=[0]*(len(a)+len(b)-1)
    for i,ai in enumerate(a):
        for j,bj in enumerate(b):
            res[i+j]+=ai*bj
    return poly_mod(res)

def trim(p):
    while p and p[-1]==0:
        p.pop()
    return p

# modulus polynomial f = x^4 + x^2 + 2x +1
f=[1,2,1,0,1] # constant up order 4? Wait polynomial: 1 +2x + x^2 +0x^3 + x^4
# Actually index 0 constant 1, index 1 coefficient 2, index2 1, index3 0, index4 1
def poly_mod_f(p):
    p=p.copy()
    while len(p)>4:
        k=len(p)-1
        coeff=p[-1]%3
        if coeff!=0:
            # subtract coeff* x^{k-4} f
            # multiply f shifted by (k-4)
            for i,fi in enumerate(f):
                idx=i+k-4
                p[idx]-= coeff*fi
        p.pop()
        p=poly_mod(p)
    return trim(p)

def poly_pow(base, exponent):
    result=[1] # one
    b=base
    e=exponent
    while e:
        if e&1:
            result=poly_mod_f(poly_mul(result,b))
        b=poly_mod_f(poly_mul(b,b))
        e>>=1
    return result

alpha=[0,1] # x
alpha8=poly_pow(alpha,8)
alpha16=poly_pow(alpha,16)
alpha8,alpha16

