import math
def prod_odd_factorials(n):
    p=1
    for k in range(1,n+1):
        p*=math.factorial(2*k-1)
    return p
sol=[]
for n in range(2,11):
    p=prod_odd_factorials(n)
    m=2
    while math.factorial(m)<p:
        m+=1
    if math.factorial(m)==p:
        sol.append((n,m))
sol

