<script>
  (function () {
    const form = document.getElementById('contactForm');
    const statusBox = document.getElementById('formStatus');
    const msg = document.getElementById('message');
    const counter = document.getElementById('charCount');

    const radioOther = document.getElementById('programmeOther');
    const otherText  = document.getElementById('programmeOtherText');
    const phone      = document.getElementById('phone');

    // --- Character counter ---
    function updateCount() { counter.textContent = String(msg.value.length); }
    msg.addEventListener('input', updateCount);
    updateCount();

    // --- "Other" toggle (robust) ---
    function syncOther() {
      const on = !!(radioOther && radioOther.checked);
      if (!otherText) return;
      otherText.disabled = !on;
      otherText.readOnly = !on;
      otherText.setAttribute('aria-disabled', String(!on));
      otherText.classList.toggle('is-disabled', !on);
      otherText.required = on; // only required if "Other" is selected
      if (on) otherText.focus();
      if (!on) otherText.value = '';
    }
    form.querySelectorAll('input[name="programme"]').forEach(r => {
      r.addEventListener('change', syncOther);
    });
    syncOther();

    // --- Phone number rules (Singapore only) ---
    function normalizePhone(showAlerts) {
      if (!phone) return true;
      let v = phone.value.trim();
      phone.setCustomValidity(''); // reset any previous error
      if (!v) return true;         // empty is allowed; field is optional

      // If it starts with '+'
      if (v.startsWith('+')) {
        if (!v.startsWith('+65')) {
          const msg = 'Only Singapore numbers allowed';
          phone.setCustomValidity(msg);
          if (showAlerts) alert(msg);
          phone.reportValidity();
          return false;
        }
        // ensure a single space after +65
        v = v.replace(/^\+65\s*/, '+65 ');
        phone.value = v;
        return true;
      }

      // No leading '+': auto‑prefix with "+65 "
      // If user typed an accidental leading "65", we still add "+65 " in front
      v = v.replace(/^\s+/, '');
      phone.value = '+65 ' + v;
      return true;
    }
    phone && phone.addEventListener('blur', () => normalizePhone(true));

    // --- Progressive enhancement: AJAX submit to Formspree ---
    form.addEventListener('submit', async (e) => {
      // First, normalize + validate phone
      if (!normalizePhone(true)) { e.preventDefault(); return; }

      // Let native HTML5 validation run
      if (!form.checkValidity()) return;
      e.preventDefault();

      statusBox.className = 'form-alert'; // reset classes
      statusBox.style.display = 'none';

      try {
        const resp = await fetch(form.action, {
          method: 'POST',
          body: new FormData(form),
          headers: { 'Accept': 'application/json' }
        });

        if (resp.ok) {
          form.reset();
          updateCount();
          syncOther();
          statusBox.textContent = 'Thanks! Your message has been sent successfully.';
          statusBox.classList.add('form-alert--success');
          statusBox.style.display = 'block';
        } else {
          const data = await resp.json().catch(() => ({}));
          const err = (data.errors && data.errors.map(e => e.message).join(' ')) ||
                      'Sorry, something went wrong. Please try again later.';
          statusBox.textContent = err;
          statusBox.classList.add('form-alert--error');
          statusBox.style.display = 'block';
        }
      } catch {
        statusBox.textContent = 'Network error. Please check your connection and try again.';
        statusBox.classList.add('form-alert--error');
        statusBox.style.display = 'block';
      }
    });
  })();
</script>
