/*!
 * tuition-js-1.0.3.js
 * Version: 1.0.3
 *
 * NOTES:
 * • Keep /assets/js/, /assets/css/ and /assets/data/ crawlable; these headers do not block them.
 * • Formspree posting is allowed by form-action. No third-party fetches are permitted (connect-src 'self').
 * -------------------------------------------------------------------------
 */

(() => {
  "use strict";

  /* =========================
   * Config & element caches
   * ========================= */
  const DESKTOP_BP = 1200;
  const isDesktop = () => window.innerWidth >= DESKTOP_BP;
  const isMobile = () => !isDesktop();

  const docEl = document.documentElement;
  const body = document.body;

  const header = document.querySelector(".header");
  const desktopNav = header?.querySelector(".nav"); // source of truth
  const desktopList = desktopNav?.querySelector("ul");

  const hamburger = document.querySelector(".hamburger");
  const overlay = document.querySelector(".overlay");
  const mobileMenu =
    document.getElementById("mobile-menu") ||
    document.querySelector(".mobile-menu");

  const themeToggle = document.querySelector(".theme-toggle");
  const mainContent = document.getElementById("main-content");

  /* =========================
   * Security helpers (NEW)
   * ========================= */
  const ALLOWED_PROTOCOLS = new Set(["http:", "https:", "mailto:", "tel:"]);

  function sanitizeHref(raw) {
    if (!raw) return null;
    const s = String(raw).trim();
    // In-page anchors are fine
    if (s.startsWith("#")) return s;
    // Disallow obvious script/data URIs early
    const lower = s.toLowerCase();
    if (lower.startsWith("javascript:") || lower.startsWith("data:"))
      return null;

    try {
      const base = document.baseURI || location.href; // robust base
      const u = new URL(s, base); // supports relative anywhere
      if (u.origin === location.origin) return u.href; // same-origin
      if (ALLOWED_PROTOCOLS.has(u.protocol)) return u.href; // allowed cross-origin schemes
    } catch (_) {
      // fall through
    }
    return null; // drop unsafe/malformed URLs
  }

  function sanitizeImgSrc(raw) {
    const safe = sanitizeHref(raw);
    if (!safe) return null;
    try {
      const u = new URL(safe);
      // Images: keep same-origin only (tighten as a policy)
      return u.origin === location.origin ? u.href : null;
    } catch (_) {
      return null;
    }
  }

  /* =========================
   * Misc helpers
   * ========================= */
  function slugify(str, fallback = "menu") {
    if (!str) return fallback;
    const s = String(str)
      .toLowerCase()
      .replace(/[^a-z0-9]+/g, "-")
      .replace(/(^-|-$)/g, "");
    return s || fallback;
  }

  // Robust focusability check (works with transforms/fixed elements)
  function isActuallyFocusable(el) {
    if (!el || el.disabled) return false;
    return el.getClientRects().length > 0;
  }

  function trapFocus(container, onClose) {
    const selectors = [
      "a[href]",
      "button",
      "input",
      "select",
      "textarea",
      '[tabindex]:not([tabindex="-1"])',
    ];
    const getFocusables = () =>
      Array.from(container.querySelectorAll(selectors.join(","))).filter(
        isActuallyFocusable
      );

    function onKeydown(e) {
      if (e.key === "Escape") {
        onClose();
        return;
      }
      if (e.key !== "Tab") return;

      const els = getFocusables();
      if (!els.length) return;

      const first = els[0];
      const last = els[els.length - 1];
      const active = document.activeElement;

      if (e.shiftKey && active === first) {
        e.preventDefault();
        last.focus();
      } else if (!e.shiftKey && active === last) {
        e.preventDefault();
        first.focus();
      }
    }

    container.addEventListener("keydown", onKeydown);
    return () => container.removeEventListener("keydown", onKeydown);
  }

  function updateFooterYear() {
    const y = String(new Date().getFullYear());
    const yearEl = document.getElementById("year");
    if (yearEl) {
      yearEl.textContent = y;
      return;
    }
    const footer = document.querySelector(".footer");
    if (footer) {
      footer.innerHTML = footer.innerHTML.replace(/\b(19|20)\d{2}\b/, y);
    }
  }

  /* =========================
   * Single-source nav builder
   * =========================
   * Builds the mobile drawer menu from the desktop <nav> list.
   * - Clears any existing mobile menu markup
   * - Clones and rewrites dropdown IDs -> unique "-m" suffix
   * - Normalizes ARIA for mobile (aria-expanded="false" by default)
   */
  function buildMobileMenuFromDesktop() {
    if (!mobileMenu || !desktopList) return;

    // Clear any duplicated markup (single source of truth)
    mobileMenu.innerHTML = "";
    const ul = document.createElement("ul");

    const liNodes = Array.from(desktopList.children); // <li.nav-item>…

    liNodes.forEach((srcItem, index) => {
      const item = srcItem.cloneNode(true);

      // Ensure top-level state is reset for mobile
      item.classList.remove("open", "force-close");

      // If the item contains a dropdown, make ID unique for mobile
      const dropdown = item.querySelector(".dropdown");
      const trigger = item.querySelector(":scope > .nav-link");

      if (dropdown) {
        // Determine a stable base id from original id or the button text
        const baseId =
          dropdown.id ||
          `dd-${slugify(trigger?.textContent || `section-${index + 1}`)}`;
        const mobileId = `${baseId}-m`;

        dropdown.id = mobileId;

        if (trigger) {
          trigger.setAttribute("aria-controls", mobileId);
          trigger.setAttribute("aria-expanded", "false");
          // Ensure the trigger remains a button
          if (trigger.tagName !== "BUTTON") {
            const btn = document.createElement("button");
            btn.className = trigger.className;
            btn.type = "button";
            btn.setAttribute("aria-haspopup", "true");
            btn.setAttribute("aria-expanded", "false");
            btn.setAttribute("aria-controls", mobileId);
            btn.innerHTML = trigger.innerHTML;
            trigger.replaceWith(btn);
          }
        }
      } else if (trigger) {
        // Non-dropdown accidentally authored as <button>? Replace with the desktop link if present.
        const desktopLink = srcItem.querySelector(":scope > a.nav-link");
        if (desktopLink) {
          const a = desktopLink.cloneNode(true);
          trigger.replaceWith(a);
        }
      }

      ul.appendChild(item);
    });

    mobileMenu.appendChild(ul);
  }

  /* =========================
   * Mobile menu behavior
   * ========================= */
  let undoTrap = null;

  function resetMobileSubmenus() {
    if (!mobileMenu) return;
    mobileMenu.querySelectorAll(".nav-item").forEach((item) => {
      item.classList.remove("open");
      const btn = item.querySelector(":scope > button.nav-link");
      if (btn) btn.setAttribute("aria-expanded", "false");
    });
  }

  function openMobileMenu() {
    if (!mobileMenu || !overlay || !hamburger) return;

    mobileMenu.classList.add("active");
    overlay.classList.add("active");
    mobileMenu.setAttribute("aria-hidden", "false");
    overlay.setAttribute("aria-hidden", "false");
    hamburger.setAttribute("aria-expanded", "true");
    hamburger.setAttribute("aria-label", "Close menu");
    body.style.overflow = "hidden";

    if (mainContent) {
      try {
        mainContent.inert = true;
      } catch (_e) { }
    }

    if (undoTrap) undoTrap();
    undoTrap = trapFocus(mobileMenu, closeMobileMenu);

    // Focus first interactive element for keyboard users
    setTimeout(() => {
      const first = mobileMenu.querySelector(
        'a,button,input,select,textarea,[tabindex]:not([tabindex="-1"])'
      );
      first?.focus();
    }, 0);
  }

  function closeMobileMenu() {
    if (!mobileMenu || !overlay || !hamburger) return;

    mobileMenu.classList.remove("active");
    overlay.classList.remove("active");
    mobileMenu.setAttribute("aria-hidden", "true");
    overlay.setAttribute("aria-hidden", "true");
    hamburger.setAttribute("aria-expanded", "false");
    hamburger.setAttribute("aria-label", "Open menu");
    body.style.overflow = "";

    if (mainContent) {
      try {
        mainContent.inert = false;
      } catch (_e) { }
    }

    if (undoTrap) {
      undoTrap();
      undoTrap = null;
    }
    resetMobileSubmenus();
  }

  function initMobileMenuHandlers() {
    if (!hamburger || !mobileMenu || !overlay) return;

    // Toggle drawer
    hamburger.addEventListener("click", () => {
      const open = mobileMenu.classList.contains("active");
      if (open) closeMobileMenu();
      else openMobileMenu();
    });

    // Click away
    overlay.addEventListener("click", () => {
      closeMobileMenu();
      hamburger.focus();
    });

    // Delegate: toggle submenus (only on mobile)
    mobileMenu.addEventListener("click", (e) => {
      if (!isMobile()) return;
      const trigger = e.target.closest(".nav-item > button.nav-link");
      if (!trigger) return;

      e.preventDefault();
      const item = trigger.parentElement;
      const nowOpen = item.classList.toggle("open");
      trigger.setAttribute("aria-expanded", nowOpen ? "true" : "false");

      // Close siblings
      mobileMenu.querySelectorAll(".nav-item").forEach((sib) => {
        if (sib !== item) {
          sib.classList.remove("open");
          const btn = sib.querySelector(":scope > button.nav-link");
          if (btn) btn.setAttribute("aria-expanded", "false");
        }
      });
    });

    // Delegate: follow link then close drawer
    mobileMenu.addEventListener("click", (e) => {
      const a = e.target.closest("a[href]");
      if (!a) return;
      // Let navigation happen; close drawer immediately for perceived performance
      closeMobileMenu();
      hamburger.focus();
    });

    // ESC closes drawer globally
    document.addEventListener("keydown", (e) => {
      if (e.key === "Escape" && mobileMenu.classList.contains("active")) {
        closeMobileMenu();
        hamburger.focus();
      }
    });

    // Defensive: ensure closed on desktop
    let resizeTimeout;
    window.addEventListener("resize", () => {
      clearTimeout(resizeTimeout);
      resizeTimeout = setTimeout(() => {
        if (isDesktop()) closeMobileMenu();
      }, 100);
    });
  }

  /* =========================
   * Desktop dropdown behavior
   * ========================= */
  function initDesktopDropdowns() {
    const desktopItems = document.querySelectorAll(".nav .nav-item");
    desktopItems.forEach((item) => {
      const btn = item.querySelector('[aria-haspopup="true"]');
      if (!btn) return;

      const setExpanded = (state) =>
        btn.setAttribute("aria-expanded", state ? "true" : "false");

      item.addEventListener("mouseenter", () => {
        if (!isDesktop()) return;
        item.classList.remove("force-close"); // fix "stuck closed"
        setExpanded(true);
      });
      item.addEventListener("focusin", () => {
        if (!isDesktop()) return;
        item.classList.remove("force-close");
        setExpanded(true);
      });
      item.addEventListener("mouseleave", () => {
        if (!isDesktop()) return;
        setExpanded(false);
      });
      item.addEventListener("focusout", () => {
        if (!isDesktop()) return;
        setExpanded(false);
      });

      item.addEventListener("keydown", (e) => {
        if (!isDesktop()) return;
        if (e.key === "Escape") {
          setExpanded(false);
          item.classList.add("force-close");
          btn.focus();
        }
      });
    });
  }

  /* =========================
   * Theme (light/dark)
   * =========================
   * Uses <html data-theme="light|dark"> as the single source of truth.
   */
  let usingAuto = false;

  function prefersDark() {
    return (
      window.matchMedia &&
      window.matchMedia("(prefers-color-scheme: dark)").matches
    );
  }

  function setTheme(dark, { persist = true } = {}) {
    docEl.setAttribute("data-theme", dark ? "dark" : "light");

    // Remove any legacy body class to avoid confusion with CSS
    body.classList.remove("dark-mode");

    if (themeToggle) {
      themeToggle.innerHTML = dark ? "&#9788;" : "&#9790;"; // sun / moon
      themeToggle.setAttribute("aria-pressed", dark ? "true" : "false");
      const label = dark ? "Switch to light mode" : "Switch to dark mode";
      themeToggle.setAttribute("aria-label", label);
      themeToggle.title = label;
    }

    if (persist) {
      try {
        localStorage.setItem("theme", dark ? "dark" : "light");
      } catch (_e) { }
      usingAuto = false;
    }
  }

  function initTheme() {
    let saved = null;
    try {
      saved = localStorage.getItem("theme");
    } catch (_e) {
      saved = null;
    }

    if (saved === "dark") {
      setTheme(true, { persist: false });
    } else if (saved === "light") {
      setTheme(false, { persist: false });
    } else {
      usingAuto = true;
      setTheme(prefersDark(), { persist: false });

      const mql = window.matchMedia
        ? window.matchMedia("(prefers-color-scheme: dark)")
        : null;
      if (mql) {
        const onChange = (e) => {
          if (usingAuto) setTheme(e.matches, { persist: false });
        };
        try {
          mql.addEventListener("change", onChange);
        } catch {
          mql.addListener(onChange);
        } // Safari fallback
      }
    }

    if (themeToggle) {
      themeToggle.addEventListener("click", () => {
        const isDark = docEl.getAttribute("data-theme") === "dark";
        setTheme(!isDark, { persist: true });
      });
    }
  }

  /* =========================
   * ARIA & boot
   * ========================= */
  function initAriaStates() {
    // Hide drawer & overlay from SR until opened
    if (mobileMenu && !mobileMenu.hasAttribute("aria-hidden")) {
      mobileMenu.setAttribute("aria-hidden", "true");
    }
    if (overlay && !overlay.hasAttribute("aria-hidden")) {
      overlay.setAttribute("aria-hidden", "true");
    }
    if (hamburger) {
      hamburger.setAttribute("aria-expanded", "false");
      hamburger.setAttribute("aria-label", "Open menu");
      hamburger.setAttribute("aria-controls", "mobile-menu");
    }

    // Defensive: if authors use .no-js for CSS fallbacks
    docEl.classList.remove("no-js");
  }

  if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", onReady);
  } else {
    onReady();
  }

  /* =========================
   * Nav from JSON (desktop -> mobile)
   * ========================= */

  async function initMenuFromFile() {
    // If a server-rendered nav is present (future SSG/SSR), skip rebuilding.
    if (desktopNav?.dataset.menu === "ssr" && desktopList?.children.length)
      return;
    if (!desktopList) return;

    let items = null;
    try {
      // same-origin fetch; keep "no-cache" to revalidate
      items = await loadJSON("assets/data/menu.json", { cache: "no-cache" });
    } catch (e) {
      console.warn("Menu JSON failed to load; using HTML fallback", e);
      return; // fallback: leave existing HTML as-is
    }

    if (!Array.isArray(items) || items.length === 0) {
      console.warn("Menu JSON empty; using HTML fallback");
      return;
    }

    renderDesktopMenu(items);
  }

  function renderDesktopMenu(items) {
    // Build exactly the DOM your CSS/JS expect:
    // .nav > ul > li.nav-item > (button.nav-link + .dropdown) | a.nav-link
    desktopList.innerHTML = "";

    const usedIds = new Set();
    const uniqueId = (base) => {
      let id = base;
      let n = 2;
      while (usedIds.has(id)) id = `${base}-${n++}`;
      usedIds.add(id);
      return id;
    };

    items.forEach((node, i) => {
      const hasChildren =
        Array.isArray(node?.children) && node.children.length > 0;

      // We'll only append the <li> if it ends up with content.
      const li = document.createElement("li");
      li.className = "nav-item";

      // inside renderDesktopMenu(items)
      if (hasChildren) {
        const baseId = `dd-${slugify(node.label || `section-${i + 1}`)}`;
        const ddId = uniqueId(baseId);

        // NEW: prefer <a> if a safe href is provided
        const safeHref = sanitizeHref(node?.href);
        let trigger; // either <a.nav-link> or <button.nav-link>

        if (safeHref) {
          const a = document.createElement("a");
          a.className = "nav-link";
          a.href = safeHref;
          a.setAttribute("aria-haspopup", "true");
          a.setAttribute("aria-expanded", "false");
          a.setAttribute("aria-controls", ddId);
          a.append(document.createTextNode(String(node.label || "Menu")));
          const arrow = document.createElement("span");
          arrow.className = "arrow";
          arrow.innerHTML = "&#9662;";
          a.appendChild(arrow);
          trigger = a;
        } else {
          const btn = document.createElement("button");
          btn.className = "nav-link";
          btn.type = "button";
          btn.setAttribute("aria-haspopup", "true");
          btn.setAttribute("aria-expanded", "false");
          btn.setAttribute("aria-controls", ddId);
          btn.append(document.createTextNode(String(node.label || "Menu")));
          const arrow = document.createElement("span");
          arrow.className = "arrow";
          arrow.innerHTML = "&#9662;";
          btn.appendChild(arrow);
          trigger = btn;
        }

        const dd = document.createElement("div");
        dd.className = "dropdown";
        dd.id = ddId;
        dd.setAttribute("role", "group");
        dd.setAttribute("aria-label", String(node.label || "Submenu"));

        node.children.forEach((child) => {
          const safe = sanitizeHref(child?.href);
          if (!safe) return;
          const a = document.createElement("a");
          a.href = safe;
          a.textContent = String(child.label || child.href);
          dd.appendChild(a);
        });

        if (dd.children.length > 0) {
          li.append(trigger, dd);
          desktopList.appendChild(li);
        }
      } else {
        const safe = sanitizeHref(node?.href);
        if (!safe) return; // skip unsafe/malformed leaf

        const a = document.createElement("a");
        a.className = "nav-link";
        a.href = safe;
        a.textContent = String(node.label || node.href);
        li.appendChild(a);
        desktopList.appendChild(li);
      }
    });
  }

  async function onReady() {
    initAriaStates();

    // 1) Replace desktop menu from JSON (no-op if JSON fails or SSR present)
    await initMenuFromFile();

    // 2) Now build the mobile drawer from the (possibly rebuilt) desktop nav
    buildMobileMenuFromDesktop();

    // 3) Behaviors
    initMobileMenuHandlers();
    initDesktopDropdowns();
    initTheme();
    updateFooterYear();
    initTestimonialsFromFile();

    // Optional: surface CSP violations in the console during Report-Only
    window.addEventListener("securitypolicyviolation", (e) => {
      // Helpful while you tune CSP in Report-Only mode
      console.warn(
        "CSP violation:",
        e.violatedDirective,
        "blocked:",
        e.blockedURI
      );
    });
  }

  /* ===== Utilities ===== */
  async function loadJSON(url, { cache = "no-cache" } = {}) {
    const res = await fetch(url, { cache });
    if (!res.ok) throw new Error(`Failed to load ${url}: ${res.status}`);
    return res.json();
  }

  /* ===== Testimonials from external JSON ===== */
  async function initTestimonialsFromFile() {
    const root = document.getElementById("testimonials");
    if (!root) return;

    const url =
      root.getAttribute("data-src") || "assets/data/testimonials.json";
    let items = [];
    try {
      items = await loadJSON(url);
    } catch (e) {
      console.warn("Testimonials JSON failed to load:", e);
    }

    if (!Array.isArray(items) || items.length === 0) {
      root.style.display = "none";
      return;
    }
    renderTestimonials(root, items);
  }

  function renderTestimonials(root, items) {
    const track = root.querySelector("#tTrack");
    const dots = root.querySelector("#tDots");
    const btnPrev = root.querySelector(".t-btn--prev");
    const btnNext = root.querySelector(".t-btn--next");

    function scrollTestimonialsIntoView() {
      const target = root.querySelector(".t-viewport") || root;
      const headerEl = document.querySelector(".header");
      const headerH = headerEl ? headerEl.getBoundingClientRect().height : 0;
      const y =
        target.getBoundingClientRect().top + window.scrollY - headerH - 8;
      const reduce =
        window.matchMedia &&
        window.matchMedia("(prefers-reduced-motion: reduce)").matches;
      window.scrollTo({
        top: Math.max(0, y),
        behavior: reduce ? "auto" : "smooth",
      });
    }

    if (!track || !dots) return;

    // Reset DOM
    track.innerHTML = "";
    dots.innerHTML = "";

    // Build slides + dots
    const slideEls = items.map((t, i) => {
      const li = document.createElement("li");
      li.className = "t-slide";
      li.id = `t-slide-${i}`;
      li.setAttribute("role", "group");
      li.setAttribute("aria-roledescription", "slide");
      li.setAttribute("aria-label", `${i + 1} of ${items.length}`);

      // Card HTML without avatar; avatar inserted safely after.
      const nameSafe = escapeHTML(t.name || "Student");
      const taglineHtml = t.tagline
        ? `<div class="t-tagline">${escapeHTML(t.tagline)}</div>`
        : "";
      const quoteHtml = escapeHTMLPreserveNewlines(t.text || "");

      li.innerHTML = `
        <article class="t-card">
          <div class="t-headerline">
            <div class="t-avatar" aria-hidden="true" data-slot="avatar"></div>
            <h3 class="t-name">${nameSafe}</h3>
            ${taglineHtml}
          </div>
          <p class="t-quote">${quoteHtml}</p>
        </article>`;

      // Safe avatar injection
      const slot = li.querySelector('[data-slot="avatar"]');
      if (slot) {
        const initials = (t.name || "?").trim().slice(0, 2).toUpperCase();
        const safeSrc = t.avatar ? sanitizeImgSrc(t.avatar) : null;
        if (safeSrc) {
          const img = document.createElement("img");
          img.src = safeSrc;
          img.alt = "";
          img.loading = "lazy";
          img.decoding = "async";
          slot.appendChild(img);
        } else {
          slot.textContent = initials;
        }
      }

      track.appendChild(li);

      const dot = document.createElement("button");
      dot.className = "t-dot";
      dot.setAttribute("role", "tab");
      dot.setAttribute("aria-controls", li.id);
      dot.setAttribute("title", `Show testimonial ${i + 1}`);
      dot.addEventListener("click", () => {
        goTo(i);
        requestAnimationFrame(scrollTestimonialsIntoView);
      });

      dots.appendChild(dot);

      return li;
    });

    let index = 0;
    const count = slideEls.length;

    const reduceMotion =
      window.matchMedia &&
      window.matchMedia("(prefers-reduced-motion: reduce)").matches;
    const TRANS = reduceMotion
      ? "none"
      : "transform .35s ease, height .18s ease-out";

    function currentHeight() {
      const el = slideEls[index];
      return el ? el.offsetHeight : 0; // includes slide padding
    }

    function applyTransformAndHeight() {
      track.style.transform = `translateX(${-index * 100}%)`;
      const h = currentHeight();
      track.style.height = h ? `${h}px` : "";
    }

    function updateUI() {
      [...dots.children].forEach((d, i) => {
        d.setAttribute("aria-selected", String(i === index));
        d.tabIndex = i === index ? 0 : -1;
      });
      slideEls.forEach((el, i) => {
        el.setAttribute("aria-hidden", String(i !== index));
      });
      if (btnPrev) btnPrev.disabled = count < 2;
      if (btnNext) btnNext.disabled = count < 2;
    }

    function goTo(next) {
      const prev = index;
      const target = (next + count) % count;
      const isWrapForward = prev === count - 1 && target === 0; // last -> first
      const isWrapBackward = prev === 0 && target === count - 1; // first -> last

      if (isWrapForward || isWrapBackward) {
        // Avoid sweeping through all slides visually
        track.style.transition = "none";
        index = target;
        applyTransformAndHeight();
        // Force reflow, then restore transition
        void track.offsetWidth;
        if (!reduceMotion) track.style.transition = TRANS;
      } else {
        if (!reduceMotion) track.style.transition = TRANS;
        index = target;
        applyTransformAndHeight();
      }
      updateUI();
    }

    // Controls
    btnPrev?.addEventListener("click", () => goTo(index - 1));
    btnNext?.addEventListener("click", () => goTo(index + 1));

    // Keyboard arrows when focus is within the section
    root.addEventListener("keydown", (e) => {
      if (e.key === "ArrowLeft") {
        e.preventDefault();
        goTo(index - 1);
      }
      if (e.key === "ArrowRight") {
        e.preventDefault();
        goTo(index + 1);
      }
    });

    // Simple swipe
    let startX = null;
    track.addEventListener("pointerdown", (e) => {
      startX = e.clientX;
      track.setPointerCapture?.(e.pointerId);
    });
    track.addEventListener("pointerup", (e) => {
      if (startX === null) return;
      const dx = e.clientX - startX;
      startX = null;
      const threshold = Math.max(40, track.clientWidth * 0.07);
      if (dx > threshold) goTo(index - 1);
