  (function () {
    const form = document.getElementById("contactForm");
    const msg = document.getElementById("message");
    const counter = document.getElementById("charCount");
    const radioOther = document.getElementById("programmeOther");
    const otherText = document.getElementById("programmeOtherText");
    const phone = document.getElementById("phone");

    // Character counter
    function updateCount() {
      if (msg && counter) counter.textContent = String(msg.value.length);
    }
    msg && msg.addEventListener("input", updateCount);
