def compactness(Msun_mult, R_km):
    G=6.67430e-11
    c=299792458.0
    Msun=1.98847e30
    M=Msun_mult*Msun
    R=R_km*1000
    return G*M/(R*c**2)
for M,R in [(1.4,12),(2.0,11),(2.0,10),(1.2,13)]:
    print(M,R,compactness(M,R))

