    color: var(--primary);
    transition: color 0.3s;
    flex-shrink: 0;
    padding: 0;
    line-height: 1;
}
.hamburger {
    display: flex;
    flex-direction: column;
    justify-content: center;
    width: 36px;
    height: 24px;
    background: none;
    border: none;
    cursor: pointer;
    flex-shrink: 0;
    padding: 0;
}
.hamburger span {
    display: block;
    height: 3px;
    width: 100%;
    background: var(--primary);
    margin: 2px 0;
    border-radius: 2px;
    transition: background 0.3s;
}
:focus-visible {
    outline: 4px solid var(--primary);
    outline-offset: 0;
}
.nav {
    display: none;
    align-items: center;
}
.overlay {
    display: none;
    position: fixed;
    inset: 0;
    background: var(--overlay);
    z-index: 1100;
    transition: background 0.3s;
}
.overlay.active {
    display: block;
}
.mobile-menu {
    display: flex;
    flex-direction: column;
    position: fixed;
    top: 0;
    right: 0;
    width: 80vw;
    max-width: 340px;
    height: 100vh;
    background: var(--mobile-menu-bg);
    z-index: 1101;
    padding: 32px 0 0 0;
    box-shadow: -2px 0 16px rgba(0, 0, 0, 0.12);
    transform: translateX(100%);
