    overflow-y: auto;
}
.mobile-menu.active {
    transform: translateX(0);
}
.mobile-menu ul {
    list-style: none;
    padding: 0;
    margin: 0;
}
.mobile-menu .nav-item {
    border-bottom: 1px solid var(--divider);
    position: relative;
}
.mobile-menu .nav-link {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 18px 28px;
    color: var(--text);
    text-decoration: none;
    font-size: 1.1em;
    font-weight: 500;
    background: none;
    border: none;
    width: 100%;
