parts:
.section-title {
  max-width: min(1160px, 86vw);
  margin-inline: auto;
  font-weight: 800;
  font-size: clamp(1.5rem, 1.12rem + 1.8vw, 2.6rem);
  line-height: 1.2;
}

.section--split>.section-title {
  text-align: center;
  font-size: clamp(1.6rem, 1.18rem + 2vw, 2.8rem);
  line-height: 1.2;
}

.section--split>.lead {
  max-width: min(1160px, 86vw);
  margin-inline: auto;
  font-size: clamp(0.96rem, 0.88rem + 0.7vw, 1.36rem);
  line-height: 1.6;
}

.section--approach {
  text-align: left;
}

.section--approach .approach-wrap {
  max-width: min(1160px, 86vw);
  margin: 0 auto;
}

.section--approach .approach-box {
  background: transparent;
  color: var(--text);
  border: 5px solid var(--text);
  border-radius: clamp(24px, 3vw, 36px);
  padding: clamp(22px, 3.6vw, 40px);
}

.section--approach .box-title {
  margin: 0 0 clamp(24px, 3vw, 40px);
  text-align: center;
  font-weight: 800;
  line-height: 1.1;
  font-size: clamp(1.9rem, 1.5rem + 2.2vw, 3.4rem);
}

.section--approach .box-body {
  max-width: 95ch;
  margin: 0 auto;
}

.section--approach .box-body :is(ul, ol) {
  margin: 0 0 clamp(12px, 1.4vw, 20px);
  padding-left: clamp(22px, 2.2vw, 30px);
}

.section--approach .box-body p.lead {
  font-size: clamp(1.2rem, 1.05rem + 1.2vw, 1.5rem);
  line-height: 1.7;
  margin: 0 0 clamp(10px, 1.6vw, 20px);
}

.section--approach .box-body ul.list--lg>li,
.section--approach .box-body ol>li {
  font-size: clamp(1.2rem, 1.05rem + 1.2vw, 1.5rem);
  line-height: 1.7;
}

:root[data-theme="light"] #main-content .section li {
  color: #660066;
}

:root[data-theme="dark"] #main-content .section li {
  color: #ffbd44;
}

:root[data-theme="light"] .section--approach .approach-box {
  border-color: #660066;
}

:root[data-theme="dark"] .section--approach .approach-box {
  border-color: #ffbd44;
}

.hero {
  position: relative;
  overflow: hidden;
}

.hero-media {
  position: absolute;
  inset: 0;
  z-index: 1;
  height: 100%;
  pointer-events: none;
}

.hero .hero-media img {
  width: 100%;
  height: 100% !important;
  object-fit: cover;
  object-position: center;
}

.hero-content {
  font-size: clamp(1rem, 5.6vw, 3rem);
  font-weight: 800;
  line-height: 1.15;
}

.section--split .split .split-text>p.lead {
  font-size: clamp(0.94rem, 0.86rem + 0.75vw, 1.3rem);
  line-height: 1.6;
}

.section--split .split .split-text ul.list--lg>li {
  font-size: clamp(0.94rem, 0.86rem + 0.72vw, 1.28rem);
  line-height: 1.55;
}

.section--highlights>.section-title {
  margin-bottom: clamp(18px, 3.5vw, 48px);
  margin-block-end: clamp(18px, 3.5vw, 48px);
}

.section--highlights .highlights {
  display: flex;
  flex-direction: column;
  gap: clamp(18px, 2vw, 32px);
  width: min(1160px, 86vw);
  margin: 0 auto;
}

.section--highlights .highlight-row {
  display: grid;
  grid-template-columns: repeat(2, minmax(clamp(260px, 42vw, 520px), 1fr));
  gap: clamp(16px, 1.8vw, 30px);
  align-items: stretch;
  min-height: clamp(260px, 32vw, 380px);
}

.section--highlights .highlight-row:nth-child(even) .highlight-media {
  order: 2;
}

.section--highlights .highlight-row:nth-child(even) .highlight-text {
  order: 1;
}

.section--highlights .highlight-media {
  position: relative;
  overflow: hidden;
  border-radius: clamp(12px, 1.6vw, 20px);
  display: flex;
  height: 100%;
  min-height: inherit;
  margin: 0;
}

.section--highlights .highlight-media img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.section--highlights .highlight-text {
  border: 1px solid var(--bg-alt);
  border-radius: clamp(12px, 1.6vw, 20px);
  padding: clamp(18px, 2vw, 28px);
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: clamp(10px, 1.2vw, 18px);
  background: var(--header-bg);
  min-height: inherit;
  height: 100%;
}

.section--highlights .highlight-text h3 {
  margin: 0;
  font-size: clamp(1.12rem, 1.02rem + 0.6vw, 1.6rem);
  line-height: 1.3;
  font-weight: 800;
}

.section--highlights .highlight-text p {
  margin: 0;
  font-size: clamp(0.94rem, 0.86rem + 0.7vw, 1.28rem);
  line-height: 1.6;
}

@media (max-width: 960px) {
  .section--highlights .highlight-row {
    grid-template-columns: 1fr;
    min-height: 0;
  }

  .section--highlights .highlight-row:nth-child(even) .highlight-media,
  .section--highlights .highlight-row:nth-child(even) .highlight-text {
    order: initial;
  }

  .section--highlights .highlight-media {
    min-height: 0;
    height: auto;
    aspect-ratio: 4 / 3;
  }

  .section--highlights .highlight-text {
    min-height: 0;
    height: auto;
  }
}

@media (max-width: 1199.98px) {
  .section--approach .box-body p.lead {
    font-size: clamp(1.02rem, 0.9rem + 0.8vw, 1.35rem);
    line-height: 1.55;
  }

  .section--approach .box-body ul.list--lg>li,
  .section--approach .box-body ol>li {
    font-size: clamp(1.02rem, 0.9rem + 0.8vw, 1.35rem);
    line-height: 1.55;
  }
}

@media (min-width: 700px) and (max-width: 1199.98px) {
  .hero {
    min-height: clamp(320px, 40vw, 480px);
    align-items: flex-start;
  }

  .hero-content {
    padding-block-start: 10px;
    padding-block-end: 12px;
    gap: clamp(6px, 1vw, 12px);
  }

  .hero-actions {
    gap: clamp(8px, 1.8vh, 24px);
  }

  #main-content>.section:first-of-type {
    padding-top: 18px;
  }
}

@media (min-width: 961px) {
  .section--highlights .highlight-row {
    grid-template-columns: 1fr 1fr;
  }
}

.section--topics .topics-grid {
  display: grid;
  gap: clamp(16px, 1.8vw, 30px);
  width: min(1160px, 86vw);
  margin: 0 auto;
  grid-template-columns: repeat(2, minmax(clamp(260px, 42vw, 520px), 1fr));
  align-items: stretch;
}

.section--topics .topics-card {
  border: 1px solid var(--bg-alt);
  border-radius: clamp(12px, 1.6vw, 20px);
  padding: clamp(18px, 2vw, 28px);
  background: var(--header-bg);
  display: flex;
  flex-direction: column;
  gap: clamp(12px, 1.4vw, 22px);
  height: 100%;
  text-align: left;
}

.section--topics .topics-card h3 {
  margin: 0;
  font-size: clamp(1.12rem, 1.02rem + 0.6vw, 1.6rem);
  line-height: 1.3;
  font-weight: 800;
}

.section--topics .topics-card ol {
  margin: 0;
  padding-left: clamp(22px, 2.2vw, 30px);
}

.section--topics .topics-card li {
  font-size: clamp(0.94rem, 0.86rem + 0.7vw, 1.28rem);
  line-height: 1.6;
}

.section--topics .topics-card li+li {
  margin-top: clamp(6px, 1.1vw, 12px);
}

@media (max-width: 960px) {
  .section--topics .topics-grid {
    grid-template-columns: 1fr;
  }

  .section--topics .topics-card {
    height: auto;
  }
}

:root {
  --light-bg: #f5f6fa;
  --light-bg-alt: #e1e3ea;
  --light-header: #fff;
  --light-footer: #222;
  --light-footer-text: #fff;
  --light-primary: #0057b8;
  --light-text: #111;
  --light-nav-hover: #e1e3ea;
  --light-overlay: rgba(0, 0, 0, 0.6);
  --light-hero-overlay: rgba(0, 0, 0, 0.6);
  --light-header-shadow: 0 2px 8px rgba(0, 0, 0, 0.04);
  --light-dropdown-shadow: 0 4px 16px rgba(0, 0, 0, 0.08);
  --light-mobile-menu-bg: #fff;
  --light-cta-bg: #fff;
  --light-cta-text: var(--light-primary);
  --light-cta-hover-bg: var(--light-primary);
  --light-cta-hover-text: #fff;
  --dark-bg: #23272f;
  --dark-bg-alt: #2e323a;
  --dark-header: #181a20;
  --dark-footer: #111;
  --dark-footer-text: #eee;
  --dark-primary: #4ea1ff;
  --dark-text: #f5f6fa;
  --dark-nav-hover: #353a42;
  --dark-overlay: rgba(0, 0, 0, 0.8);
  --dark-hero-overlay: rgba(0, 0, 0, 0.6);
  --dark-header-shadow: 0 2px 8px rgba(0, 0, 0, 0.18);
  --dark-dropdown-shadow: 0 4px 16px rgba(0, 0, 0, 0.18);
  --dark-mobile-menu-bg: rgba(30, 34, 40, 0.98);
  --dark-primary-strong: #2563eb;
  --bg: var(--light-bg);
  --bg-alt: var(--light-bg-alt);
  --header-bg: var(--light-header);
  --header-shadow: var(--light-header-shadow);
  --footer-bg: var(--light-footer);
  --footer-text: var(--light-footer-text);
  --primary: var(--light-primary);
  --text: var(--light-text);
  --nav-hover: var(--light-nav-hover);
  --overlay: var(--light-overlay);
  --hero-overlay: var(--light-hero-overlay);
  --dropdown-shadow: var(--light-dropdown-shadow);
  --mobile-menu-bg: var(--light-mobile-menu-bg);
  --cta-bg: var(--light-cta-bg);
  --cta-text: var(--light-cta-text);
  --cta-hover-bg: var(--light-cta-hover-bg);
  --cta-hover-text: var(--light-cta-hover-text);
  --divider: #e5e7eb;
  --tbtn-bg: #000;
  --tbtn-fg: #fff;
  --t-dot-ring: #000;
  --t-dot-fill: transparent;
  --t-dot-active: var(--light-primary);
  --hero-bg: #b68144;
  --hero-text: #fff;
  --hero-check: #16a34a;
  --hero-focus-outline: #fff;
  --section-link: #0b5bd3;
  --section-link-hover: #c00000;
  --program-cta-ring: #000;
  --program-cta-focus: #000;
  --contact-card-bg: #2b1600;
  --contact-card-text: #fff;
  --contact-card-link: #7ec1ff;
  --contact-card-link-hover: #ff7578;
  --contact-card-focus: #fff;
  --contact-pill-ring: #000;
  --contact-pill-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
}
:root[data-theme="dark"] {
  --bg: var(--dark-bg);
  --bg-alt: var(--dark-bg-alt);
  --header-bg: var(--dark-header);
  --header-shadow: var(--dark-header-shadow);
  --footer-bg: var(--dark-footer);
  --footer-text: var(--dark-footer-text);
  --primary: var(--dark-primary);
  --text: var(--dark-text);
  --nav-hover: var(--dark-nav-hover);
  --overlay: var(--dark-overlay);
  --hero-overlay: var(--dark-hero-overlay);
  --dropdown-shadow: var(--dark-dropdown-shadow);
  --mobile-menu-bg: var(--dark-mobile-menu-bg);
  --cta-bg: var(--dark-primary-strong);
  --cta-text: #fff;
  --cta-hover-bg: #fff;
  --cta-hover-text: var(--dark-primary-strong);
  --divider: #3a3f46;
  --tbtn-bg: #fff;
  --tbtn-fg: #000;
  --t-dot-ring: #fff;
  --t-dot-fill: transparent;
  --t-dot-active: var(--dark-primary);
  --hero-bg: #b68144;
  --hero-text: #fff;
  --hero-check: #16a34a;
  --hero-focus-outline: #fff;
  --section-link: #7ec1ff;
  --section-link-hover: #ff7578;
  --program-cta-ring: #fff;
  --program-cta-focus: #fff;
  --contact-card-bg: #2b1600;
  --contact-card-text: #fff;
  --contact-card-link: #7ec1ff;
  --contact-card-link-hover: #ff7578;
  --contact-card-focus: #fff;
  --contact-pill-ring: #fff;
  --contact-pill-shadow: 0 2px 8px rgba(0, 0, 0, 0.18);
}
html {
  color-scheme: light dark;
}
.no-js .nav,
.no-js .hamburger,
.no-js .overlay,
.no-js .mobile-menu {
  display: none !important;
}
.header-right-controls:has(.nav ul:empty) .hamburger {
  display: none;
}
*,
*::before,
*::after {
  box-sizing: border-box;
}
.skip-link {
  position: absolute;
  left: -9999px;
  top: auto;
  width: 1px;
  height: 1px;
  overflow: hidden;
}
.skip-link:focus {
  left: 8px;
  top: 8px;
  width: auto;
  height: auto;
  padding: 8px 12px;
  background: var(--header-bg);
  color: var(--text);
  border-radius: 4px;
  z-index: 2000;
}
body {
  margin: 0;
  font-family: "Segoe UI", Arial, sans-serif;
  background: var(--bg);
  color: var(--text);
  overflow-x: hidden;
}
.header {
  position: sticky;
  top: 0;
  background: var(--header-bg);
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 16px;
  height: 56px;
  z-index: 1000;
  box-shadow: var(--header-shadow);
  transition: background 0.3s;
}
.logo-dark {
  display: none;
}
:root[data-theme="dark"] .logo-light {
  display: none;
}
:root[data-theme="dark"] .logo-dark {
  display: inline-block;
}
:root[data-theme="dark"] .brand img {
  filter: none;
}
.brand {
  display: inline-flex;
  align-items: center;
  gap: 0.6rem;
  text-decoration: none;
  color: var(--text);
}
.brand:focus-visible {
  outline: 2px solid var(--primary);
  outline-offset: 2px;
}
.brand-title {
  font-weight: 700;
  line-height: 1;
  letter-spacing: 0.2px;
  font-size: clamp(0.95rem, 1rem + 0.5vw, 1.25rem);
  white-space: nowrap;
  max-width: 18ch;
  overflow: hidden;
  text-overflow: ellipsis;
}
.header-right-controls {
  display: flex;
  align-items: center;
  gap: 16px;
}
.theme-toggle {
  background: none;
  border: none;
  font-size: 1.5em;
  cursor: pointer;
  color: var(--primary);
  transition: color 0.3s;
  flex-shrink: 0;
  padding: 0;
  line-height: 1;
}
.hamburger {
  display: flex;
  flex-direction: column;
  justify-content: center;
  width: 36px;
  height: 24px;
  background: none;
  border: none;
  cursor: pointer;
  flex-shrink: 0;
  padding: 0;
}
.hamburger span {
  display: block;
  height: 3px;
  width: 100%;
  background: var(--primary);
  margin: 2px 0;
  border-radius: 2px;
  transition: background 0.3s;
}
:focus-visible {
  outline: 4px solid var(--primary);
  outline-offset: 0;
}
.nav {
  display: none;
  align-items: center;
}
.overlay {
  display: none;
  position: fixed;
  inset: 0;
  background: var(--overlay);
  z-index: 1100;
  transition: background 0.3s;
}
.overlay.active {
  display: block;
}
.mobile-menu {
  display: flex;
  flex-direction: column;
  position: fixed;
  top: 0;
  right: 0;
  width: 80vw;
  max-width: 340px;
  height: 100vh;
  background: var(--mobile-menu-bg);
  z-index: 1101;
  padding: 32px 0 0 0;
  box-shadow: -2px 0 16px rgba(0, 0, 0, 0.12);
  transform: translateX(100%);
  transition: transform 0.3s;
  overflow-y: auto;
}
.mobile-menu.active {
  transform: translateX(0);
}
.mobile-menu ul {
  list-style: none;
  padding: 0;
  margin: 0;
}
.mobile-menu .nav-item {
  border-bottom: 1px solid var(--divider);
  position: relative;
}
.mobile-menu .nav-link {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 18px 28px;
  color: var(--text);
  text-decoration: none;
  font-size: 1.1em;
  font-weight: 500;
  background: none;
  border: none;
  width: 100%;
  cursor: pointer;
  transition: background 0.2s;
}
.mobile-menu :is(.nav-link, .dropdown a):hover {
  background: var(--nav-hover);
}
.mobile-menu .arrow {
  margin-left: 8px;
  font-size: 1em;
  transition: transform 0.2s;
}
.mobile-menu .dropdown {
  display: none;
  flex-direction: column;
  background: none;
  box-shadow: none;
  position: static;
  padding: 0 0 0 24px;
}
.mobile-menu .dropdown a {
  display: block;
  padding: 12px 0;
  color: var(--text);
  text-decoration: none;
  font-size: 1em;
  transition: background 0.2s;
}
.mobile-menu .nav-item.open > .dropdown {
  display: flex;
}
.mobile-menu .nav-item.open > .nav-link .arrow {
  transform: rotate(180deg);
}
.hero {
  position: relative;
  display: grid;
  place-items: center;
  text-align: center;
  overflow: hidden;
  min-height: clamp(320px, 60vw, 420px);
  color: var(--hero-text);
  background: var(--hero-bg);
  --hero-cta-bg: var(--dark-primary-strong);
  --hero-cta-text: #fff;
  --hero-cta-hover-bg: #8b0000;
  --hero-cta-hover-text: #fff;
  --hero-link: #7ee1ff;
  --hero-link-hover: #ff6d6f;
  --hero-link-underline: color-mix(in srgb, var(--hero-link) 60%, transparent);
}
.hero::after {
  content: "";
  position: absolute;
  inset: 0;
  background: var(--hero-overlay);
  z-index: 1;
}
.hero .hero-media {
  position: absolute;
  inset: 0;
  z-index: 1;
  display: block;
  width: 100%;
  height: 100%;
  pointer-events: none;
}
.hero .hero-media img {
  display: block;
  width: 100%;
  height: 100% !important;
  max-width: none;
  object-fit: cover;
  object-position: center;
}
.hero-content {
  grid-area: 1 / 1;
  position: relative;
  z-index: 2;
  max-width: min(92vw, 980px);
  padding: clamp(16px, 3vw, 40px);
  padding-top: clamp(8px, 3vh, 16px);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: clamp(8px, 1.5vw, 18px);
  overflow: visible;
}
.hero-title {
  margin: 0;
  font-weight: 800;
  line-height: 1.15;
  padding-bottom: 0.12em;
  font-size: clamp(1rem, 5.6vw, 3rem);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.hero-bullets {
  list-style: none;
  margin: 6px 0 12px;
  padding: 0;
  display: inline-block;
  text-align: left;
}
.hero-bullets li {
  font-weight: 700;
  display: flex;
  margin: 6px 0;
  line-height: 1.5;
  font-size: clamp(0.95rem, 0.85rem + 0.6vw, 1.08rem);
  align-items: baseline;
  gap: 10px;
}
.hero-bullets li::before {
  content: "✓";
  display: inline-grid;
  place-items: center;
  flex: none;
  width: 1em;
  height: 1em;
  font-weight: 900;
  line-height: 1;
  color: var(--hero-check);
  transform: translateY(0.06em);
}

.hero-bullets a {
  color: var(--hero-link);
  font-weight: 700;
  text-decoration: underline;
  text-decoration-color: var(--hero-link-underline, currentColor);
  text-underline-offset: 3px;
}
.hero-bullets a:hover,
.hero-bullets a:focus-visible {
  color: var(--hero-link-hover);
  text-decoration-color: var(--hero-link-hover);
}
.hero-actions {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: clamp(12px, 4vh, 60px);
}
.hero-cta {
  display: inline-block;
  padding: 12px 28px;
  border-radius: 30px;
  background: var(--hero-cta-bg);
  color: var(--hero-cta-text);
  font-weight: 600;
  font-size: clamp(1.06rem, 0.6rem + 1vw, 1.375rem);
  line-height: 1.2;
  text-decoration: none;
  box-shadow: 0 0 0 1px var(--hero-focus-outline), 0 2px 8px rgba(0, 0, 0, 0.08);
  transition: background 0.2s ease, color 0.2s ease, transform 0.06s ease-in-out;
}
.hero-cta:hover,
.hero-cta:focus-visible {
  background: var(--hero-cta-hover-bg);
  color: var(--hero-cta-hover-text);
}
.hero-cta:active {
  transform: translateY(1px);
}
.hero-cta--contact {
  display: none;
}

.hero :is(a, .hero-cta):focus-visible {
  outline: 3px solid var(--hero-focus-outline);
  outline-offset: 3px;
}
.section {
  width: 100%;
  padding: 36px 0;
  text-align: center;
  content-visibility: auto;
  contain-intrinsic-size: 500px;
}
#main-content > .section:nth-child(even) {
  background: var(--bg-alt);
}
#main-content > .section:nth-child(odd) {
  background: var(--bg);
}
.section-title {
  font-size: 1.2rem;
  margin-bottom: 12px;
  font-weight: 600;
}
.section-content {
  max-width: 90vw;
  margin: 0 auto;
  font-size: 1rem;
  line-height: 1.7;
}
:where(#main-content .section a) {
  color: var(--section-link);
  font-weight: 800;
  text-decoration: underline;
  text-decoration-thickness: 2px;
  text-underline-offset: 3px;
  text-decoration-color: currentColor;
}
:where(#main-content .section a:hover),
:where(#main-content .section a:focus-visible) {
  color: var(--section-link-hover);
}
.section--split {
  text-align: left;
}
.section--split .split {
  display: grid;
  grid-template-columns: 1fr;
  align-items: center;
  gap: clamp(24px, 4vw, 56px);
  max-width: min(1160px, 86vw);
  margin: 0 auto;
}
.section--split .section-title {
  margin: 0 0 clamp(10px, 1.2vw, 18px);
  font-weight: 800;
  line-height: 1.1;
  font-size: clamp(2.1rem, 3.4vw, 3.1rem);
}
.section--split .lead {
  font-size: clamp(1.02rem, 0.9rem + 0.8vw, 1.35rem);
  line-height: 1.55;
  margin: 0 0 clamp(10px, 1.6vw, 20px);
}
.section--split .split-text p {
  font-size: clamp(1rem, 0.88rem + 0.7vw, 1.2rem);
  line-height: 1.55;
  margin: 0 0 clamp(10px, 1.4vw, 18px);
}
.section--split .split-text {
  max-width: 68ch;
}
.section--split .split-media img {
  display: block;
  width: 100%;
  height: auto;
  border-radius: 16px;
  box-shadow: 0 10px 24px rgba(0, 0, 0, 0.18);
}
.section--split .split-media {
  position: relative;
  isolation: isolate;
  overflow: visible;
  z-index: 0;
  order: -1;
}

.section--split .split-media.split-media--accent::after {
  content: "";
  position: absolute;
  inset: auto 0 0 auto;
  transform: translate(8%, 10%);
  inline-size: clamp(220px, 85%, 640px);
  aspect-ratio: 1.6;
  background: #f59e0b;
  border-radius: clamp(18px, 2.2vw, 24px);
  box-shadow: 0 12px 28px rgba(0, 0, 0, 0.12);
  z-index: -1;
}

.section--split .split {
  grid-template-areas:
    "media"
    "text";
}
.section--split .split .split-media {
  grid-area: media;
}
.section--split .split .split-text {
  grid-area: text;
}

#qualifications .split-text ul {
  margin: 0 0 clamp(10px, 1.4vw, 18px);
  padding-left: clamp(22px, 2.2vw, 30px);
  list-style: disc outside;
}
#qualifications .split-text li {
  font-size: clamp(1.12rem, 0.72rem + 1.05vw, 1.35rem);
  line-height: 1.7;
  color: var(--text);
}
.split-text ul.list--lg > li {
  font-size: clamp(1.12rem, 0.72rem + 1.05vw, 1.35rem);
  line-height: 1.7;
}
.section--programs .section-title {
  margin: 0 0 clamp(10px, 1.2vw, 18px);
  font-weight: 800;
  line-height: 1.1;
  font-size: clamp(2.1rem, 3.4vw, 3.1rem);
}
.section--programs .programs-grid {
  max-width: min(1160px, 92vw);
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr;
  gap: clamp(18px, 2.6vw, 36px);
}

.section--programs .program {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 12px;
}
.section--programs .program-media {
  width: min(75vw, 320px);
  inline-size: min(75vw, 320px);
  aspect-ratio: 1 / 1;
  overflow: hidden;
  border-radius: 16px;
  box-shadow: 0 8px 18px rgba(0, 0, 0, 0.12);
  background: var(--header-bg);
}
.section--programs .program-media img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}
.section--programs .program-text {
  width: min(75vw, 320px);
  inline-size: min(75vw, 320px);
  max-width: min(75vw, 320px);
  text-align: left;
}
.section--programs .program-title {
  margin: 2px 0 6px;
  font-weight: 800;
  line-height: 1.2;
  font-size: clamp(1.25rem, 1.05rem + 0.9vw, 1.6rem);
}
.section--programs .program-desc {
  margin: 0;
  font-size: clamp(1rem, 0.92rem + 0.4vw, 1.1rem);
  line-height: 1.65;
}
.section--programs .program-title a {
  text-decoration: none;
}
.section--programs a:focus-visible {
  outline: 2px solid var(--primary);
  outline-offset: 2px;
  border-radius: 4px;
}
.section--programs {
  --hero-cta-bg: var(--dark-primary-strong);
  --hero-cta-text: #fff;
  --hero-cta-hover-bg: #8b0000;
  --hero-cta-hover-text: #fff;
}
.section--programs .hero-cta {
  margin-top: 10px;
  padding: 10px 22px;
  font-size: clamp(1.12rem, 0.7rem + 1vw, 1.45rem);
  font-weight: 700;
  text-decoration: none;
  color: #fff;
  box-shadow: 0 0 0 1px var(--program-cta-ring), 0 2px 8px rgba(0, 0, 0, 0.08);
}
.section--programs .hero-cta:hover,
.section--programs .hero-cta:focus-visible {
  color: #fff;
}
.section--programs .hero-cta:focus-visible {
  outline: 3px solid var(--program-cta-focus);
  outline-offset: 3px;
}
.section--testimonials {
  text-align: center;
  content-visibility: visible;
  contain-intrinsic-size: auto;
}
.section--testimonials .t-wrap {
  max-width: min(1160px, 92vw);
  margin: 0 auto;
  text-align: center;
  position: relative;
}
.section--testimonials .t-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
  margin-bottom: clamp(6px, 0.9vw, 12px);
}
.section--testimonials .t-title {
  margin: 0;
  font-weight: 800;
  line-height: 1.1;
  font-size: clamp(1.45rem, 6vw, 1.9rem);
}
.section--testimonials .t-nav {
  display: inline-flex;
  gap: 10px;
}
.section--testimonials .t-btn {
  inline-size: 44px;
  block-size: 44px;
  border-radius: 50%;
  border: 2px solid var(--divider);
  background: var(--tbtn-bg);
  color: var(--tbtn-fg);
  font-size: 24px;
  line-height: 1;
  display: grid;
  place-items: center;
  cursor: pointer;
  transition: background 0.2s ease, border-color 0.2s ease,
    transform 0.06s ease-in-out;
  border-color: var(--tbtn-bg);
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
}
.section--testimonials .t-btn:hover,
.section--testimonials .t-btn:focus-visible {
  background: var(--tbtn-bg);
  color: var(--tbtn-fg);
  border-color: var(--primary);
  box-shadow: 0 0 0 2px var(--primary), 0 8px 20px rgba(0, 0, 0, 0.18);
}
.section--testimonials .t-btn:active {
  transform: translateY(1px);
}
.section--testimonials .t-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.section--testimonials .t-track {
  list-style: none;
  margin: 0;
  padding: 0;
  display: grid;
  grid-auto-flow: column;
  grid-auto-columns: 100%;
  overflow: visible;
  border-radius: 16px;
  position: relative;
  will-change: transform;
  align-items: start;
  transition: height 0.25s ease;
}
.section--testimonials .t-slide {
  display: grid;
  place-items: start center;
  padding: clamp(14px, 2.4vw, 28px);
}
.section--testimonials .t-card {
  max-width: min(100ch, 90%);
  background: var(--header-bg);
  color: var(--text);
  border-radius: 18px;
  box-shadow: 0 10px 24px rgba(0, 0, 0, 0.12);
  padding: clamp(18px, 3vw, 34px);
  border: 1px solid var(--divider);
  border-bottom: 4px solid #f59e0b;
}
.section--testimonials .t-headerline {
  display: grid;
  justify-items: center;
  gap: 8px;
  margin-bottom: clamp(8px, 1.2vw, 12px);
}
.section--testimonials .t-avatar {
  inline-size: 72px;
  block-size: 72px;
  border-radius: 50%;
  background: var(--bg-alt);
  color: var(--text);
  display: grid;
  place-items: center;
  font-weight: 800;
  font-size: 1.25rem;
  border: 2px solid var(--divider);
  overflow: hidden;
}
.section--testimonials .t-avatar img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}
.section--testimonials .t-name {
  font-weight: 800;
  font-size: clamp(1.25rem, 1rem + 0.9vw, 1.6rem);
  margin: 0;
}
.section--testimonials .t-tagline {
  margin: 0;
  opacity: 0.85;
  font-size: clamp(1rem, 0.92rem + 0.5vw, 1.1rem);
}
.section--testimonials .t-quote {
  margin: clamp(14px, 2vw, 20px) auto 0;
  font-size: clamp(1.12rem, 0.9rem + 1.1vw, 1.5rem);
  line-height: 1.8;
  max-width: 66ch;
}
#testimonials {
  --testimonial-scale: 0.75;
}
.section--testimonials .t-quote {
  font-size: clamp(
    calc(1.12rem * var(--testimonial-scale)),
    calc((0.9rem + 1.1vw) * var(--testimonial-scale)),
    calc(1.5rem * var(--testimonial-scale))
  );
}

.section--testimonials .t-viewport {
  overflow: hidden;
  border-radius: 16px;
}
.section--testimonials .t-dots {
  margin-top: clamp(8px, 1.2vw, 14px);
  display: flex;
  justify-content: center;
  gap: 12px;
  flex-wrap: wrap;
  row-gap: 6px;
}
.section--testimonials .t-dot {
  inline-size: 40px;
  block-size: 40px;
  border: 0;
  background: transparent;
  padding: 0;
  cursor: pointer;
  position: relative;
  border-radius: 999px;
}
.section--testimonials .t-dot::before {
  content: "";
  position: absolute;
  inset: 0;
  margin: auto;
  inline-size: 10px;
  block-size: 10px;
  border-radius: 50%;
  background: var(--t-dot-fill);
  box-shadow: 0 0 0 2px var(--t-dot-ring) !important;
  transition: transform 0.12s ease, box-shadow 0.12s ease,
    background-color 0.12s ease;
}
.section--testimonials .t-dot[aria-selected="true"]::before {
  background: var(--t-dot-active) !important;
  box-shadow: 0 0 0 2px var(--t-dot-ring) !important;
}
.section--testimonials .t-dot:hover::before {
  transform: scale(1.15);
  box-shadow: 0 0 0 2px var(--t-dot-ring) !important;
}
.section--testimonials .t-dot:focus-visible {
  outline: 3px solid var(--t-dot-ring) !important;
  outline-offset: 2px;
}

.section--testimonials .t-dots:has(.t-dot:nth-child(n + 12)) .t-dot {
  inline-size: 32px;
  block-size: 32px;
}
.section--testimonials .t-dots:has(.t-dot:nth-child(n + 12)) .t-dot::before {
  inline-size: 10px;
  block-size: 10px;
}
@supports not selector(:has(*)) {
  .section--testimonials .t-dot {
    inline-size: 36px;
    block-size: 36px;
  }
}
.section--testimonials .t-dot::after {
  content: none !important;
}
.section--contact {
  text-align: left;
}
.section--contact .section-title {
  text-align: center;
  margin-bottom: clamp(14px, 1.8vw, 20px);
  font-weight: 800;
  line-height: 1.1;
  font-size: clamp(2.2rem, 2rem + 1.2vw, 3.2rem);
}
.contact-grid {
  display: grid;
  gap: clamp(16px, 2.2vw, 24px);
  grid-template-columns: 1fr;
  max-width: 96vw;
  margin: 0 auto;
}

.contact-card {
  background: var(--contact-card-bg);
  color: var(--contact-card-text);
  border-radius: 18px;
  padding: 12px;
  box-shadow: 0 12px 28px rgba(0, 0, 0, 0.22);
}
.contact-card .card-title {
  margin: 0 0 8px;
  font-weight: 900;
  font-size: clamp(1.8rem, 1.1rem + 1.8vw, 2.2rem);
  line-height: 1.15;
}
.contact-card .card-blurb {
  margin: 0 0 14px;
  line-height: 1.7;
  font-size: clamp(1.06rem, 0.95rem + 0.6vw, 1.25rem);
}
.meta {
  display: grid;
  gap: 14px;
  margin-top: 8px;
  font-size: clamp(1.02rem, 0.9rem + 0.5vw, 1.2rem);
}
.meta-item {
  display: flex;
  align-items: center;
  gap: 12px;
}
.avatar {
  inline-size: 64px;
  block-size: 64px;
  border-radius: 50%;
  display: grid;
  place-items: center;
  font-weight: 800;
  background: rgba(255, 255, 255, 0.16);
  border: 2px solid rgba(255, 255, 255, 0.7);
}
.contact-card a {
  color: var(--contact-card-link);
  text-decoration: underline;
  text-decoration-color: var(--contact-card-link);
  text-underline-offset: 3px;
  font-weight: 800;
}
.contact-card a:hover,
.contact-card a:focus-visible {
  color: var(--contact-card-link-hover);
  text-decoration-color: var(--contact-card-link-hover);
}
.contact-card a:focus-visible {
  outline: 3px solid var(--contact-card-focus);
  outline-offset: 3px;
  border-radius: 6px;
}
.icon {
  inline-size: 22px;
  block-size: 22px;
  fill: currentColor;
  opacity: 0.95;
}
.contact-form {
  background: var(--header-bg);
  color: var(--text);
  border: 1px solid var(--divider);
  border-radius: 18px;
  padding: 12px;
  box-shadow: 0 12px 28px rgba(0, 0, 0, 0.1);
}
.form-row {
  display: grid;
  grid-template-columns: 1fr;
  gap: 16px;
}
.form-row--2 {
  grid-template-columns: 1fr;
}

.form-field {
  display: flex;
  flex-direction: column;
  gap: 8px;
}
.form-label {
  font-weight: 800;
  font-size: clamp(1.06rem, 0.9rem + 0.5vw, 1.25rem);
}
.form-hint {
  font-size: 0.98rem;
  opacity: 0.85;
}
input[type="text"],
input[type="email"],
input[type="tel"],
textarea {
  font: inherit;
  color: var(--text);
  background: var(--bg);
  border: 1px solid var(--divider);
  border-radius: 12px;
  padding: 14px 16px;
  font-size: clamp(1.02rem, 0.9rem + 0.4vw, 1.2rem);
}
textarea {
  min-height: 160px;
  resize: vertical;
}
input:focus-visible,
textarea:focus-visible {
  outline: 3px solid var(--primary);
  outline-offset: 2px;
  border-color: var(--primary);
}
fieldset {
  border: 0;
  padding: 0;
  margin: 0 0 10px;
}
.form-legend {
  font-weight: 900;
  margin-bottom: 8px;
  font-size: clamp(1.15rem, 1rem + 0.6vw, 1.35rem);
}
.options {
  display: flex;
  flex-wrap: wrap;
  gap: 12px 20px;
}
.option {
  display: inline-flex;
  align-items: center;
  gap: 10px;
  font-size: clamp(1.02rem, 0.9rem + 0.5vw, 1.2rem);
}
.option--other {
  flex-wrap: wrap;
  align-items: flex-start;
  gap: 8px 10px;
}
.option input[type="radio"] {
  accent-color: var(--primary);
}
.option--other input[type="text"] {
  min-width: 0;
  flex: 1 1 100%;
  width: 100%;
}
.required-badge {
  font-weight: 700;
  color: #ff0000;
}
.btn-pill {
  display: inline-block;
  padding: 14px 28px;
  border-radius: 30px;
  background: var(--cta-bg);
  color: var(--cta-text);
  font-weight: 800;
  border: 0;
  cursor: pointer;
  font-size: clamp(1.02rem, 0.9rem + 0.5vw, 1.2rem);
  box-shadow: 0 0 0 1px color-mix(in srgb, var(--text) 20%, transparent),
    0 2px 8px rgba(0, 0, 0, 0.08);
  transition: background 0.2s ease, color 0.2s ease, transform 0.06s ease-in-out;
}
.btn-pill:hover,
.btn-pill:focus-visible {
  background: var(--cta-hover-bg);
  color: var(--cta-hover-text);
}
.btn-pill:active {
  transform: translateY(1px);
}
.form-actions {
  margin-top: 12px;
}
.form-alert {
  display: none;
  margin-top: 12px;
  padding: 12px 14px;
  border-radius: 12px;
  font-size: 1rem;
}
.form-alert--success {
  background: #e8f8ee;
  color: #065f46;
  border: 1px solid #10b981;
}
.form-alert--error {
  background: #fde8e8;
  color: #7f1d1d;
  border: 1px solid #ef4444;
}
:root[data-theme="dark"] .form-alert--success {
  background: #0b2b1f;
  color: #b7f3dc;
}
:root[data-theme="dark"] .form-alert--error {
  background: #3a0f0f;
  color: #fecaca;
}
.counter {
  font-size: 1rem;
  opacity: 0.75;
  text-align: right;
}
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  padding: 0;
  border: 0;
  white-space: nowrap;
  clip-path: inset(50%);
  clip: rect(0 0 0 0);
  overflow: hidden;
}
.section--contact .btn-pill {
  box-shadow: 0 0 0 2px var(--contact-pill-ring), var(--contact-pill-shadow);
}
.contact-grid > * {
  min-width: 0;
}
.section--contact input[type="text"],
.section--contact input[type="email"],
.section--contact input[type="tel"],
.section--contact textarea {
  width: 100%;
  max-width: 100%;
}

.footer {
  background: var(--footer-bg);
  color: var(--footer-text);
  text-align: center;
  padding: 24px 0 16px 0;
  font-size: 1rem;
  letter-spacing: 0.5px;
  transition: background 0.3s, color 0.3s;
}

.whatsapp-button {
  position: fixed;
  bottom: 20px;
  right: 20px;
  background-color: transparent;
  border-radius: 50%;
  cursor: pointer;
  transition: transform 0.2s ease-in-out;
  z-index: 1002;
  width: 60px;
  height: 60px;
}
.whatsapp-button img {
  width: 100%;
  height: 100%;
  object-fit: contain;
}
.whatsapp-button:hover,
.whatsapp-button:active {
  transform: scale(1.1);
  animation: pulse 1s infinite;
}

@keyframes pulse {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
  100% {
    transform: scale(1);
  }
}

@media (min-width: 360px) {
  .hero-bullets li {
    line-height: 1.6;
    font-size: clamp(1.12rem, 1rem + 1.35vw, 2rem);
    gap: clamp(15px, 2vw, 30px);
  }
  .option--other {
    flex-wrap: nowrap;
    align-items: center;
    gap: 10px;
  }
  .option--other input[type="text"] {
    min-width: min(320px, 60vw);
    flex: 0 0 auto;
    width: auto;
  }
  .contact-card,
  .contact-form {
    padding: clamp(18px, 2.8vw, 28px);
  }
}

@media (min-width: 380px) {
  .section--testimonials .t-dot {
    inline-size: 44px;
    block-size: 44px;
  }
  .section--testimonials .t-dot::before {
    inline-size: 12px;
    block-size: 12px;
  }
}

@media (min-width: 400px) {
  .contact-grid {
    max-width: min(1160px, 92vw);
  }
  .section--testimonials .t-title {
    font-size: clamp(2.2rem, 2rem + 1.2vw, 3.2rem);
  }
  .section--testimonials .t-btn {
    inline-size: 48px;
    block-size: 48px;
    font-size: 30px;
  }
  .section--split .split-media.split-media--accent::after {
    aspect-ratio: 1.3;
  }
}

@media (min-width: 420px) {
  .section--testimonials .t-btn {
    inline-size: 60px;
    block-size: 60px;
    font-size: 36px;
  }
}

@media (min-width: 550px) {
  .hero-cta--contact {
    display: inline-block;
  }
}

@media (min-width: 600px) {
  .section--split .split {
    justify-items: center;
  }
  .section--split .split > * {
    justify-self: center;
  }

  .section--programs .program-title {
    font-size: clamp(1.35rem, 0.8rem + 2vw, 1.7rem);
  }
  .section--programs .program-desc {
    font-size: clamp(1.06rem, 0.9rem + 1.1vw, 1.3rem);
    line-height: 1.65;
  }
}

@media (min-width: 600px) {
  @supports not selector(:has(*)) {
    .section--testimonials .t-dot {
      inline-size: 44px;
      block-size: 44px;
    }
  }
}

@media (min-width: 700px) {
  .hero {
    min-height: clamp(320px, 40vw, 480px);
  }
  .hero-content {
    padding-block-start: 10px;
    padding-block-end: 12px;
    gap: clamp(6px, 1vw, 12px);
  }
  .hero-actions {
    gap: clamp(8px, 1.8vh, 24px);
  }
  #main-content > .section:first-of-type {
    padding-top: 18px;
  }

  .form-row--2 {
    grid-template-columns: 1fr 1fr;
  }
}

@media (min-width: 768px) {
  .hero {
    align-items: center;
    justify-items: center;
    min-height: clamp(420px, 52vw, 640px);
  }
  .hero-content {
    padding-top: clamp(16px, 3vw, 40px);
  }

  .whatsapp-button {
    width: 80px;
    height: 80px;
  }
  .whatsapp-button:hover,
  .whatsapp-button:active {
    transform: scale(1.15);
  }
}

@media (min-width: 900px) {
  .section--split .split {
    grid-template-columns: 1.08fr 1fr;
    justify-items: initial;
  }
  .section--split .split > * {
    justify-self: stretch;
  }
  .section--split .split .split-media {
    order: 0;
  }
  .section--split .split.split--image-left {
    grid-template-columns: 1.08fr 1fr;
    grid-template-areas: "media text";
  }
  .section--split .split.split--image-right {
    grid-template-columns: 1fr 1.08fr;
    grid-template-areas: "text media";
  }
  .section--split .split.split--image-right .split-media {
    grid-column: 2;
  }
  .section--split .split.split--image-right .split-text {
    grid-column: 1;
  }

  .section--programs .programs-grid {
    grid-template-columns: repeat(3, 1fr);
  }
  .section--programs .program-media,
  .section--programs .program-text {
    width: 100%;
    inline-size: 100%;
    max-width: 46ch;
  }
  .section--programs .program-title {
    font-size: clamp(1.25rem, 1.05rem + 0.9vw, 1.6rem);
  }
  .section--programs .program-desc {
    font-size: clamp(1rem, 0.92rem + 0.4vw, 1.1rem);
  }
}

@media (min-width: 1000px) {
  .contact-grid {
    grid-template-columns: minmax(280px, 380px) 1fr;
  }
}

@media (min-width: 1200px) {
  .section--split .lead {
    font-size: clamp(1.18rem, 1.05rem + 1.2vw, 1.6rem);
    line-height: 1.7;
  }
  .section--split .split-text p {
    font-size: clamp(1.06rem, 0.6rem + 1vw, 1.25rem);
    line-height: 1.7;
  }

  .header-right-controls:has(.nav ul:empty) .nav {
    display: none !important;
  }
  .header {
    height: 72px;
    padding: 0 40px;
  }
  .logo img {
    max-height: 40px;
  }
  .theme-toggle {
    font-size: 1.7em;
  }
  .hamburger {
    display: none;
  }
  .nav {
    display: flex;
    align-items: center;
    gap: 0;
  }
  .nav ul {
    display: flex;
    align-items: center;
    gap: 0;
    margin: 0;
    padding: 0;
    list-style: none;
  }
  .nav-item {
    position: relative;
  }
  .nav-link {
    text-decoration: none;
    color: var(--text);
    font-weight: 500;
    padding: 12px 16px;
    border-radius: 4px;
    transition: background 0.2s;
    display: flex;
    align-items: center;
    cursor: pointer;
    background: none;
    border: none;
    font-size: 1em;
    line-height: 1.2;
  }
  .nav-link:hover {
    background: var(--nav-hover);
  }
  .arrow {
    margin-left: 6px;
    font-size: 0.8em;
    transition: transform 0.2s;
  }
  .dropdown {
    display: none;
    position: absolute;
    top: 100%;
    right: 0;
    background: var(--header-bg);
    min-width: 180px;
    box-shadow: var(--dropdown-shadow);
    border-radius: 6px;
    z-index: 1001;
    padding: 8px 0;
    flex-direction: column;
  }
  .dropdown a {
    display: block;
    padding: 10px 20px;
    color: var(--text);
    text-decoration: none;
    font-size: 1em;
    transition: background 0.2s;
  }
  .dropdown a:hover {
    background: var(--nav-hover);
  }
  .nav-item:hover .dropdown,
  .nav-item:focus-within .dropdown {
    display: flex;
  }
  .nav-item.force-close .dropdown {
    display: none !important;
  }
  .nav-item:focus-within .arrow,
  .nav-item:hover .arrow {
    transform: rotate(180deg);
  }
  .mobile-menu,
  .overlay {
    display: none !important;
  }
  .hero {
    height: 420px;
  }
  .hero-content {
    align-self: start;
    padding-top: 60px;
    padding-left: 40px;
    max-width: 1100px;
  }
  .section {
    padding: 64px 0;
  }
  .section-title {
    font-size: 2rem;
  }
  .section-content {
    max-width: 700px;
    font-size: 1.1rem;
  }
  .section--programs .section-title {
    font-size: clamp(2.4rem, 2rem + 1vw, 3.4rem);
  }
  .section--programs .program-title {
    font-size: clamp(1.6rem, 1.2rem + 1.1vw, 2rem);
  }
  .section--programs .program-desc {
    font-size: clamp(1.15rem, 1rem + 0.6vw, 1.3rem);
    line-height: 1.7;
  }
}

@media (min-width: 1600px) {
  .hero {
    align-items: flex-start;
  }
}

@media (prefers-reduced-motion: reduce) {
  .section--testimonials .t-track {
    scroll-behavior: auto;
  }
  .section--testimonials .t-btn {
    transition: none;
  }

  * {
    animation: none !important;
    transition: none !important;
  }
  .whatsapp-button:hover,
  .whatsapp-button:active {
    transform: none;
    animation: none;
  }
}

