def within_school_effect(df, outcome):
    effects={}
    for school, g in df.groupby('school'):
        t=g[g.group=="T"]; c=g[g.group=="C"]
        effects[school]=wmean(t[outcome],t.n)-wmean(c[outcome],c.n)
    return effects
effects_sleep1=within_school_effect(df,'sleep1')
effects_score1=within_school_effect(df,'score1')
effects_sleep_change=within_school_effect(df,'sleep_change') # may fail due to no column in df; let's compute in df.

