pop = pd.DataFrame({
'stratum':['A10','A11','B10','B11'],
'N':[180,150,220,200],
})
sample = pd.DataFrame({
'stratum':['A10','A11','B10','B11'],
'n':[18,15,22,20],
'sum_self':[129.6,103.5,165.0,138.0]
})
dfm=pop.merge(sample,on='stratum')
dfm['mean_self']=dfm.sum_self/dfm.n
strat_est=(dfm.N*dfm.mean_self).sum()/dfm.N.sum()
dfm, strat_est

