\documentclass[12pt]{article}

\usepackage[a4paper,margin=1in]{geometry}
\usepackage{amsmath,amssymb}
\usepackage{enumitem}

\setlength{\parindent}{0pt}
\setlength{\parskip}{8pt}

\begin{document}

\begin{enumerate}[label=\textbf{\arabic*.}, leftmargin=*]

% ------------------------------------------------------------
\item \textbf{Factorise the following.}

\begin{enumerate}[label=\textbf{(\alph*)}, leftmargin=*]

\item
\begin{align*}
(a+3b)x+(a+3b)y
&=(a+3b)(x+y).
\end{align*}

\item
\begin{align*}
m(2p-q)-3n(2p-q)
&=(2p-q)(m-3n).
\end{align*}

\item
\begin{align*}
3x(5-a)+(5-a)
&=(5-a)(3x+1).
\end{align*}

\item
\begin{align*}
4y(b-2)-(b-2)
&=(b-2)(4y-1).
\end{align*}

\item
\begin{align*}
a(x-2y)+3b(2y-x)
&=a(x-2y)+3b\bigl(-(x-2y)\bigr)\\
&=a(x-2y)-3b(x-2y)\\
&=(a-3b)(x-2y).
\end{align*}

\item
\begin{align*}
5c(4r-s)-d(s-4r)
&=5c(4r-s)-d\bigl(-(4r-s)\bigr)\\
&=5c(4r-s)+d(4r-s)\\
&=(5c+d)(4r-s).
\end{align*}

\item
\begin{align*}
4m(3a+b)+8n(3a+b)
&=(3a+b)(4m+8n)\\
&=(3a+b)\cdot 4(m+2n)\\
&=4(3a+b)(m+2n).
\end{align*}

\item
\begin{align*}
6ab(p-2q)-3ac(p-2q)
&=(p-2q)(6ab-3ac)\\
&=(p-2q)\cdot 3a(2b-c)\\
&=3a(p-2q)(2b-c).
\end{align*}

\end{enumerate}

% ------------------------------------------------------------
\item \textbf{Factorise the following completely.}

\begin{enumerate}[label=\textbf{(\alph*)}, leftmargin=*]

\item
\begin{align*}
5r+ar+5s+as
&=(5r+ar)+(5s+as)\\
&=r(5+a)+s(5+a)\\
&=(5+a)(r+s).
\end{align*}

\item
\begin{align*}
ax-3ay+bx-3by
&=(ax-3ay)+(bx-3by)\\
&=a(x-3y)+b(x-3y)\\
&=(a+b)(x-3y).
\end{align*}

\item
\begin{align*}
8px-6p+4qx-3q
&=(8px-6p)+(4qx-3q)\\
&=2p(4x-3)+q(4x-3)\\
&=(4x-3)(2p+q).
\end{align*}

\item
\begin{align*}
10px+15qx+8py+12qy
&=(10px+15qx)+(8py+12qy)\\
&=5x(2p+3q)+4y(2p+3q)\\
&=(2p+3q)(5x+4y).
\end{align*}

\item
\begin{align*}
21mx-7kx+6my-2ky
&=(21mx-7kx)+(6my-2ky)\\
&=7x(3m-k)+2y(3m-k)\\
&=(3m-k)(7x+2y).
\end{align*}

\item
\begin{align*}
5px-6py+25qx-30qy
&=(5px+25qx)+(-6py-30qy)\\
&=5x(p+5q)-6y(p+5q)\\
&=(p+5q)(5x-6y).
\end{align*}

\end{enumerate}

% ------------------------------------------------------------
\item \textbf{Factorise the following completely.}

\begin{enumerate}[label=\textbf{(\alph*)}, leftmargin=*]

\item
\begin{align*}
cv-c+v-1
&=(cv-c)+(v-1)\\
&=c(v-1)+1(v-1)\\
&=(c+1)(v-1).
\end{align*}

\item
\begin{align*}
1+2r-k-2kr
&=(1-k)+(2r-2kr)\\
&=(1-k)+2r(1-k)\\
&=(1-k)(1+2r).
\end{align*}

\item
\begin{align*}
au-5b+ab-5u
&=au-5u+ab-5b\\
&=u(a-5)+b(a-5)\\
&=(a-5)(u+b).
\end{align*}

\item
\begin{align*}
12ax-by+4ay-3bx
&=(12ax-3bx)+(4ay-by)\\
&=3x(4a-b)+y(4a-b)\\
&=(4a-b)(3x+y).
\end{align*}

\item
\begin{align*}
3m-6n+36nt-18mt
&=(3m-18mt)+(-6n+36nt)\\
&=3m(1-6t)-6n(1-6t)\\
&=(1-6t)(3m-6n)\\
&=(1-6t)\cdot 3(m-2n)\\
&=3(1-6t)(m-2n).
\end{align*}

\item
\begin{align*}
3apx+6apy-bpx-2bpy
&=(3apx+6apy)+(-bpx-2bpy)\\
&=3ap(x+2y)-bp(x+2y)\\
&=(x+2y)(3ap-bp)\\
&=(x+2y)\cdot p(3a-b)\\
&=p(x+2y)(3a-b).
\end{align*}

\item
\begin{align*}
6p^2-3p+2ps-s
&=(6p^2-3p)+(2ps-s)\\
&=3p(2p-1)+s(2p-1)\\
&=(2p-1)(3p+s).
\end{align*}

\item
\begin{align*}
5p^2-8q+2p-20pq
&=(5p^2+2p)+(-20pq-8q)\\
&=p(5p+2)-4q(5p+2)\\
&=(5p+2)(p-4q).
\end{align*}

\end{enumerate}

\end{enumerate}

\end{document}
