\section*{Section 1: Exercise 1A}

\begin{enumerate}
\item Simplify each of the following expressions.
  \begin{enumerate}
  \item $\dfrac{5}{6}x+\dfrac{1}{3}x$
  \item $\dfrac{7}{8}y-\dfrac{1}{4}y$
  \item $\dfrac{3m}{5}+\dfrac{m}{10}$
  \item $\dfrac{9p}{4}-\dfrac{p}{8}$
  \end{enumerate}

\item Simplify each of the following expressions.
  \begin{enumerate}
  \item $\dfrac{3}{5}x+\dfrac{1}{4}y-\dfrac{1}{10}x-\dfrac{3}{8}y$
  \item $\dfrac{1}{2}a-\dfrac{2}{7}b+\dfrac{3}{4}a+\dfrac{5}{14}b$
  \item $\dfrac{4}{9}c-\dfrac{1}{6}d+\dfrac{1}{3}c+\dfrac{5}{12}d$
  \item $3f-\dfrac{7}{4}h+\dfrac{5}{2}h-\dfrac{3}{5}f$
  \end{enumerate}

\item Expand and simplify each of the following expressions.
  \begin{enumerate}
  \item $\dfrac{3}{4}(8x-12)$
  \item $\dfrac{2}{3}(9p+6)$
  \item $\dfrac{1}{4}\bigl[12a-5(2a-4b)\bigr]$
  \item $\dfrac{1}{2}\bigl[6x-4-2(3-5x)\bigr]$
  \end{enumerate}

\item Express each of the following as a fraction in its simplest form.
  \begin{enumerate}
  \item $\dfrac{3t}{7}+\dfrac{2}{5}(t-1)$
  \item $\dfrac{5x-1}{6}-\dfrac{x+4}{9}$
  \item $\dfrac{2y+5}{8}+\dfrac{3y-1}{12}$
  \item $\dfrac{1}{3}(a+2)-\dfrac{1}{4}(2a-5)$
  \end{enumerate}

\item Expand and simplify each of the following expressions.
  \begin{enumerate}
  \item $x-\dfrac{3}{5}(10x-5y)$
  \item $-\dfrac{1}{4}\bigl[8(m-n)-2(3m+n)\bigr]$
  \end{enumerate}

\item Express each of the following as a fraction in its simplest form.
  \begin{enumerate}
  \item $\dfrac{5(x-2)}{3}+\dfrac{7(2x+1)}{4}$
  \item $\dfrac{a+1}{2}+\dfrac{a-3}{5}-\dfrac{3a+4}{10}$
  \end{enumerate}
\end{enumerate}

