\section*{Section 2: Exercise 1B}

\begin{enumerate}
\item Solve each of the following equations.
  \begin{enumerate}
  \item $\dfrac14 x=9$
  \item $\dfrac56 y=-10$
  \item $\dfrac15 x+7=3$
  \item $\dfrac{z}{3}-5=1$
  \item $4p-\dfrac{7}{2}=5$
  \item $6-\dfrac35 q=1.8$
  \end{enumerate}

\item Solve each of the following equations.
  \begin{enumerate}
  \item $x=18-\dfrac12 x$
  \item $\dfrac34 y=\dfrac12 y+5$
  \item $\dfrac{z}{3}+2=\dfrac{z}{6}+5$
  \item $\dfrac35 p-1=\dfrac15 p+3$
  \end{enumerate}

\item Solve each of the following equations.
  \begin{enumerate}
  \item $\dfrac{3}{a}=\dfrac14$
  \item $\dfrac{15}{b-2}=-5$
  \end{enumerate}

\item A smaller number is $\dfrac35$ of a larger number. The sum of the two numbers is $64$.
  \begin{enumerate}[label=(\roman*)]
  \item Find the larger number.
  \item Hence find the smaller number.
  \end{enumerate}

\item Solve each of the following equations.
  \begin{enumerate}
  \item $\dfrac{4x-1}{3}=5$
  \item $\dfrac{2y+3}{5}=\dfrac{y-1}{2}$
  \item $\dfrac13(6p-9)=\dfrac12(p+3)$
  \item $\dfrac{3q+6}{4}-\dfrac{q+2}{2}=1$
  \end{enumerate}

\item
  \begin{enumerate}
  \item Given that $x=\dfrac32$ is a solution of $5x-2=kx+1$, find $k$.
  \item If $3x+2y=5y$, find $\dfrac{x}{y}$.
  \end{enumerate}
\end{enumerate}

