The following files are files in my custom wordpress theme.

Enhance the header menu so that on mobile devices, a hamburger icon is displayed in the header, and the menu items are only displayed as dropdown items when the icon is clicked.

---


FILE: style.css

/*
Theme Name: My Custom Theme
Theme URI: https://example.com/
Author: Your Name
Author URI: https://example.com/
Description: A custom WordPress theme with mobile-friendly design, security, and SEO optimization.
Version: 1.0
License: GNU General Public License v2 or later
License URI: https://www.gnu.org/licenses/gpl-2.0.html
Text Domain: my-custom-theme
*/

@import url('https://fonts.googleapis.com/css?family=Open+Sans:400,700&display=swap');

/* CSS Variables for Color Schemes */
:root {
    /* Header and Footer */
    --header-bg: #000000;
    --header-text-light-gray: #bdbdbd;
    --header-text-orange: #f7931e;
    --header-text-yellow: #fec34e;
    --header-text-white: #ffffff;

    /* Landing Page Content - Darker Gray */
    --darker-gray-bg: #eaeaea;
    --darker-gray-text-orange: #ff9900;
    --darker-gray-text-gray: #676767;

    /* Landing Page Content - Lighter Gray */
    --lighter-gray-bg: #fafafa;
    --lighter-gray-text-orange1: #ff9900;
    --lighter-gray-text-dark-gray: #676767;
    --lighter-gray-text-orange2: #f7941e;
    --lighter-gray-text-blue: #4a8cf6;
    --lighter-gray-text-green: #00a857;

    /* General */
    --font-family: 'Open Sans', sans-serif;
}

/* Reset and Base Styles */
*,
*::before,
*::after {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: var(--font-family);
    line-height: 1.6;
    color: var( --lighter-gray-bg);
}

/* Fixed Header */
header {
    position: fixed;
    top: 0;
    width: 100%;
    background-color: var(--header-bg);
    color: var(--header-text-light-gray);
    padding: 10px 20px;
    z-index: 1000;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

header a {
    color: var(--header-text-white);
    text-decoration: none;
    margin-left: 15px;
}

header a:hover {
    color: var(--header-text-orange);
}

/* Main Content Padding to Avoid Overlap with Fixed Header */
.main-content {
    padding-top: 60px;
}

/* Section Styles */
.section {
    width: 100%;
    padding: 60px 20px;
}

.darker-gray {
    background-color: var(--darker-gray-bg);
    color: var(--darker-gray-text-gray);
}

.darker-gray h2,
.darker-gray h3 {
    color: var(--darker-gray-text-orange);
}

.lighter-gray {
    background-color: var(--lighter-gray-bg);
    color: var(--lighter-gray-text-dark-gray);
}

.lighter-gray h2,
.lighter-gray h3 {
    color: var(--lighter-gray-text-orange1);
}

.lighter-gray .highlight-orange {
    color: var(--lighter-gray-text-orange2);
}

.lighter-gray .highlight-blue {
    color: var(--lighter-gray-text-blue);
}

.lighter-gray .highlight-green {
    color: var(--lighter-gray-text-green);
}

/* Fixed Footer */
footer {
    background-color: var(--header-bg);
    color: var(--header-text-light-gray);
    text-align: center;
    padding: 20px;
    position: relative;
    width: 100%;
    clear: both;
}

/* Responsive Design */
@media (max-width: 768px) {
    /* Mobile-specific styles */

    .desktop-only {
        display: none;
    }

    .mobile-only {
        display: block;
    }

    header {
        flex-direction: column;
        align-items: flex-start;
    }

    .section {
        padding: 40px 10px;
    }

    header nav ul {
        flex-direction: column;
    }

    header nav ul li ul {
        position: static;
    }

}



/* Hide on mobile */
.desktop-only {
    display: block;
}

.mobile-only {
    display: none;
}


/* Navigation Menu Styles */
header nav {
    position: relative;
}

header nav ul {
    list-style: none;
    display: flex;
}

header nav ul li {
    position: relative;
}

header nav ul li a {
    color: var(--header-text-white);
    padding: 10px 15px;
    display: block;
    text-decoration: none;
}

header nav ul li a:hover {
    color: var(--header-text-orange);
}

/* Submenu Styles */
header nav ul li ul {
    display: none;
    position: absolute;
    top: 100%;
    left: 0;
    background-color: var(--header-bg);
    min-width: 200px;
    z-index: 999;
}

header nav ul li:hover > ul {
    display: block;
}

header nav ul li ul li {
    width: 100%;
}

header nav ul li ul li a {
    padding: 10px 15px;
    color: var(--header-text-white);
}

header nav ul li ul li a:hover {
    background-color: var(--header-bg);
    color: var(--header-text-orange);
}

