FILE: functions.php

<?php
// functions.php

// Exit if accessed directly
if ( ! defined( 'ABSPATH' ) ) {
    exit;
}

/**
 * Theme setup
 */
function my_custom_theme_setup() {
    // Add support for dynamic title tag
    add_theme_support( 'title-tag' );

    // Add support for post thumbnails
    add_theme_support( 'post-thumbnails' );

    // Register navigation menu
    register_nav_menus( array(
        'primary' => __( 'Primary Menu', 'my-custom-theme' ),
    ) );

    // Add support for HTML5
    add_theme_support( 'html5', array(
        'search-form',
        'comment-form',
        'comment-list',
        'gallery',
        'caption',
    ) );

    // Add support for custom logo
    add_theme_support( 'custom-logo' );

    // Add support for Gutenberg wide images
    add_theme_support( 'align-wide' );
}
add_action( 'after_setup_theme', 'my_custom_theme_setup' );

/**
 * Enqueue scripts and styles
 */
function my_custom_theme_enqueue_scripts() {
    // Enqueue main stylesheet
    wp_enqueue_style( 'my-custom-theme-style', get_stylesheet_uri(), array(), wp_get_theme()->get( 'Version' ) );

    // Enqueue additional CSS if needed
    // wp_enqueue_style( 'custom-style', get_template_directory_uri() . '/css/custom.css', array(), '1.0' );

    // Enqueue JavaScript only if necessary (minimize JS usage)
    // wp_enqueue_script( 'my-custom-theme-js', get_template_directory_uri() . '/js/main.js', array(), '1.0', true );
}
add_action( 'wp_enqueue_scripts', 'my_custom_theme_enqueue_scripts' );

/**
 * Register custom widgets
 */
function my_custom_theme_register_widgets() {
    require_once get_template_directory() . '/widgets/custom-widget.php';
    register_widget( 'Custom_Widget' );
}
add_action( 'widgets_init', 'my_custom_theme_register_widgets' );

/**
 * Register widget areas (if needed)
 */
function my_custom_theme_widgets_init() {
    register_sidebar( array(
        'name'          => __( 'Sidebar', 'my-custom-theme' ),
        'id'            => 'sidebar-1',
        'description'   => __( 'Add widgets here.', 'my-custom-theme' ),
        'before_widget' => '<div id="%1$s" class="widget %2$s">',
        'after_widget'  => '</div>',
        'before_title'  => '<h2 class="widget-title">',
        'after_title'   => '</h2>',
    ) );
}
add_action( 'widgets_init', 'my_custom_theme_widgets_init' );

/**
 * Optimize for SEO
 */
function my_custom_theme_seo_setup() {
    // Ensure theme is compatible with Yoast SEO
    add_theme_support( 'yoast-seo-breadcrumbs' );
}
add_action( 'after_setup_theme', 'my_custom_theme_seo_setup' );


/**
 * Register multiple homepage widget areas.
 */
function my_custom_theme_homepage_widgets_init() {
    register_sidebar( array(
        'name'          => __( 'Homepage Section 1', 'my-custom-theme' ),
        'id'            => 'homepage-section-1',
        'description'   => __( 'Add widgets here for the first section of the homepage.', 'my-custom-theme' ),
        'before_widget' => '<div id="%1$s" class="widget %2$s">',
        'after_widget'  => '</div>',
    ) );

    register_sidebar( array(
        'name'          => __( 'Homepage Section 2', 'my-custom-theme' ),
        'id'            => 'homepage-section-2',
        'description'   => __( 'Add widgets here for the second section of the homepage.', 'my-custom-theme' ),
        'before_widget' => '<div id="%1$s" class="widget %2$s">',
        'after_widget'  => '</div>',
    ) );

    register_sidebar( array(
        'name'          => __( 'Homepage Section 3', 'my-custom-theme' ),
        'id'            => 'homepage-section-3',
        'description'   => __( 'Add widgets here for the third section of the homepage.', 'my-custom-theme' ),
        'before_widget' => '<div id="%1$s" class="widget %2$s">',
        'after_widget'  => '</div>',
    ) );

    // Register more sections as needed
}
add_action( 'widgets_init', 'my_custom_theme_homepage_widgets_init' );


/**
 * Create a default menu if none exists
 */
function my_custom_theme_create_default_menu() {
    // Check if the menu already exists
    $menu_name = 'Primary Menu';
    $menu_exists = wp_get_nav_menu_object( $menu_name );

    if ( !$menu_exists ) {
        // Create the menu
        $menu_id = wp_create_nav_menu( $menu_name );

        // Define default menu items
        $menu_items = array(
            array(
                'menu-item-title'   =>  'Home',
                'menu-item-classes' => 'home',
                'menu-item-url'     => home_url('/'),
                'menu-item-status'  => 'publish'
            ),
            array(
                'menu-item-title'   =>  'About',
                'menu-item-classes' => 'about',
                'menu-item-url'     => home_url('/about/'),
                'menu-item-status'  => 'publish'
            ),
            array(
                'menu-item-title'   =>  'Services',
                'menu-item-classes' => 'services',
                'menu-item-url'     => home_url('/services/'),
                'menu-item-status'  => 'publish'
            ),
            array(
                'menu-item-title'   =>  'Contact',
                'menu-item-classes' => 'contact',
                'menu-item-url'     => home_url('/contact/'),
                'menu-item-status'  => 'publish'
            )
        );

        // Add menu items to the menu
        foreach ( $menu_items as $item ) {
            wp_update_nav_menu_item( $menu_id, 0, $item );
        }

        // Add sub-items under 'Services'
        $services_menu_item = wp_get_nav_menu_items( $menu_id, array( 'title' => 'Services' ) );
        if ( $services_menu_item ) {
            $services_item_id = $services_menu_item[0]->ID;

            $sub_menu_items = array(
                array(
                    'menu-item-title'   =>  'Web Development',
                    'menu-item-url'     => home_url('/services/web-development/'),
                    'menu-item-status'  => 'publish',
                    'menu-item-parent-id' => $services_item_id
                ),
                array(
                    'menu-item-title'   =>  'SEO Optimization',
                    'menu-item-url'     => home_url('/services/seo-optimization/'),
                    'menu-item-status'  => 'publish',
                    'menu-item-parent-id' => $services_item_id
                ),
                array(
                    'menu-item-title'   =>  'Digital Marketing',
                    'menu-item-url'     => home_url('/services/digital-marketing/'),
                    'menu-item-status'  => 'publish',
                    'menu-item-parent-id' => $services_item_id
                )
            );

            foreach ( $sub_menu_items as $sub_item ) {
                wp_update_nav_menu_item( $menu_id, 0, $sub_item );
            }
        }

        // Assign the menu to the 'primary' location
        $locations = get_theme_mod( 'nav_menu_locations' );
        $locations['primary'] = $menu_id;
        set_theme_mod( 'nav_menu_locations', $locations );
    }
}
add_action( 'after_setup_theme', 'my_custom_theme_create_default_menu' );


