Enhance the "Clear" and "Create/Update" buttons rendered by the HTML page with the following functionality:

- For "Clear", reset all the entries in the Create/Update Book table that was displayed by the HTML page
- For "Create/Update", 
  - if the `Bid` value does not correspond to any of the existing entries in the `books` array, append a new entry to the array and call the renderBooksTable() function. The net effect should be a complete refresh of the View All Books table displayed by the HTML page
  - if the `Bid` value corresponds to one of the existing entries in the `books` array, change the existing entry in the array and call the renderBooksTable() function.


---

FILE: scripts/book.js

var books = [
    { 'Bid': 'B001', 'Bname': 'The Great Gatsby', 'Bpd': '2022-06-15', 'Bcountry': 'USA', 'Bprice': 15.99 },
    { 'Bid': 'B002', 'Bname': 'To Kill a Mockingbird', 'Bpd': '2022-07-05', 'Bcountry': 'USA', 'Bprice': 12.50 },
    { 'Bid': 'B003', 'Bname': 'Pride and Prejudice', 'Bpd': '2022-06-30', 'Bcountry': 'England', 'Bprice': 10.99 },
    { 'Bid': 'B004', 'Bname': '1984', 'Bpd': '2022-08-10', 'Bcountry': 'UK', 'Bprice': 14.75 },
    { 'Bid': 'B005', 'Bname': 'The Catcher in the Rye', 'Bpd': '2022-07-20', 'Bcountry': 'USA', 'Bprice': 13.25 }
];


function renderBooksTable() {
    // Select the table body of the booksTable
    const tbody = document.querySelector('#booksTable tbody');

    // Clear any existing rows
    tbody.innerHTML = '';

    // Loop through each book in the books array
    books.forEach((book, index) => {
        // Create a new table row
        const row = document.createElement('tr');

        // Create and append cells for Bid, Bname, Bpd, Bcountry, and Bprice
        const cellBid = document.createElement('td');
        cellBid.textContent = book.Bid;
        row.appendChild(cellBid);

        const cellBname = document.createElement('td');
        cellBname.textContent = book.Bname;
        row.appendChild(cellBname);

        const cellBpd = document.createElement('td');
        cellBpd.textContent = book.Bpd;
        row.appendChild(cellBpd);

        const cellBcountry = document.createElement('td');
        cellBcountry.textContent = book.Bcountry;
        row.appendChild(cellBcountry);

        const cellBprice = document.createElement('td');
        // Format the price to two decimal places
        cellBprice.textContent = book.Bprice.toFixed(2);
        row.appendChild(cellBprice);

        // Create the Action cell with text hyperlinks for Update and Delete
        const cellAction = document.createElement('td');

        // Update hyperlink
        const updateLink = document.createElement('a');
        updateLink.href = "#";
        updateLink.textContent = 'Update';
        updateLink.onclick = function () {
            updateBook(book.Bid);
            return false; // Prevent default link behavior
        };
        cellAction.appendChild(updateLink);

        // Add a pipe separator between the links
        cellAction.appendChild(document.createTextNode(' | '));

        // Delete hyperlink
        const deleteLink = document.createElement('a');
        deleteLink.href = "#";
        deleteLink.textContent = 'Delete';
        deleteLink.onclick = function () {
            deleteBook(book.Bid);
            return false; // Prevent default link behavior
        };
        cellAction.appendChild(deleteLink);

        // Append the action cell to the row
        row.appendChild(cellAction);

        // Append the row to the table body
        tbody.appendChild(row);
    });
}

// Copies the book attributes to the Create/Update form
function updateBook(bookId) {
    // Find the book in the books array by its Bid
    var book = books.find(function (b) { return b.Bid === bookId; });
    if (book) {
        // Populate the form fields with the book's details
        document.getElementById('Bid').value = book.Bid;
        document.getElementById('Bname').value = book.Bname;
        document.getElementById('Bpd').value = book.Bpd;
        document.getElementById('Bcountry').value = book.Bcountry;
        document.getElementById('Bprice').value = book.Bprice;
    }
}

// Removes the book from the books array and re-renders the table
function deleteBook(bookId) {
    // Find the index of the book with the given Bid
    var index = books.findIndex(function (b) { return b.Bid === bookId; });
    if (index !== -1) {
        // Remove the book from the array
        books.splice(index, 1);
        // Re-render the table to reflect the changes
        renderBooksTable();
    }
}

document.addEventListener('DOMContentLoaded', () => {
    renderBooksTable();
});

