document.addEventListener('DOMContentLoaded', () => {
    renderBooksTable();

    // Reference the Create/Update form
    const bookForm = document.getElementById('bookForm');

    // Intercept the form submit for Create/Update functionality
    bookForm.addEventListener('submit', (event) => {
        event.preventDefault(); // Prevent the default form submission

        // Read the form values
        const bid = document.getElementById('Bid').value.trim();
        const bname = document.getElementById('Bname').value.trim();
        const bpd = document.getElementById('Bpd').value;
        const bcountry = document.getElementById('Bcountry').value;
        const bprice = parseFloat(document.getElementById('Bprice').value);

        if (!bid) {
            // Optionally, you could show an error message if Bid is empty.
            return;
        }

        // Check if the Bid already exists in the books array
        const index = books.findIndex(book => book.Bid === bid);
        if (index === -1) {
            // If not found, add a new entry
            books.push({
                'Bid': bid,
                'Bname': bname,
                'Bpd': bpd,
                'Bcountry': bcountry,
                'Bprice': bprice
            });
        } else {
            // If found, update the existing book entry
            books[index] = {
                'Bid': bid,
                'Bname': bname,
                'Bpd': bpd,
                'Bcountry': bcountry,
                'Bprice': bprice
            };
        }

        // Refresh the books table
        renderBooksTable();

        // Clear the form entries after Create/Update
        bookForm.reset();
    });

    // Optional: Enhance the Clear button (reset) to clear any additional states if needed.
    bookForm.addEventListener('reset', (event) => {
        // In addition to the default reset behavior, you can clear any custom state or messages here.
        // For now, this simply ensures the form fields are cleared.
        // You could also set defaults here if needed.
    });
});
