def schur(a,b,c,r):
    return a**r*(a-b)*(a-c)+b**r*(b-c)*(b-a)+c**r*(c-a)*(c-b)
def schur_alt(a,b,c,r):
    return 0.5*((a**(r-1)+b**(r-1))*(a-b)**2*(a+b-c) +
                (b**(r-1)+c**(r-1))*(b-c)**2*(b+c-a) +
                (c**(r-1)+a**(r-1))*(c-a)**2*(c+a-b))
a,b,c=sp.symbols('a b c', positive=True)
sp.simplify(schur(a,b,c,2)-schur_alt(a,b,c,2))

