def poly_div(f,g):
    # divide f by g, return remainder list
    f=f.copy()
    while len(f)>=len(g):
        if f[-1]==0:
            f.pop()
            continue
        deg_diff=len(f)-len(g)
        factor = f[-1]*pow(g[-1],-1,3) %3
        for i,b in enumerate(g):
            idx=i+deg_diff
            f[idx] = (f[idx] - factor*b) %3
        if f[-1]==0:
            f.pop()
    return poly_normalize(f)

def modulus_zero(rem):
    return len(rem)==1 and rem[0]==0

def poly_divides(f,g):
    return modulus_zero(poly_div(f,g))

