from itertools import product

symbols=[0,1,2]

def backtrack(pos, codewords, agreement_counts, zeros_counts):
    # codewords: list of lists length 4 each prefix length pos
    # agreement_counts: dict pair tuple -> count
    # zeros_counts: list length 4
    n=20
    if pos==n:
        # check weight constraint
        if all(zeros<=3 for zeros in zeros_counts):
            return codewords
        else:
            return None
    # choose column
    for col in product(symbols, repeat=4):
        # update counts
        new_agree=agreement_counts.copy()
        valid=True
        for i in range(4):
            for j in range(i+1,4):
                if col[i]==col[j]:
                    new_agree[(i,j)]+=1
                    if new_agree[(i,j)]>3:
                        valid=False
                        break
            if not valid:
                break
        if not valid:
            continue
        new_zeros=list(zeros_counts)
        for i in range(4):
            if col[i]==0:
                new_zeros[i]+=1
                if new_zeros[i]>3:
                    valid=False
                    break
        if not valid:
            continue
        # optimistic bound: remaining positions = n-pos-1
        remaining=n-pos-1
        # minimal possible additional agreements is 0 if we choose appropriate columns; but we cannot exceed 3.
        for pair,count in new_agree.items():
            if count >3:
                valid=False
                break
        if not valid:
            continue
        # proceed
        res=backtrack(pos+1,
                      [cw+[col[i]] for i,cw in enumerate(codewords)],
                      new_agree,
                      new_zeros)
        if res is not None:
            return res
    return None

