## `assets/css/corporate-2.0.0.css`

```css
/* ========================================================================
   Corporate UI — Modular CSS (v2.0.0)
   Architecture: @layer tokens | base | a11y | components | sections | overrides
   - No markup changes needed; selectors preserved for current HTML.
   - Design tokens (light/dark) drive colors; components consume semantic vars.
   - Keep @layer order; it defines cascade priority intentionally.
   ======================================================================== */

@layer tokens, base, a11y, components, sections, overrides;

/* ===== TOKENS =========================================================== */
@layer tokens {
  /* --- Design Tokens (Palette + Semantic) --- */
  :root {
    /* Light palette */
    --light-bg: #f5f6fa;
    --light-bg-alt: #e1e3ea;
    --light-header: #fff;
    --light-footer: #222;
    --light-footer-text: #fff;
    --light-primary: #0057b8;
    --light-text: #111;
    --light-nav-hover: #e1e3ea;
    --light-overlay: rgba(0, 0, 0, 0.6);
    --light-hero-overlay: rgba(0, 0, 0, 0.6);
    --light-header-shadow: 0 2px 8px rgba(0, 0, 0, 0.04);
    --light-dropdown-shadow: 0 4px 16px rgba(0, 0, 0, 0.08);
    --light-mobile-menu-bg: #fff;
    --light-cta-bg: #fff;
    --light-cta-text: var(--light-primary);
    --light-cta-hover-bg: var(--light-primary);
    --light-cta-hover-text: #fff;

    /* Dark palette */
    --dark-bg: #23272f;
    --dark-bg-alt: #2e323a;
    --dark-header: #181a20;
    --dark-footer: #111;
    --dark-footer-text: #eee;
    --dark-primary: #4ea1ff;
    --dark-text: #f5f6fa;
    --dark-nav-hover: #353a42;
    --dark-overlay: rgba(0, 0, 0, 0.8);
    --dark-hero-overlay: rgba(0, 0, 0, 0.6);
    --dark-header-shadow: 0 2px 8px rgba(0, 0, 0, 0.18);
    --dark-dropdown-shadow: 0 4px 16px rgba(0, 0, 0, 0.18);
    --dark-mobile-menu-bg: rgba(30, 34, 40, 0.98);
    --dark-cta-bg: var(--dark-primary);
    --dark-cta-text: #fff;
    --dark-cta-hover-bg: #fff;
    --dark-cta-hover-text: var(--dark-primary);
    --dark-primary-strong: #2563eb;

    /* Semantic tokens (mapped to palette) */
    --bg: var(--light-bg);
    --bg-alt: var(--light-bg-alt);
    --header-bg: var(--light-header);
    --header-shadow: var(--light-header-shadow);
    --footer-bg: var(--light-footer);
    --footer-text: var(--light-footer-text);
    --primary: var(--light-primary);
    --text: var(--light-text);
    --nav-hover: var(--light-nav-hover);
    --overlay: var(--light-overlay);
    --hero-overlay: var(--light-hero-overlay);
    --dropdown-shadow: var(--light-dropdown-shadow);
    --mobile-menu-bg: var(--light-mobile-menu-bg);
    --cta-bg: var(--light-cta-bg);
    --cta-text: var(--light-cta-text);
    --cta-hover-bg: var(--light-cta-hover-bg);
    --cta-hover-text: var(--light-cta-hover-text);
    --divider: #e5e7eb;
  }

  /* Toggle semantic tokens under dark mode */
  :root[data-theme="dark"] {
    --bg: var(--dark-bg);
    --bg-alt: var(--dark-bg-alt);
    --header-bg: var(--dark-header);
    --header-shadow: var(--dark-header-shadow);
    --footer-bg: var(--dark-footer);
    --footer-text: var(--dark-footer-text);
    --primary: var(--dark-primary);
    --text: var(--dark-text);
    --nav-hover: var(--dark-nav-hover);
    --overlay: var(--dark-overlay);
    --hero-overlay: var(--dark-hero-overlay);
    --dropdown-shadow: var(--dark-dropdown-shadow);
    --mobile-menu-bg: var(--dark-mobile-menu-bg);
    --cta-bg: var(--dark-primary-strong);
    --cta-text: #fff;
    --cta-hover-bg: #fff;
    --cta-hover-text: var(--dark-primary-strong);
    --divider: #3a3f46;
  }

  /* Hero background base (visible pre-image-load) */
  :root[data-theme="light"] .hero,
  :root[data-theme="dark"] .hero {
    background-color: #b68144;
  }
}

/* ===== BASE ============================================================= */
@layer base {
  html { color-scheme: light dark; }

  *,
  *::before,
  *::after { box-sizing: border-box; }

  body {
    margin: 0;
    font-family: "Segoe UI", Arial, sans-serif;
    background: var(--bg);
    color: var(--text);
  }
}

/* ===== A11Y ============================================================= */
@layer a11y {
  /* Visually skip to main content; visible on focus */
  .skip-link {
    position: absolute;
    left: -9999px;
    top: auto;
    width: 1px;
    height: 1px;
    overflow: hidden;
  }
  .skip-link:focus {
    left: 8px;
    top: 8px;
    width: auto;
    height: auto;
    padding: 8px 12px;
    background: var(--header-bg);
    color: var(--text);
    border-radius: 4px;
    z-index: 2000;
  }

  /* Global keyboard focus indicator */
  :focus-visible {
    outline: 4px solid var(--primary);
    outline-offset: 0;
  }

  /* Respect users who prefer reduced motion */
  @media (prefers-reduced-motion: reduce) {
    * { animation: none !important; transition: none !important; }
  }
}

/* ===== COMPONENTS ======================================================= */
@layer components {
  /* --- Header (sticky) --- */
  .header {
    position: sticky;
    top: 0;
    background: var(--header-bg);
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 16px;
    height: 56px;
    z-index: 1000;
    box-shadow: var(--header-shadow);
    transition: background 0.3s;
  }

  /* Brand: logo + title */
  .brand {
    display: inline-flex;
    align-items: center;
    gap: 0.6rem;
    text-decoration: none;
    color: var(--text);
  }
  .brand:focus-visible { outline: 2px solid var(--primary); outline-offset: 2px; }

  .brand-title {
    font-weight: 700;
    line-height: 1;
    letter-spacing: 0.2px;
    font-size: clamp(0.95rem, 1rem + 0.5vw, 1.25rem);
    white-space: nowrap;
    max-width: 18ch;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  /* Default: show light logo; swap in dark mode */
  .logo-dark { display: none; }
  :root[data-theme="dark"] .logo-light { display: none; }
  :root[data-theme="dark"] .logo-dark { display: inline-block; }
  :root[data-theme="dark"] .brand img { filter: none; }

  .header-right-controls {
    display: flex;
    align-items: center;
    gap: 16px;
  }

  /* Theme toggle */
  .theme-toggle {
    background: none;
    border: none;
    font-size: 1.5em;
    cursor: pointer;
    color: var(--primary);
    transition: color 0.3s;
    flex-shrink: 0;
    padding: 0;
    line-height: 1;
  }

  /* Hamburger (mobile only) */
  .hamburger {
    display: flex;
    flex-direction: column;
    justify-content: center;
    width: 36px;
    height: 24px;
    background: none;
    border: none;
    cursor: pointer;
    flex-shrink: 0;
    padding: 0;
  }
  .hamburger span {
    display: block;
    height: 3px;
    width: 100%;
    background: var(--primary);
    margin: 2px 0;
    border-radius: 2px;
    transition: background 0.3s;
  }

  /* --- Nav (mobile-first) --- */
  .nav { display: none; align-items: center; } /* hidden on mobile; desktop at ≥1200px */

  /* Dropdowns (desktop) */
  .nav-item { position: relative; }
  .nav-link {
    text-decoration: none;
    color: var(--text);
    font-weight: 500;
    padding: 12px 16px;
    border-radius: 4px;
    transition: background 0.2s;
    display: flex;
    align-items: center;
    cursor: pointer;
    background: none;
    border: none;
    font-size: 1em;
    line-height: 1.2;
  }
  .nav-link:hover { background: var(--nav-hover); }
  .arrow { margin-left: 6px; font-size: 0.8em; transition: transform 0.2s; }

  .dropdown {
    display: none;
    position: absolute;
    top: 100%; right: 0;
    background: var(--header-bg);
    min-width: 180px;
    box-shadow: var(--dropdown-shadow);
    border-radius: 6px;
    z-index: 1001;
    padding: 8px 0;
    flex-direction: column;
  }
  .dropdown a {
    display: block;
    padding: 10px 20px;
    color: var(--text);
    text-decoration: none;
    font-size: 1em;
    transition: background 0.2s;
  }
  .dropdown a:hover { background: var(--nav-hover); }

  /* Keyboard-friendly open */
  .nav-item:hover .dropdown,
  .nav-item:focus-within .dropdown { display: flex; }
  .nav-item.force-close .dropdown { display: none !important; }
  .nav-item:focus-within .arrow,
  .nav-item:hover .arrow { transform: rotate(180deg); }

  /* --- Mobile menu overlay/sheet --- */
  .overlay {
    display: none;
    position: fixed;
    inset: 0;
    background: var(--overlay);
    z-index: 1100;
    transition: background 0.3s;
  }
  .overlay.active { display: block; }

  .mobile-menu {
    display: flex;
    flex-direction: column;
    position: fixed;
    top: 0; right: 0;
    width: 80vw; max-width: 340px;
    height: 100vh;
    background: var(--mobile-menu-bg);
    z-index: 1101;
    padding: 32px 0 0 0;
    box-shadow: -2px 0 16px rgba(0,0,0,0.12);
    transform: translateX(100%);
    transition: transform 0.3s;
    overflow-y: auto;
  }
  .mobile-menu.active { transform: translateX(0); }
  .mobile-menu ul { list-style: none; padding: 0; margin: 0; }
  .mobile-menu .nav-item { border-bottom: 1px solid var(--divider); position: relative; }
  .mobile-menu .nav-link {
    display: flex; align-items: center; justify-content: space-between;
    padding: 18px 28px;
    color: var(--text);
    text-decoration: none;
    font-size: 1.1em; font-weight: 500;
    background: none; border: none; width: 100%;
    cursor: pointer; transition: background 0.2s;
  }
  .mobile-menu :is(.nav-link, .dropdown a):hover { background: var(--nav-hover); }
  .mobile-menu .arrow { margin-left: 8px; font-size: 1em; transition: transform 0.2s; }
  .mobile-menu .dropdown {
    display: none; flex-direction: column;
    background: none; box-shadow: none; position: static; padding: 0 0 0 24px;
  }
  .mobile-menu .dropdown a {
    display: block; padding: 12px 0; color: var(--text);
    text-decoration: none; font-size: 1em; transition: background 0.2s;
  }
  .mobile-menu .nav-item.open > .dropdown { display: flex; }
  .mobile-menu .nav-item.open > .nav-link .arrow { transform: rotate(180deg); }

  /* --- Desktop layout tweaks --- */
  @media (min-width: 1200px) {
    .header { height: 72px; padding: 0 40px; }
    .logo img { max-height: 40px; }
    .theme-toggle { font-size: 1.7em; }
    .hamburger { display: none; }
    .nav { display: flex; align-items: center; gap: 0; }
    .nav ul { display: flex; align-items: center; gap: 0; margin: 0; padding: 0; list-style: none; }
    .mobile-menu, .overlay { display: none !important; }
  }
}

/* ===== SECTIONS ========================================================= */
@layer sections {
  /* --- HERO (image + overlay + centered content) --- */
  .hero {
    position: relative;
    display: grid; place-items: center;
    text-align: center;
    overflow: hidden;
    min-height: clamp(420px, 52vw, 640px);
    color: #fff;

    /* Hero-scoped tokens (theme-agnostic) */
    --hero-cta-bg: var(--dark-primary-strong);
    --hero-cta-text: #fff;
    --hero-cta-hover-bg: #8b0000;
    --hero-cta-hover-text: #fff;
    --hero-link: #7ee1ff;
    --hero-link-hover: #ff6d6f;
    --hero-link-underline: color-mix(in srgb, var(--hero-link) 60%, transparent);
  }
  .hero::after {
    content: "";
    position: absolute; inset: 0;
    background: var(--hero-overlay);
    z-index: 1;
  }

  .hero .hero-media { grid-area: 1/1; display: block; width: 100%; justify-self: stretch; }
  .hero .hero-media img {
    display: block; width: 100%; height: auto; object-position: center;
  }

  .hero-content {
    grid-area: 1/1;
    position: relative; z-index: 2;
    max-width: min(92vw, 980px);
    padding: clamp(16px, 3vw, 40px);
    display: flex; flex-direction: column; align-items: center;
    gap: clamp(8px, 1.5vw, 18px);
  }

  .hero-title {
    margin: 0; font-weight: 800; line-height: 1.1;
    font-size: clamp(1rem, 5.6vw, 3rem);
    white-space: nowrap; overflow: hidden; text-overflow: ellipsis;
  }

  .hero-bullets {
    list-style: none; margin: 6px 0 12px; padding: 0;
    display: inline-block; text-align: left;
  }
  .hero-bullets li {
    font-weight: 700; display: flex; margin: 6px 0;
    line-height: 1.6; font-size: clamp(1.12rem, 1rem + 1.35vw, 2rem);
    align-items: baseline; gap: clamp(15px, 2vw, 30px);
  }
  /* Scalable check icon (retina-safe) */
  .hero-bullets li::before {
    content: ""; display: inline-block; flex: none;
    width: 1em; height: 1em;
    background-image: url("../media/check-24.png");
    background-image: -webkit-image-set(
      url("../media/check-24.png") 1x,
      url("../media/check-48.png") 2x
    );
    background-image: image-set(
      url("../media/check-24.png") 1x,
      url("../media/check-48.png") 2x
    );
    background-repeat: no-repeat; background-position: center; background-size: contain;
    transform: translateY(0.06em);
    opacity: 1;
  }
  .hero-bullets a {
    color: var(--hero-link);
    font-weight: 700;
    text-decoration: underline;
    text-decoration-color: var(--hero-link-underline, currentColor);
    text-underline-offset: 3px;
  }
  .hero-bullets a:hover,
  .hero-bullets a:focus-visible {
    color: var(--hero-link-hover);
    text-decoration-color: var(--hero-link-hover);
  }

  /* Stacked hero CTAs (reused pill) */
  .hero-actions {
    display: flex; flex-direction: column; align-items: center;
    gap: clamp(12px, 4vh, 60px);
  }
  .hero-cta {
    display: inline-block;
    padding: 12px 28px; border-radius: 30px;
    background: var(--hero-cta-bg); color: var(--hero-cta-text);
    font-weight: 600; font-size: clamp(1.06rem, 0.6rem + 1vw, 1.375rem);
    line-height: 1.2; text-decoration: none;
    box-shadow: 0 0 0 1px #ffffff, 0 2px 8px rgba(0,0,0,0.08); /* visible keyline */
    transition: background 0.2s ease, color 0.2s ease, transform 0.06s ease-in-out;
  }
  .hero-cta:hover,
  .hero-cta:focus-visible { background: var(--hero-cta-hover-bg); color: var(--hero-cta-hover-text); }
  .hero-cta:active { transform: translateY(1px); }

  /* Hero responsiveness */
  @media (max-width: 767.98px) {
    .hero { align-items: flex-start; min-height: clamp(320px, 60vw, 420px); }
    .hero-content { padding-top: clamp(8px, 3vh, 16px); }
  }
  @media (min-width: 1200px) {
    .hero { height: 420px; }
    .hero-content { align-self: start; padding-top: 60px; padding-left: 40px; max-width: 1100px; }
  }
  @media (max-width: 549.98px) { .hero-cta--contact { display: none; } }
  @media (min-width: 1600px) { .hero { align-items: flex-start; } }

  /* High-contrast focus ring on dark hero */
  .hero :is(a, .hero-cta):focus-visible {
    outline: 3px solid #ffffff; /* ≥3:1 on dark overlay */
    outline-offset: 3px;
  }

  /* --- Generic Section shell --- */
  .section {
    width: 100%;
    padding: 36px 0;
    text-align: center;
    content-visibility: auto;
    contain-intrinsic-size: 500px; /* performance hint */
  }
  /* Alternating backgrounds (scope to main content only) */
  #main-content > .section:nth-child(even) { background: var(--bg-alt); }
  #main-content > .section:nth-child(odd)  { background: var(--bg); }

  .section-title {
    font-size: 1.2rem; margin-bottom: 12px; font-weight: 600;
  }
  .section-content {
    max-width: 90vw; margin: 0 auto; font-size: 1rem; line-height: 1.7;
  }

  /* Section links (theme-aware; scoped to main) */
  :root[data-theme="light"] #main-content .section a { color: #0b5bd3; }
  :root[data-theme="light"] #main-content .section a:hover,
  :root[data-theme="light"] #main-content .section a:focus-visible { color: #c00000; }
  :root[data-theme="dark"] #main-content .section a { color: #7ec1ff; }
  :root[data-theme="dark"] #main-content .section a:hover,
  :root[data-theme="dark"] #main-content .section a:focus-visible { color: #ff7578; }
  #main-content .section a {
    font-weight: 800; text-decoration: underline;
    text-decoration-thickness: 2px; text-underline-offset: 3px;
  }

  /* --- Split layout (About / Qualifications) --- */
  .section--split { text-align: left; }
  .section--split .split {
    display: grid; grid-template-columns: 1fr;
    align-items: center;
    gap: clamp(24px, 4vw, 56px);
    max-width: min(1160px, 86vw);
    margin: 0 auto;

    /* Mobile-first grid areas */
    grid-template-areas:
      "media"
      "text";
  }
  .section--split .split .split-media { grid-area: media; }
  .section--split .split .split-text  { grid-area: text; }

  .section--split .section-title {
    margin: 0 0 clamp(10px, 1.2vw, 18px);
    font-weight: 800; line-height: 1.1;
    font-size: clamp(2.1rem, 3.4vw, 3.1rem);
  }
  .section--split .lead {
    font-size: clamp(1.18rem, 1.05rem + 1.2vw, 1.6rem);
    line-height: 1.7; margin: 0 0 clamp(10px, 1.6vw, 20px);
  }
  .section--split .split-text p {
    font-size: clamp(1.06rem, 0.6rem + 1vw, 1.25rem);
    line-height: 1.7; margin: 0 0 clamp(10px, 1.4vw, 18px);
  }
  .section--split .split-text { max-width: 68ch; }
  .section--split .split-media img {
    display: block; width: 100%; height: auto;
    border-radius: 16px; box-shadow: 0 10px 24px rgba(0,0,0,0.18);
  }

  /* Orange accent card behind image */
  .section--split .split-media {
    position: relative; isolation: isolate; overflow: visible; z-index: 0;
  }
  .section--split .split-media.split-media--accent::after {
    content: "";
    position: absolute; inset: auto 0 0 auto; transform: translate(8%, 10%);
    inline-size: clamp(220px, 85%, 640px);
    aspect-ratio: 1.3;
    background: #f59e0b;
    border-radius: clamp(18px, 2.2vw, 24px);
    box-shadow: 0 12px 28px rgba(0,0,0,0.12);
    z-index: -1;
  }

  /* Split responsiveness + orientation helpers */
  @media (max-width: 1199.98px) {
    .section--split .lead { font-size: clamp(1.02rem, 0.9rem + 0.8vw, 1.35rem); line-height: 1.55; }
    .section--split .split-text p { font-size: clamp(1rem, 0.88rem + 0.7vw, 1.2rem); line-height: 1.55; }
  }
  @media (max-width: 899.98px) { .section--split .split-media { order: -1; } }
  @media (min-width: 600px) and (max-width: 899.98px) {
    .section--split .split { justify-items: center; }
    .section--split .split > * { justify-self: center; }
  }
  @media (min-width: 900px) {
    .section--split .split { grid-template-columns: 1.08fr 1fr; grid-template-areas: "media text"; }
    .section--split .split.split--image-right {
      grid-template-columns: 1fr 1.08fr; grid-template-areas: "text media";
    }
  }
  @media (max-width: 400px) { /* slightly shorter accent card on tiny phones */
    .section--split .split-media.split-media--accent::after { aspect-ratio: 1.6; }
  }

  /* Qualifications: larger list items */
  #qualifications .split-text ul {
    margin: 0 0 clamp(10px, 1.4vw, 18px);
    padding-left: clamp(22px, 2.2vw, 30px);
    list-style: disc outside;
  }
  #qualifications .split-text li,
  .split-text ul.list--lg > li {
    font-size: clamp(1.12rem, 0.72rem + 1.05vw, 1.35rem);
    line-height: 1.7; color: var(--text);
  }

  /* --- Programs (3-up cards) --- */
  .section--programs .section-title {
    margin: 0 0 clamp(10px, 1.2vw, 18px);
    font-weight: 800; line-height: 1.1;
    font-size: clamp(2.1rem, 3.4vw, 3.1rem);
  }
  .section--programs .programs-grid {
    max-width: min(1160px, 92vw); margin: 0 auto;
    display: grid; grid-template-columns: 1fr;
    gap: clamp(18px, 2.6vw, 36px);
  }
  @media (min-width: 900px) {
