  background-color: #b68144;
}

:root[data-theme="dark"] .hero {
  background-color: #b68144;
}

html {
  color-scheme: light dark;
}

/* === No‑JS mode: keep only brand + theme toggle visible === */
.no-js .nav,
.no-js .hamburger,
.no-js .overlay,
.no-js .mobile-menu {
