body {
  margin: 0;
  font-family: "Segoe UI", Arial, sans-serif;
  background: var(--bg);
  color: var(--text);
}

/* ===== Header ===== */
.header {
  position: sticky;
  top: 0;
  background: var(--header-bg);
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 16px;
  height: 56px;
  z-index: 1000;
  box-shadow: var(--header-shadow);
  transition: background 0.3s;
}

/* default: show light logo, hide dark one */
.logo-dark {
  display: none;
}

:root[data-theme="dark"] .logo-light {
  display: none;
}

:root[data-theme="dark"] .logo-dark {
  display: inline-block;
}

:root[data-theme="dark"] .brand img {
  filter: none;
}

/* Brand area aligns logo + short title on one line */
.brand {
  display: inline-flex;
  align-items: center;
  gap: 0.6rem;
  text-decoration: none;
  color: var(--text);
}

.brand:focus-visible {
  outline: 2px solid var(--primary);
  outline-offset: 2px;
}

/* Short site title: compact, bold, respects theme colors */
.brand-title {
  font-weight: 700;
  line-height: 1;
  letter-spacing: 0.2px;
  font-size: clamp(0.95rem, 1rem + 0.5vw, 1.25rem);
  white-space: nowrap;
  max-width: 18ch;
  overflow: hidden;
  text-overflow: ellipsis;
}

.header-right-controls {
  display: flex;
  align-items: center;
  gap: 16px;
}

/* Light/Dark Toggle */
.theme-toggle {
  background: none;
  border: none;
  font-size: 1.5em;
  cursor: pointer;
  color: var(--primary);
  transition: color 0.3s;
  flex-shrink: 0;
  padding: 0;
  line-height: 1;
}

/* ===== Hamburger ===== */
.hamburger {
  display: flex;
  /* Default for mobile */
  flex-direction: column;
  justify-content: center;
  width: 36px;
  height: 24px;
  background: none;
  border: none;
  cursor: pointer;
  flex-shrink: 0;
  padding: 0;
}

.hamburger span {
  display: block;
  height: 3px;
  width: 100%;
  background: var(--primary);
  margin: 2px 0;
  border-radius: 2px;
  transition: background 0.3s;
}

:focus-visible {
  outline: 4px solid var(--primary);
  outline-offset: 0px;
}

/* ===== Navigation (Mobile First) ===== */
.nav {
  display: none;
  /* Hidden on mobile by default */
  align-items: center;
}

/* Mobile Menu Overlay */
.overlay {
  display: none;
  position: fixed;
  inset: 0;
  background: var(--overlay);
  z-index: 1100;
  transition: background 0.3s;
}

.overlay.active {
  display: block;
}

.mobile-menu {
  display: flex;
  flex-direction: column;
  position: fixed;
  top: 0;
  right: 0;
  width: 80vw;
  max-width: 340px;
  height: 100vh;
  background: var(--mobile-menu-bg);
  z-index: 1101;
  padding: 32px 0 0 0;
  box-shadow: -2px 0 16px rgba(0, 0, 0, 0.12);
  transform: translateX(100%);
  transition: transform 0.3s;
  overflow-y: auto;
}

.mobile-menu.active {
  transform: translateX(0);
}

.mobile-menu ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.mobile-menu .nav-item {
  border-bottom: 1px solid var(--divider);
  position: relative;
}

.mobile-menu .nav-link {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 18px 28px;
  color: var(--text);
  text-decoration: none;
  font-size: 1.1em;
  font-weight: 500;
  background: none;
  border: none;
  width: 100%;
