# Create blog.html, blog-post.html, blog JS, and a tiny PHP proxy (optional) for WordPress REST API.
import os, textwrap, json, pathlib

os.makedirs("/mnt/data/assets/js", exist_ok=True)
os.makedirs("/mnt/data/assets/css", exist_ok=True)
os.makedirs("/mnt/data/api", exist_ok=True)

blog_html = """<!DOCTYPE html>
<html lang="en" data-theme="auto" class="no-js">
  <head>
    <meta charset="UTF-8" />
    <title>Blog — SG Maths Tuition</title>
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <meta name="description" content="Latest posts from SG Maths Tuition: study tips, explanations, and updates." />

    <!-- Theme preloader (copied pattern from index.html) -->
    <script>
      (function () {
        try {
          var saved = localStorage.getItem("theme"); // 'light' | 'dark' | null
          var prefersDark =
            window.matchMedia &&
            window.matchMedia("(prefers-color-scheme: dark)").matches;
          var theme = saved || (prefersDark ? "dark" : "light");
          if (theme === "dark")
            document.documentElement.setAttribute("data-theme", "dark");
          else document.documentElement.setAttribute("data-theme", "light");
        } catch (e) {}
      })();
    </script>

    <link rel="stylesheet" href="assets/css/tuition-css-1.0.0.css?v=1.0.2.B" />
    <link rel="stylesheet" href="assets/css/blog.css?v=1.0.0" />

    <!-- Canonical & robots -->
    <link rel="canonical" href="https://www.sgmathtuition.com/blog.html" />
    <meta name="robots" content="index, follow" />

    <!-- Open Graph -->
    <meta property="og:type" content="website" />
    <meta property="og:title" content="Blog — SG Maths Tuition" />
    <meta property="og:description" content="Latest posts from SG Maths Tuition: study tips, explanations, and updates." />
    <meta property="og:image" content="assets/media/hero-1536.jpg" />
  </head>

  <body>
    <a class="skip-link" href="#main-content">Skip to content</a>

    <!-- Header (same structure as index.html; menu is populated from menu.json by tuition-js) -->
    <header class="header">
      <a class="brand" href="index.html" aria-label="SG Maths Tuition - Home">
        <img class="logo logo-light" src="assets/media/logo-cap-black.png" alt="SG Maths Tuition" height="36" width="37" />
        <img class="logo logo-dark" src="assets/media/logo-cap-white.png" alt="" aria-hidden="true" height="36" width="37" />
        <span class="brand-title">SG Maths Tuition</span>
      </a>

      <div class="header-right-controls">
        <nav class="nav" aria-label="Main navigation">
          <ul></ul>
        </nav>

        <button class="hamburger" aria-label="Open menu" aria-controls="mobile-menu" aria-expanded="false">
          <span aria-hidden="true"></span>
          <span aria-hidden="true"></span>
          <span aria-hidden="true"></span>
        </button>

        <button class="theme-toggle" aria-label="Toggle light/dark mode" title="Toggle light/dark mode" aria-pressed="false">
          &#9790;
        </button>
      </div>
    </header>

    <div class="overlay" aria-hidden="true"></div>
    <nav class="mobile-menu" id="mobile-menu" aria-label="Mobile navigation" aria-hidden="true"></nav>

    <!-- Hero (reuse look/feel) -->
    <section class="hero">
      <picture class="hero-media">
        <source srcset="assets/media/hero-768.jpg" media="(max-width: 767.98px)" />
        <img
          src="assets/media/hero-1536.jpg"
          srcset="assets/media/hero-1536.jpg 1536w, assets/media/hero-768.jpg 768w"
          sizes="100vw"
          alt=""
          aria-hidden="true"
          width="1536"
          height="640"
          decoding="async"
          fetchpriority="high"
        />
      </picture>

      <div class="hero-content">
        <h1 class="hero-title">Blog</h1>
        <ul class="hero-bullets">
          <li>Latest posts and study tips</li>
          <li>Short, readable explanations</li>
          <li>Updates on coursework and exams</li>
        </ul>
        <div class="hero-actions">
          <a class="hero-cta hero-cta--whatsapp" href="https://wa.me/6592997306?text=Hello.%20" target="_blank" rel="noopener noreferrer">
            WhatsApp (65) 92997306
          </a>
          <a class="hero-cta hero-cta--contact" href="#blog">Jump to posts</a>
        </div>
      </div>
    </section>

    <!-- Main -->
    <main id="main-content">
      <section id="blog" class="section section--blog">
        <div class="blog-wrap">
          <!-- Controls -->
          <form id="blog-controls" class="blog-controls" role="search" aria-label="Search and filter blog posts">
            <div class="control-group">
              <label for="q" class="visually-hidden">Search</label>
              <input id="q" name="q" type="search" placeholder="Search posts…" />
            </div>

            <div class="control-group">
              <label for="category" class="visually-hidden">Category</label>
              <select id="category" name="category">
                <option value="">All categories</option>
              </select>
            </div>

            <div class="control-group">
              <label for="month" class="visually-hidden">Month</label>
              <select id="month" name="month">
                <option value="">Any month</option>
              </select>
            </div>

            <div class="control-group">
              <label for="perPage" class="visually-hidden">Per page</label>
              <select id="perPage" name="perPage">
                <option value="6">6</option>
                <option value="9" selected>9</option>
                <option value="12">12</option>
              </select>
            </div>

            <button class="btn-pill" type="submit">Apply</button>
          </form>

          <!-- Results -->
          <div id="blog-list" class="blog-grid" role="list" aria-live="polite"></div>

          <!-- Pagination -->
          <nav id="pager" class="pager" aria-label="Pagination"></nav>

          <noscript>
            <p>
              JavaScript is disabled. You can browse the blog directly on WordPress:
              <a href="https://sgmathtuition.com/blog/">sgmathtuition.com/blog/</a>
            </p>
          </noscript>
        </div>
      </section>
    </main>

    <!-- Footer -->
    <footer class="footer">
      &copy; <span id="year"></span> Integrated Math and Science Tuition |
      Singapore | integrated.math.tuition@gmail.com | +65 9299 7306 | v1.1.0
    </footer>

    <!-- Site scripts -->
    <script>
      window.__WP_CONFIG__ = {
        // CHANGE THIS if your WordPress lives at a different origin or subdomain.
        base: "https://sgmathtuition.com",
        // If you hit CORS issues, upload /api/wp-proxy.php then switch this to true.
        useProxy: false,
        proxyPath: "/api/wp-proxy.php?endpoint=",
      };
    </script>
    <script src="assets/js/tuition-js-1.0.3.js?v=1.0.9" defer></script>
    <script src="assets/js/blog-1.0.0.js?v=1.0.0" defer></script>
  </body>
</html>
"""

