blog_css = """
/* Minimal blog styles that reuse your design tokens */
.visually-hidden { position: absolute; left: -9999px; top: auto; width: 1px; height: 1px; overflow: hidden; }
.section--blog .blog-wrap { max-width: min(1160px, 92vw); margin: 0 auto; text-align: left; }

.blog-controls {
  display: grid;
  grid-template-columns: 1fr;
  gap: 10px;
  margin: 0 0 16px;
}
.blog-controls .control-group { display: flex; gap: 8px; }
.blog-controls input[type="search"],
.blog-controls select {
  flex: 1;
  font: inherit;
  padding: 10px 12px;
  border: 1px solid var(--divider);
  border-radius: 8px;
  background: var(--header-bg);
  color: var(--text);
}

.blog-grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: clamp(16px, 2.6vw, 24px);
}
@media (min-width: 900px) {
  .blog-grid { grid-template-columns: repeat(2, 1fr); }
}

.post-card {
  background: var(--header-bg);
  color: var(--text);
  border: 1px solid var(--divider);
  border-radius: 16px;
  padding: clamp(16px, 2.2vw, 22px);
  box-shadow: 0 10px 24px rgba(0,0,0,0.10);
  display: flex;
  flex-direction: column;
  gap: 8px;
}
.post-card h3 { margin: 0; line-height: 1.2; font-weight: 800; font-size: clamp(1.2rem, 1rem + 0.8vw, 1.6rem); }
.post-card time { opacity: 0.8; font-size: 0.95rem; }
.post-card .excerpt { line-height: 1.7; }
.post-card .actions { margin-top: 6px; }
.post-card .actions a { text-decoration: none; }

.pager,
.post-pager {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  gap: 8px;
  margin-top: 16px;
}
.pager a,
.pager button,
.post-pager a {
  display: inline-block;
  padding: 8px 12px;
  border-radius: 999px;
  border: 1px solid var(--divider);
  background: var(--header-bg);
  color: var(--text);
  text-decoration: none;
}
.pager .current {
  font-weight: 800;
  border-color: var(--primary);
}

.post { max-width: 70ch; margin: 0 auto; }
.post-body :is(img, video, iframe) { max-width: 100%; height: auto; }
.post-body pre { overflow: auto; padding: 12px; background: var(--bg-alt); border-radius: 8px; }

/* Reuse existing button pill if present */
.btn-pill {
  background: var(--cta-bg);
  color: var(--cta-text);
  border-radius: 999px;
  padding: 10px 18px;
  border: none;
  cursor: pointer;
}
.btn-pill:hover,
.btn-pill:focus-visible {
  background: var(--cta-hover-bg);
  color: var(--cta-hover-text);
}
"""

blog_js = r"""
(() => {
  "use strict";

  const cfg = (window.__WP_CONFIG__ || {});
  const BASE = (cfg.base || "").replace(/\/+$/, "");
  const USE_PROXY = !!cfg.useProxy;
  const PROXY = cfg.proxyPath || "/api/wp-proxy.php?endpoint=";

  const state = {
    page: 1,
    perPage: 9,
    totalPages: 1,
    q: "",
    category: "",
    month: "", // YYYY-MM
  };

  function apiUrl(path) {
    if (!path.startsWith("/")) path = "/" + path;
    const url = BASE + path;
    return USE_PROXY ? (PROXY + encodeURIComponent(path)) : url;
  }

  async function fetchJSON(path) {
    const res = await fetch(apiUrl(path), { mode: USE_PROXY ? "cors" : "cors" });
    if (!res.ok) throw new Error(`HTTP ${res.status}`);
    const data = await res.json();
    // Try to capture pagination headers if available (WP exposes these)
    const totalPages = Number(res.headers.get("X-WP-TotalPages") || state.totalPages || 1);
    return { data, headers: res.headers, totalPages };
  }

  function qs(sel, root = document) { return root.querySelector(sel); }
  function qsa(sel, root = document) { return Array.from(root.querySelectorAll(sel)); }

  function fmtDate(s) {
    const d = new Date(s);
    return d.toLocaleDateString(undefined, { year: "numeric", month: "short", day: "numeric" });
  }

  function stripDangerous(html) {
    const tpl = document.createElement("template");
    tpl.innerHTML = html || "";
    // Remove scripts/iframes inline
    tpl.content.querySelectorAll("script, iframe, object, embed, link").forEach((el) => el.remove());
    // Remove on* handlers
    tpl.content.querySelectorAll("*").forEach((el) => {
      [...el.attributes].forEach((a) => {
        if (a.name.toLowerCase().startsWith("on")) el.removeAttribute(a.name);
      });
    });
    return tpl.innerHTML;
  }

  function paramsFromURL() {
    const u = new URL(location.href);
    state.page = Math.max(1, Number(u.searchParams.get("page") || 1));
    state.perPage = Number(u.searchParams.get("perPage") || state.perPage);
    state.q = (u.searchParams.get("q") || "").trim();
    state.category = (u.searchParams.get("category") || "").trim();
    state.month = (u.searchParams.get("month") || "").trim();
  }

  function syncControls() {
    qs("#q").value = state.q;
    qs("#category").value = state.category;
    qs("#month").value = state.month;
    qs("#perPage").value = String(state.perPage);
  }

  function pushURL() {
    const u = new URL(location.href);
    u.searchParams.set("page", String(state.page));
    u.searchParams.set("perPage", String(state.perPage));
    if (state.q) u.searchParams.set("q", state.q); else u.searchParams.delete("q");
    if (state.category) u.searchParams.set("category", state.category); else u.searchParams.delete("category");
    if (state.month) u.searchParams.set("month", state.month); else u.searchParams.delete("month");
    history.replaceState(null, "", u);
  }

  async function loadCategories() {
    try {
      const { data } = await fetchJSON("/wp-json/wp/v2/categories?per_page=100&orderby=count&order=desc");
      const sel = qs("#category");
      data.forEach((c) => {
        const opt = document.createElement("option");
        opt.value = String(c.id);
        opt.textContent = c.name;
        sel.appendChild(opt);
      });
    } catch (e) {
      // Ignore; keep "All categories"
      console.warn("Categories failed", e);
    }
  }

  function buildMonthList() {
    // Build a dropdown of months from Jan 2024 to now (safe default).
    const start = new Date();
    start.setFullYear(start.getFullYear() - 3); // last 3 years by default
    start.setMonth(0, 1);
    const end = new Date();
    const sel = qs("#month");
    const fmt = (y, m) => `${y}-${String(m+1).padStart(2, "0")}`;
    for (let y = end.getFullYear(); y >= start.getFullYear(); y--) {
      const mStart = (y === end.getFullYear()) ? end.getMonth() : 11;
      const mEnd = (y === start.getFullYear()) ? 0 : 0;
      for (let m = mStart; m >= mEnd; m--) {
        const v = fmt(y, m);
        const date = new Date(y, m, 1);
        const label = date.toLocaleDateString(undefined, { year: "numeric", month: "long" });
        const opt = document.createElement("option");
        opt.value = v;
        opt.textContent = label;
        sel.appendChild(opt);
      }
    }
  }

  function buildQuery() {
    const p = new URLSearchParams();
    p.set("per_page", String(state.perPage));
    p.set("page", String(state.page));
    p.set("_embed", "1");
    p.set("_fields", "id,slug,date,link,title,excerpt");
    if (state.q) p.set("search", state.q);
    if (state.category) p.set("categories", state.category);
    if (state.month) {
      const [y, m] = state.month.split("-").map(Number);
      const after = new Date(y, m - 1, 1).toISOString();
      const before = new Date(y, m, 0, 23, 59, 59, 999).toISOString(); // end of month
      p.set("after", after);
      p.set("before", before);
    }
    return p.toString();
  }

  function renderPosts(items) {
    const root = qs("#blog-list");
    root.innerHTML = "";
    if (!Array.isArray(items) || items.length === 0) {
      root.innerHTML = `<p>No posts found.</p>`;
      qs("#pager").innerHTML = "";
      return;
    }

    const frag = document.createDocumentFragment();
    items.forEach((p) => {
      const art = document.createElement("article");
      art.className = "post-card";
      art.setAttribute("role", "listitem");
      const title = p?.title?.rendered || "Untitled";
      const excerpt = stripDangerous(p?.excerpt?.rendered || "");
      art.innerHTML = `
        <h3><a href="blog-post.html?slug=${encodeURIComponent(p.slug)}">${title}</a></h3>
        <time datetime="${p.date}">${fmtDate(p.date)}</time>
        <div class="excerpt">${excerpt}</div>
        <div class="actions">
          <a class="hero-cta hero-cta--contact" href="blog-post.html?slug=${encodeURIComponent(p.slug)}">Read more</a>
        </div>
      `;
      frag.appendChild(art);
    });
    root.appendChild(frag);
  }

  function renderPager() {
    const nav = qs("#pager");
    nav.innerHTML = "";
    if (state.totalPages <= 1) return;
    // Prev
    if (state.page > 1) {
      const a = document.createElement("a");
      a.href = "#";
      a.textContent = "‹ Prev";
      a.addEventListener("click", (e) => { e.preventDefault(); state.page--; pushURL(); load(); });
      nav.appendChild(a);
    }
    // Pages (compact)
    const maxShown = 6;
    const pages = [];
    const start = Math.max(1, state.page - Math.floor(maxShown/2));
    const end = Math.min(state.totalPages, start + maxShown - 1);
    for (let i = start; i <= end; i++) pages.push(i);
    pages.forEach((i) => {
      const el = document.createElement(i === state.page ? "span" : "a");
      if (i === state.page) el.className = "current";
      el.textContent = String(i);
      if (i !== state.page) {
        el.href = "#";
        el.addEventListener("click", (e) => { e.preventDefault(); state.page = i; pushURL(); load(); });
      }
      nav.appendChild(el);
    });
    // Next
    if (state.page < state.totalPages) {
      const a = document.createElement("a");
      a.href = "#";
      a.textContent = "Next ›";
      a.addEventListener("click", (e) => { e.preventDefault(); state.page++; pushURL(); load(); });
      nav.appendChild(a);
    }
  }

  async function load() {
    const query = buildQuery();
    try {
      const { data, totalPages } = await fetchJSON(`/wp-json/wp/v2/posts?${query}`);
      state.totalPages = Number(totalPages || 1);
      renderPosts(data);
      renderPager();
    } catch (e) {
      console.error(e);
      qs("#blog-list").innerHTML = `<p>Could not load posts. <a href="https://sgmathtuition.com/blog/">Open the WordPress blog</a>.</p>`;
      qs("#pager").innerHTML = "";
    }
  }

  function onSubmitControls(e) {
    e.preventDefault();
    state.q = qs("#q").value.trim();
    state.category = qs("#category").value.trim();
    state.month = qs("#month").value.trim();
    state.perPage = Number(qs("#perPage").value || 9);
    state.page = 1;
    pushURL();
    load();
  }

  // Boot
  document.addEventListener("DOMContentLoaded", async () => {
    paramsFromURL();
    syncControls();
    buildMonthList();
    await loadCategories();
    pushURL();
    load();
    qs("#blog-controls").addEventListener("submit", onSubmitControls);
  });
})();
"""

