  }

  function stripDangerous(html) {
    const tpl = document.createElement("template");
    tpl.innerHTML = html || "";
    tpl.content.querySelectorAll("script, iframe, object, embed, link").forEach((el) => el.remove());
    tpl.content.querySelectorAll("*").forEach((el) => {
