  font-weight: 800;
  border: 0;
  cursor: pointer;
  font-size: clamp(1.02rem, 0.9rem + 0.5vw, 1.2rem);
  box-shadow: 0 0 0 1px color-mix(in srgb, var(--text) 20%, transparent),
    0 2px 8px rgba(0, 0, 0, 0.08);
  transition: background 0.2s ease, color 0.2s ease, transform 0.06s ease-in-out;
}

.btn-pill:hover,
.btn-pill:focus-visible {
  background: var(--cta-hover-bg);
  color: var(--cta-hover-text);
}

.btn-pill:active {
  transform: translateY(1px);
}

.form-actions {
  margin-top: 12px;
}

.form-alert {
  display: none;
  margin-top: 12px;
  padding: 12px 14px;
  border-radius: 12px;
  font-size: 1rem;
}

.form-alert--success {
  background: #e8f8ee;
  color: #065f46;
  border: 1px solid #10b981;
}

.form-alert--error {
  background: #fde8e8;
  color: #7f1d1d;
  border: 1px solid #ef4444;
}

:root[data-theme="dark"] .form-alert--success {
  background: #0b2b1f;
  color: #b7f3dc;
}

:root[data-theme="dark"] .form-alert--error {
  background: #3a0f0f;
  color: #fecaca;
}

.counter {
  font-size: 1rem;
  opacity: 0.75;
  text-align: right;
}

/* accessible “screen reader only” helper */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  padding: 0;
  border: 0;
  white-space: nowrap;
  clip-path: inset(50%);
  clip: rect(0 0 0 0);
  overflow: hidden;
}

/* =========================
     PATCH: Contact card links
     ========================= */
/* Use dark‑mode link/hover colours on the left card even when the page is in light mode. 
     We out‑specific the global '#main-content .section a' rules. */
:root[data-theme="light"]
  #main-content
  .section.section--contact
  .contact-card
  a {
  color: #7ec1ff;
  /* same as dark mode link colour */
  text-decoration-color: #7ec1ff;
  /* keep underline consistent */
}

:root[data-theme="light"]
  #main-content
  .section.section--contact
  .contact-card
  a:hover,
:root[data-theme="light"]
  #main-content
  .section.section--contact
  .contact-card
  a:focus-visible {
  color: #ff7578;
  /* same as dark mode hover colour */
  text-decoration-color: #ff7578;
}

/* Keep the white focus outline on the dark card for visibility */
.section--contact .contact-card a:focus-visible {
  outline: 3px solid #fff;
  outline-offset: 3px;
  border-radius: 6px;
}

/* ==============================================
     PATCH: "Send Message" pill – thicker theme ring
     ============================================== */
/* Light mode: thicker black perimeter */
:root[data-theme="light"] .section--contact .btn-pill {
  /* was a 1px subtle keyline; make it clearly 2px */
  box-shadow: 0 0 0 2px #000000, 0 2px 8px rgba(0, 0, 0, 0.08);
}

/* Dark mode: analogous 2px white perimeter */
:root[data-theme="dark"] .section--contact .btn-pill {
  box-shadow: 0 0 0 2px #ffffff, 0 2px 8px rgba(0, 0, 0, 0.18);
}

/* ================================
   CONTACT: XS overflow hardening
   ================================= */

/* Let grid children shrink with the viewport (safe globally) */
.contact-grid > * {
  min-width: 0;
  /* overrides grid default min-width:auto */
}

/* Inputs should never exceed their container */
.section--contact input[type="text"],
.section--contact input[type="email"],
.section--contact input[type="tel"],
.section--contact textarea {
  width: 100%;
  max-width: 100%;
}

/* When space is tight, allow the "Other" text box to wrap under its label */
@media (max-width: 359.98px) {
  .option--other {
    flex-wrap: wrap;
    /* allow children to break to a new line */
    align-items: flex-start;
    gap: 8px 10px;
    /* slightly denser on XS */
  }

  .option--other input[type="text"] {
    /* override global min(320px,60vw) so it cannot force the card wider */
    min-width: 0;
    flex: 1 1 100%;
    width: 100%;
  }

  /* Slightly tighter card padding to reclaim a few px on ultra-narrow phones */
  .contact-card,
  .contact-form {
    padding: 12px;
  }
}

/* Optional: give the grid a touch more usable width on very small phones */
@media (max-width: 399.98px) {
  .contact-grid {
    max-width: 96vw;
    /* was 92vw; reduces side margins to cut risk of scrollbars */
  }
}

/* ===== Footer ===== */
.footer {
  background: var(--footer-bg);
  color: var(--footer-text);
  text-align: center;
  padding: 24px 0 16px 0;
  font-size: 1rem;
  letter-spacing: 0.5px;
  transition: background 0.3s, color 0.3s;
}

/* ====== Desktop Styles ====== */
@media (min-width: 1200px) {
  .header {
    height: 72px;
    padding: 0 40px;
  }

  .logo img {
    max-height: 40px;
  }

  .theme-toggle {
    font-size: 1.7em;
  }

  .hamburger {
    display: none;
  }

  /* Hide hamburger on desktop */
  .nav {
    display: flex;
    align-items: center;
    gap: 0;
  }

  .nav ul {
    display: flex;
    align-items: center;
    gap: 0;
    margin: 0;
    padding: 0;
    list-style: none;
  }

  .nav-item {
    position: relative;
  }

  .nav-link {
    text-decoration: none;
    color: var(--text);
    font-weight: 500;
    padding: 12px 16px;
    border-radius: 4px;
    transition: background 0.2s;
    display: flex;
    align-items: center;
    cursor: pointer;
    background: none;
    border: none;
    font-size: 1em;
    line-height: 1.2;
  }

  .nav-link:hover {
    background: var(--nav-hover);
  }

  .arrow {
    margin-left: 6px;
    font-size: 0.8em;
    transition: transform 0.2s;
  }

  .dropdown {
    display: none;
    position: absolute;
    top: 100%;
    right: 0;
    background: var(--header-bg);
    min-width: 180px;
    box-shadow: var(--dropdown-shadow);
    border-radius: 6px;
    z-index: 1001;
    padding: 8px 0;
    flex-direction: column;
  }

  .dropdown a {
    display: block;
    padding: 10px 20px;
    color: var(--text);
    text-decoration: none;
    font-size: 1em;
    transition: background 0.2s;
  }

  .dropdown a:hover {
    background: var(--nav-hover);
  }

  /* Make dropdown keyboard-openable, not just hover */
  .nav-item:hover .dropdown,
  .nav-item:focus-within .dropdown {
    display: flex;
  }

  .nav-item.force-close .dropdown {
    display: none !important;
  }

  .nav-item:focus-within .arrow,
  .nav-item:hover .arrow {
    transform: rotate(180deg);
  }

  .mobile-menu,
  .overlay {
    display: none !important;
  }

  /* Hide mobile UI */
  .hero {
    height: 420px;
  }

  .section {
    padding: 64px 0;
  }

  .section-title {
    font-size: 2rem;
  }

  .section-content {
    max-width: 700px;
    font-size: 1.1rem;
  }
}

/* ===== WhatsApp floating button ===== */
.whatsapp-button {
  position: fixed;
  bottom: 20px;
  right: 20px;
  background-color: transparent;
  border-radius: 50%;
  cursor: pointer;
  transition: transform 0.2s ease-in-out;
  z-index: 1002;
  width: 60px;
  height: 60px;
}

.whatsapp-button img {
  width: 100%;
  height: 100%;
  object-fit: contain;
}

.whatsapp-button:hover,
.whatsapp-button:active {
  transform: scale(1.1);
  animation: pulse 1s infinite;
}

@media (min-width: 768px) {
  .whatsapp-button {
    width: 80px;
    height: 80px;
  }

  .whatsapp-button:hover,
  .whatsapp-button:active {
    transform: scale(1.15);
  }
}

@keyframes pulse {
  0% {
    transform: scale(1);
  }

  50% {
    transform: scale(1.05);
  }

  100% {
    transform: scale(1);
  }
}

/* Respect users who prefer reduced motion */
@media (prefers-reduced-motion: reduce) {
  * {
    animation: none !important;
    transition: none !important;
  }

  .whatsapp-button:hover,
  .whatsapp-button:active {
    transform: none;
    animation: none;
  }
}

