        localStorage.setItem("theme", dark ? "dark" : "light");
      } catch (_e) {}
      usingAuto = false;
    }
  }

  function initTheme() {
    let saved = null;
    try {
      saved = localStorage.getItem("theme");
    } catch (_e) {
      saved = null;
    }

    if (saved === "dark") {
      setTheme(true, { persist: false });
    } else if (saved === "light") {
      setTheme(false, { persist: false });
    } else {
      usingAuto = true;
      setTheme(prefersDark(), { persist: false });

      const mql = window.matchMedia
        ? window.matchMedia("(prefers-color-scheme: dark)")
        : null;
      if (mql) {
        const onChange = (e) => {
          if (usingAuto) setTheme(e.matches, { persist: false });
        };
        try {
          mql.addEventListener("change", onChange);
        } catch {
          mql.addListener(onChange);
        } // Safari fallback
      }
    }

    if (themeToggle) {
      themeToggle.addEventListener("click", () => {
        const isDark = docEl.getAttribute("data-theme") === "dark";
        setTheme(!isDark, { persist: true });
      });
    }
  }

  /* =========================
   * ARIA & boot
   * ========================= */
  function initAriaStates() {
    // Hide drawer & overlay from SR until opened
    if (mobileMenu && !mobileMenu.hasAttribute("aria-hidden")) {
      mobileMenu.setAttribute("aria-hidden", "true");
    }
    if (overlay && !overlay.hasAttribute("aria-hidden")) {
      overlay.setAttribute("aria-hidden", "true");
    }
    if (hamburger) {
      hamburger.setAttribute("aria-expanded", "false");
      hamburger.setAttribute("aria-label", "Open menu");
      hamburger.setAttribute("aria-controls", "mobile-menu");
    }

    // Defensive: if authors use .no-js for CSS fallbacks
    docEl.classList.remove("no-js");
  }

  if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", onReady);
  } else {
    onReady();
  }

  /* =========================
   * Nav from JSON (desktop -> mobile)
   * ========================= */

  async function initMenuFromFile() {
    // If a server-rendered nav is present (future SSG/SSR), skip rebuilding.
    if (desktopNav?.dataset.menu === "ssr" && desktopList?.children.length)
      return;
    if (!desktopList) return;

    let items = null;
    try {
      // same-origin fetch; keep "no-cache" to revalidate
      items = await loadJSON("assets/data/menu.json", { cache: "no-cache" });
    } catch (e) {
      console.warn("Menu JSON failed to load; using HTML fallback", e);
      return; // fallback: leave existing HTML as-is
    }

    if (!Array.isArray(items) || items.length === 0) {
      console.warn("Menu JSON empty; using HTML fallback");
      return;
    }

    renderDesktopMenu(items);
  }

  function renderDesktopMenu(items) {
    // Build exactly the DOM your CSS/JS expect:
    // .nav > ul > li.nav-item > (button.nav-link + .dropdown) | a.nav-link
    desktopList.innerHTML = "";

    const usedIds = new Set();
    const uniqueId = (base) => {
      let id = base;
      let n = 2;
      while (usedIds.has(id)) id = `${base}-${n++}`;
      usedIds.add(id);
      return id;
    };

    items.forEach((node, i) => {
      const hasChildren =
        Array.isArray(node?.children) && node.children.length > 0;

      // We'll only append the <li> if it ends up with content.
      const li = document.createElement("li");
      li.className = "nav-item";

      // inside renderDesktopMenu(items)
      if (hasChildren) {
        const baseId = `dd-${slugify(node.label || `section-${i + 1}`)}`;
        const ddId = uniqueId(baseId);

        // NEW: prefer <a> if a safe href is provided
        const safeHref = sanitizeHref(node?.href);
        let trigger; // either <a.nav-link> or <button.nav-link>

        if (safeHref) {
          const a = document.createElement("a");
          a.className = "nav-link";
          a.href = safeHref;
          a.setAttribute("aria-haspopup", "true");
          a.setAttribute("aria-expanded", "false");
          a.setAttribute("aria-controls", ddId);
          a.append(document.createTextNode(String(node.label || "Menu")));
          const arrow = document.createElement("span");
          arrow.className = "arrow";
          arrow.innerHTML = "&#9662;";
          a.appendChild(arrow);
          trigger = a;
        } else {
          const btn = document.createElement("button");
          btn.className = "nav-link";
          btn.type = "button";
          btn.setAttribute("aria-haspopup", "true");
          btn.setAttribute("aria-expanded", "false");
          btn.setAttribute("aria-controls", ddId);
          btn.append(document.createTextNode(String(node.label || "Menu")));
          const arrow = document.createElement("span");
          arrow.className = "arrow";
          arrow.innerHTML = "&#9662;";
          btn.appendChild(arrow);
          trigger = btn;
        }

        const dd = document.createElement("div");
        dd.className = "dropdown";
        dd.id = ddId;
        dd.setAttribute("role", "group");
        dd.setAttribute("aria-label", String(node.label || "Submenu"));

        node.children.forEach((child) => {
          const safe = sanitizeHref(child?.href);
          if (!safe) return;
          const a = document.createElement("a");
          a.href = safe;
          a.textContent = String(child.label || child.href);
          dd.appendChild(a);
        });

        if (dd.children.length > 0) {
          li.append(trigger, dd);
          desktopList.appendChild(li);
        }
      } else {
        const safe = sanitizeHref(node?.href);
        if (!safe) return; // skip unsafe/malformed leaf

        const a = document.createElement("a");
        a.className = "nav-link";
        a.href = safe;
        a.textContent = String(node.label || node.href);
        li.appendChild(a);
        desktopList.appendChild(li);
      }
    });
  }

  async function onReady() {
    initAriaStates();

    // 1) Replace desktop menu from JSON (no-op if JSON fails or SSR present)
    await initMenuFromFile();

    // 2) Now build the mobile drawer from the (possibly rebuilt) desktop nav
    buildMobileMenuFromDesktop();

    // 3) Behaviors
    initMobileMenuHandlers();
    initDesktopDropdowns();
    initTheme();
    updateFooterYear();
    initTestimonialsFromFile();

    // Optional: surface CSP violations in the console during Report-Only
    window.addEventListener("securitypolicyviolation", (e) => {
      // Helpful while you tune CSP in Report-Only mode
      console.warn(
        "CSP violation:",
        e.violatedDirective,
        "blocked:",
        e.blockedURI
      );
    });
  }

  /* ===== Utilities ===== */
  async function loadJSON(url, { cache = "no-cache" } = {}) {
    const res = await fetch(url, { cache });
    if (!res.ok) throw new Error(`Failed to load ${url}: ${res.status}`);
    return res.json();
  }

  /* ===== Testimonials from external JSON ===== */
  async function initTestimonialsFromFile() {
    const root = document.getElementById("testimonials");
    if (!root) return;

    const url =
      root.getAttribute("data-src") || "assets/data/testimonials.json";
    let items = [];
    try {
      items = await loadJSON(url);
    } catch (e) {
      console.warn("Testimonials JSON failed to load:", e);
    }

    if (!Array.isArray(items) || items.length === 0) {
      root.style.display = "none";
      return;
    }
    renderTestimonials(root, items);
  }

  function renderTestimonials(root, items) {
    const track = root.querySelector("#tTrack");
    const dots = root.querySelector("#tDots");
    const btnPrev = root.querySelector(".t-btn--prev");
    const btnNext = root.querySelector(".t-btn--next");

    function scrollTestimonialsIntoView() {
      const target = root.querySelector(".t-viewport") || root;
      const headerEl = document.querySelector(".header");
      const headerH = headerEl ? headerEl.getBoundingClientRect().height : 0;
      const y =
        target.getBoundingClientRect().top + window.scrollY - headerH - 8;
      const reduce =
        window.matchMedia &&
        window.matchMedia("(prefers-reduced-motion: reduce)").matches;
      window.scrollTo({
        top: Math.max(0, y),
        behavior: reduce ? "auto" : "smooth",
      });
    }

    if (!track || !dots) return;

    // Reset DOM
    track.innerHTML = "";
    dots.innerHTML = "";

    // Build slides + dots
    const slideEls = items.map((t, i) => {
      const li = document.createElement("li");
      li.className = "t-slide";
      li.id = `t-slide-${i}`;
      li.setAttribute("role", "group");
      li.setAttribute("aria-roledescription", "slide");
      li.setAttribute("aria-label", `${i + 1} of ${items.length}`);

      // Card HTML without avatar; avatar inserted safely after.
      const nameSafe = escapeHTML(t.name || "Student");
      const taglineHtml = t.tagline
        ? `<div class="t-tagline">${escapeHTML(t.tagline)}</div>`
        : "";
      const quoteHtml = escapeHTMLPreserveNewlines(t.text || "");

      li.innerHTML = `
        <article class="t-card">
          <div class="t-headerline">
            <div class="t-avatar" aria-hidden="true" data-slot="avatar"></div>
            <h3 class="t-name">${nameSafe}</h3>
            ${taglineHtml}
          </div>
          <p class="t-quote">${quoteHtml}</p>
        </article>`;

      // Safe avatar injection
      const slot = li.querySelector('[data-slot="avatar"]');
      if (slot) {
        const initials = (t.name || "?").trim().slice(0, 2).toUpperCase();
        const safeSrc = t.avatar ? sanitizeImgSrc(t.avatar) : null;
        if (safeSrc) {
          const img = document.createElement("img");
          img.src = safeSrc;
          img.alt = "";
          img.loading = "lazy";
          img.decoding = "async";
          slot.appendChild(img);
        } else {
          slot.textContent = initials;
        }
      }

      track.appendChild(li);

      const dot = document.createElement("button");
      dot.className = "t-dot";
      dot.setAttribute("role", "tab");
      dot.setAttribute("aria-controls", li.id);
      dot.setAttribute("title", `Show testimonial ${i + 1}`);
      dot.addEventListener("click", () => {
        goTo(i);
        requestAnimationFrame(scrollTestimonialsIntoView);
      });

      dots.appendChild(dot);

      return li;
    });

    let index = 0;
    const count = slideEls.length;

    const reduceMotion =
      window.matchMedia &&
      window.matchMedia("(prefers-reduced-motion: reduce)").matches;
    const TRANS = reduceMotion
      ? "none"
      : "transform .35s ease, height .18s ease-out";

    function currentHeight() {
      const el = slideEls[index];
      return el ? el.offsetHeight : 0; // includes slide padding
    }

    function applyTransformAndHeight() {
      track.style.transform = `translateX(${-index * 100}%)`;
      const h = currentHeight();
      track.style.height = h ? `${h}px` : "";
    }

    function updateUI() {
      [...dots.children].forEach((d, i) => {
        d.setAttribute("aria-selected", String(i === index));
        d.tabIndex = i === index ? 0 : -1;
      });
      slideEls.forEach((el, i) => {
        el.setAttribute("aria-hidden", String(i !== index));
      });
      if (btnPrev) btnPrev.disabled = count < 2;
      if (btnNext) btnNext.disabled = count < 2;
    }

    function goTo(next) {
      const prev = index;
      const target = (next + count) % count;
      const isWrapForward = prev === count - 1 && target === 0; // last -> first
      const isWrapBackward = prev === 0 && target === count - 1; // first -> last

      if (isWrapForward || isWrapBackward) {
        // Avoid sweeping through all slides visually
        track.style.transition = "none";
        index = target;
        applyTransformAndHeight();
        // Force reflow, then restore transition
        void track.offsetWidth;
        if (!reduceMotion) track.style.transition = TRANS;
      } else {
        if (!reduceMotion) track.style.transition = TRANS;
        index = target;
        applyTransformAndHeight();
      }
      updateUI();
    }

    // Controls
    btnPrev?.addEventListener("click", () => goTo(index - 1));
    btnNext?.addEventListener("click", () => goTo(index + 1));

    // Keyboard arrows when focus is within the section
    root.addEventListener("keydown", (e) => {
      if (e.key === "ArrowLeft") {
        e.preventDefault();
        goTo(index - 1);
      }
      if (e.key === "ArrowRight") {
        e.preventDefault();
        goTo(index + 1);
      }
    });

    // Simple swipe
    let startX = null;
    track.addEventListener("pointerdown", (e) => {
      startX = e.clientX;
      track.setPointerCapture?.(e.pointerId);
    });
    track.addEventListener("pointerup", (e) => {
      if (startX === null) return;
      const dx = e.clientX - startX;
      startX = null;
      const threshold = Math.max(40, track.clientWidth * 0.07);
      if (dx > threshold) goTo(index - 1);
      else if (dx < -threshold) goTo(index + 1);
    });

    // Resize: recalc height for current slide
    let rAF = 0;
    window.addEventListener("resize", () => {
      cancelAnimationFrame(rAF);
      rAF = requestAnimationFrame(() => {
        applyTransformAndHeight();
      });
    });

    // Initial paint
    requestAnimationFrame(() => {
      if (!reduceMotion) track.style.transition = TRANS;
      applyTransformAndHeight();
      updateUI();
    });

    // --- small HTML escape helpers (scoped) ---
    function escapeHTML(s) {
      return String(s)
        .replace(/&/g, "&amp;")
        .replace(/</g, "&lt;")
        .replace(/>/g, "&gt;")
        .replace(/"/g, "&quot;")
        .replace(/'/g, "&#39;");
    }
    function escapeHTMLPreserveNewlines(s) {
      // Preserve paragraph breaks reasonably
      return escapeHTML(s)
        .replace(/\n{2,}/g, "<br><br>")
        .replace(/\n/g, "<br>");
    }
  }

  // Contact form behaviors (unchanged, kept here for completeness)
  (function () {
    const form = document.getElementById("contactForm");
    const msg = document.getElementById("message");
    const counter = document.getElementById("charCount");
    const radioOther = document.getElementById("programmeOther");
    const otherText = document.getElementById("programmeOtherText");
    const phone = document.getElementById("phone");

    // Character counter
    function updateCount() {
      if (msg && counter) counter.textContent = String(msg.value.length);
    }
    msg && msg.addEventListener("input", updateCount);
    updateCount();

    // "Other" toggle
    function syncOther() {
      if (!radioOther || !otherText) return;
      const on = radioOther.checked;
      otherText.disabled = !on;
      otherText.readOnly = !on;
      otherText.setAttribute("aria-disabled", String(!on));
      otherText.required = on; // required only when "Other" is selected
      if (!on) otherText.value = "";
    }
    document.querySelectorAll('input[name="programme"]').forEach((r) => {
      r.addEventListener("change", syncOther);
    });
    syncOther();

    // Phone rules (Singapore only)
    function normalizePhone(options) {
      const { alertOnError = false } = options || {};
      if (!phone) return true;

      let v = phone.value.trim();
      phone.setCustomValidity(""); // reset any previous error
      if (!v) return true; // empty is allowed

      if (v.startsWith("+")) {
        if (!v.startsWith("+65")) {
          const err = "Only Singapore numbers allowed";
          phone.setCustomValidity(err);
          if (alertOnError) alert(err);
          phone.reportValidity();
          return false;
        }
        // Ensure a single space after +65
        phone.value = v.replace(/^\+65\s*/, "+65 ");
        return true;
      }

      // No leading '+': prepend "+65 " automatically
      phone.value = "+65 " + v.replace(/^\s+/, "");
      return true;
    }

    // On blur: quietly normalize/prefix
    phone &&
      phone.addEventListener("blur", () =>
        normalizePhone({ alertOnError: false })
      );

    // Submit: validate, then allow normal POST to Formspree (no AJAX)
    form.addEventListener("submit", (e) => {
      if (!normalizePhone({ alertOnError: true })) {
        e.preventDefault(); // block submit if invalid
        return;
      }
      if (!form.checkValidity()) {
        e.preventDefault();
        form.reportValidity();
        return;
      }
      // No preventDefault — browser navigates to Formspree as-is.
    });
  })();
})();

