    flex: 1;
    font: inherit;
    padding: 10px 12px;
    border: 1px solid var(--divider);
    border-radius: 8px;
    background: var(--header-bg);
    color: var(--text);
}

.blog-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: clamp(16px, 2.6vw, 24px);
}

@media (min-width: 900px) {
    .blog-grid {
        grid-template-columns: repeat(2, 1fr);
    }
}

.post-card {
    background: var(--header-bg);
    color: var(--text);
    border: 1px solid var(--divider);
    border-radius: 16px;
    padding: clamp(16px, 2.2vw, 22px);
    box-shadow: 0 10px 24px rgba(0, 0, 0, 0.10);
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.post-card h3 {
    margin: 0;
    line-height: 1.2;
    font-weight: 800;
    font-size: clamp(1.2rem, 1rem + 0.8vw, 1.6rem);
}

.post-card time {
    opacity: 0.8;
    font-size: 0.95rem;
}

.post-card .excerpt {
    line-height: 1.7;
}

.post-card .actions {
    margin-top: 6px;
}

.post-card .actions a {
    text-decoration: none;
}

.pager,
.post-pager {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    gap: 8px;
    margin-top: 16px;
}

.pager a,
.pager button,
.post-pager a {
    display: inline-block;
    padding: 8px 12px;
    border-radius: 999px;
    border: 1px solid var(--divider);
    background: var(--header-bg);
    color: var(--text);
    text-decoration: none;
}

.pager .current {
    font-weight: 800;
    border-color: var(--primary);
}

.post {
    max-width: 70ch;
    margin: 0 auto;
}

.post-body :is(img, video, iframe) {
    max-width: 100%;
    height: auto;
}

.post-body pre {
    overflow: auto;
    padding: 12px;
    background: var(--bg-alt);
    border-radius: 8px;
}

/* Reuse existing button pill if present */
.btn-pill {
    background: var(--cta-bg);
    color: var(--cta-text);
    border-radius: 999px;
    padding: 10px 18px;
    border: none;
    cursor: pointer;
}

.btn-pill:hover,
.btn-pill:focus-visible {
    background: var(--cta-hover-bg);
    color: var(--cta-hover-text);
}


/* ---------- BLOG & POST: wrap long hero titles ---------- */
.hero--wrap .hero-title,
.page--blog .hero .hero-title,
.page--post .hero .hero-title {
    /* turn off single-line truncation */
    white-space: normal;
    overflow: visible;
    text-overflow: clip;

    /* robust wrapping for long words / URLs */
    overflow-wrap: anywhere;
    /* modern */
    word-break: break-word;
    /* Safari safety */
    hyphens: auto;
    /* needs lang="en" on <html> (you have it) */

    /* comfortable reading measure + side padding */
    max-width: 72ch;
    margin-inline: clamp(12px, 4vw, 32px);
}


/* ---------- BLOG & POST: slimmer hero ---------- */
.hero.hero--slim,
.page--blog .hero,
.page--post .hero {
    min-height: clamp(220px, 24vw, 360px);
    padding-bottom: clamp(8px, 2vw, 20px);
    /* keep top look, trim bottom */
}

/* tighten further on phones, still keeping the top look intact */
@media (max-width: 767.98px) {

    .hero.hero--slim,
    .page--blog .hero,
    .page--post .hero {
        min-height: clamp(200px, 40vw, 300px);
    }
}


/* ---------- Blog filter controls: responsive + no overflow ---------- */

/* 1) Let controls wrap on small/medium widths */
.blog-controls,
.blog-filters,
.post-filters {
    display: flex;
    flex-wrap: wrap;
    gap: 8px;
    align-items: center;
}

/* 2) Allow shrinking inside flex row (critical on iOS Safari) */
.blog-controls input[type="search"],
.blog-controls select,
.blog-controls button,
.blog-filters input[type="search"],
.blog-filters select,
.blog-filters button,
.post-filters input[type="search"],
.post-filters select,
.post-filters button {
    min-width: 0;
    /* without this, controls may refuse to shrink */
}

/* 3) On very small phones, stack everything to full width */
@media (max-width: 379.98px) {

    .blog-controls,
    .blog-filters,
    .post-filters {
        display: grid;
        grid-template-columns: 1fr;
    }

    .blog-controls input[type="search"],
    .blog-controls select,
    .blog-controls button,
    .blog-filters input[type="search"],
    .blog-filters select,
    .blog-filters button,
    .post-filters input[type="search"],
    .post-filters select,
    .post-filters button {
        width: 100%;
    }
}

@media (max-width: 549.98px) {
    .hero-cta--contact {
        display: none;
    }
}

/* BLOG: keep hero CTAs visible on small screens */
@media (max-width: 549.98px) {
    .hero-cta--contact {
        display: inline-block !important;
        /* re-show "Jump to Posts" / "Back to Blog" */
    }
}

