.hero {
  /* layout */
  position: relative;
  display: grid;
  place-items: center;
  text-align: center;
  overflow: hidden;

  /* sizing */
  min-height: clamp(420px, 52vw, 640px);

  /* colour + fallbacks */
  color: #fff;
  background: #b68144;
  /* visible only while image loads */
  /* hero-scoped tokens (no theme duplication) */
  --hero-cta-bg: var(--dark-primary-strong);
  --hero-cta-text: #fff;
  --hero-cta-hover-bg: #8b0000;
  --hero-cta-hover-text: #fff;
  --hero-link: #7ee1ff;
  --hero-link-hover: #ff6d6f;
  --hero-link-underline: color-mix(in srgb, var(--hero-link) 60%, transparent);
}
.hero::after {
  content: "";
  position: absolute;
  inset: 0;
  background: var(--hero-overlay);
  z-index: 1;
}
.hero .hero-media {
  grid-area: 1 / 1;
  display: block;
  width: 100%;
  justify-self: stretch;
}
.hero .hero-media img {
  display: block;
  width: 100%;
  height: auto;
  /* safe with responsive picture sources */
  object-position: center;
  /* consistent crop focus */
}
.hero-content {
  grid-area: 1 / 1;
  position: relative;
  z-index: 2;
  max-width: min(92vw, 980px);
  padding: clamp(16px, 3vw, 40px);

  /* unify spacing without extra wrappers */
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: clamp(8px, 1.5vw, 18px);
}
.hero-title {
  margin: 0;
  font-weight: 800;
  line-height: 1.1;
  font-size: clamp(1rem, 5.6vw, 3rem);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.hero-bullets {
  list-style: none;
  margin: 6px 0 12px;
  padding: 0;
  display: inline-block;
  /* centers the UL */
  text-align: left;
}
.hero-bullets li {
  font-weight: 700;
  display: flex;
  margin: 6px 0;
  line-height: 1.6;
  font-size: clamp(1.12rem, 1rem + 1.35vw, 2rem);
  align-items: baseline;
  /* baseline-align icon + text */
  gap: clamp(15px, 2vw, 30px);
  /* scale spacing with viewport */
}
.hero-bullets li::before {
  content: "";
  display: inline-block;
  flex: none;
  /* keep icon from stretching in the flex row */
  width: 1em;
  /* proportional to the LI font-size */
  height: 1em;
  /* proportional to the LI font-size */
  background-image: url("../media/check-24.png");
  /* Hi‑DPI / Retina: serve the 2x asset when available */
  background-image: -webkit-image-set(url("../media/check-24.png") 1x,
      url("../media/check-48.png") 2x);
  background-image: image-set(url("../media/check-24.png") 1x,
      url("../media/check-48.png") 2x);
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
  /* preserve aspect; no distortion */
  transform: translateY(0.06em);
  /* subtle baseline alignment */
  opacity: 1;
  /* full contrast on dark hero overlay */
}
.hero-bullets a {
  color: var(--hero-link);
  font-weight: 700;
  text-decoration: underline;
  text-decoration-color: var(--hero-link-underline, currentColor);
  text-underline-offset: 3px;
}
.hero-bullets a:hover,
.hero-bullets a:focus-visible {
  color: var(--hero-link-hover);
  text-decoration-color: var(--hero-link-hover);
}
.hero-actions {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: clamp(12px, 4vh, 60px);
}
.hero-cta {
  display: inline-block;
