  }

  /* =========================
   * Nav from JSON (desktop -> mobile)
   * ========================= */

  async function initMenuFromFile() {
    // If a server-rendered nav is present (future SSG/SSR), skip rebuilding.
    if (desktopNav?.dataset.menu === "ssr" && desktopList?.children.length)
      return;
    if (!desktopList) return;

    let items = null;
    try {
      // same-origin fetch; keep "no-cache" to revalidate
      items = await loadJSON("assets/data/menu.json", { cache: "no-cache" });
    } catch (e) {
      console.warn("Menu JSON failed to load; using HTML fallback", e);
      return; // fallback: leave existing HTML as-is
    }

    if (!Array.isArray(items) || items.length === 0) {
      console.warn("Menu JSON empty; using HTML fallback");
      return;
    }

    renderDesktopMenu(items);
  }

  function renderDesktopMenu(items) {
    // Build exactly the DOM your CSS/JS expect:
    // .nav > ul > li.nav-item > (button.nav-link + .dropdown) | a.nav-link
    desktopList.innerHTML = "";

    const usedIds = new Set();
    const uniqueId = (base) => {
      let id = base;
      let n = 2;
      while (usedIds.has(id)) id = `${base}-${n++}`;
      usedIds.add(id);
      return id;
    };

    items.forEach((node, i) => {
      const hasChildren =
        Array.isArray(node?.children) && node.children.length > 0;

      // We'll only append the <li> if it ends up with content.
      const li = document.createElement("li");
      li.className = "nav-item";

      // inside renderDesktopMenu(items)
      if (hasChildren) {
        const baseId = `dd-${slugify(node.label || `section-${i + 1}`)}`;
        const ddId = uniqueId(baseId);

        // NEW: prefer <a> if a safe href is provided
        const safeHref = sanitizeHref(node?.href);
        let trigger; // either <a.nav-link> or <button.nav-link>

        if (safeHref) {
          const a = document.createElement("a");
          a.className = "nav-link";
          a.href = safeHref;
          a.setAttribute("aria-haspopup", "true");
          a.setAttribute("aria-expanded", "false");
          a.setAttribute("aria-controls", ddId);
          a.append(document.createTextNode(String(node.label || "Menu")));
          const arrow = document.createElement("span");
          arrow.className = "arrow";
          arrow.innerHTML = "&#9662;";
          a.appendChild(arrow);
          trigger = a;
        } else {
          const btn = document.createElement("button");
          btn.className = "nav-link";
          btn.type = "button";
          btn.setAttribute("aria-haspopup", "true");
          btn.setAttribute("aria-expanded", "false");
          btn.setAttribute("aria-controls", ddId);
          btn.append(document.createTextNode(String(node.label || "Menu")));
          const arrow = document.createElement("span");
          arrow.className = "arrow";
          arrow.innerHTML = "&#9662;";
          btn.appendChild(arrow);
          trigger = btn;
        }

        const dd = document.createElement("div");
        dd.className = "dropdown";
        dd.id = ddId;
        dd.setAttribute("role", "group");
        dd.setAttribute("aria-label", String(node.label || "Submenu"));

        node.children.forEach((child) => {
          const safe = sanitizeHref(child?.href);
          if (!safe) return;
          const a = document.createElement("a");
          a.href = safe;
          a.textContent = String(child.label || child.href);
          dd.appendChild(a);
        });

        if (dd.children.length > 0) {
          li.append(trigger, dd);
          desktopList.appendChild(li);
        }
      } else {
        const safe = sanitizeHref(node?.href);
        if (!safe) return; // skip unsafe/malformed leaf

        const a = document.createElement("a");
        a.className = "nav-link";
        a.href = safe;
        a.textContent = String(node.label || node.href);
        li.appendChild(a);
        desktopList.appendChild(li);
      }
    });
  }

  async function onReady() {
    initAriaStates();

    // 1) Replace desktop menu from JSON (no-op if JSON fails or SSR present)
    await initMenuFromFile();

    // 2) Now build the mobile drawer from the (possibly rebuilt) desktop nav
    buildMobileMenuFromDesktop();

    // 3) Behaviors
    initMobileMenuHandlers();
    initDesktopDropdowns();
    initTheme();
    updateFooterYear();
    initTestimonialsFromFile();

    // Optional: surface CSP violations in the console during Report-Only
    window.addEventListener("securitypolicyviolation", (e) => {
      // Helpful while you tune CSP in Report-Only mode
      console.warn(
        "CSP violation:",
        e.violatedDirective,
        "blocked:",
        e.blockedURI
      );
    });
  }

  /* ===== Utilities ===== */
  async function loadJSON(url, { cache = "no-cache" } = {}) {
    const res = await fetch(url, { cache });
    if (!res.ok) throw new Error(`Failed to load ${url}: ${res.status}`);
    return res.json();
  }

  /* ===== Testimonials from external JSON ===== */
  async function initTestimonialsFromFile() {
    const root = document.getElementById("testimonials");
    if (!root) return;

    const url =
      root.getAttribute("data-src") || "assets/data/testimonials.json";
    let items = [];
    try {
      items = await loadJSON(url);
    } catch (e) {
      console.warn("Testimonials JSON failed to load:", e);
    }

    if (!Array.isArray(items) || items.length === 0) {
      root.style.display = "none";
      return;
    }
    renderTestimonials(root, items);
  }

  function renderTestimonials(root, items) {
    const track = root.querySelector("#tTrack");
    const dots = root.querySelector("#tDots");
    const btnPrev = root.querySelector(".t-btn--prev");
    const btnNext = root.querySelector(".t-btn--next");

    function scrollTestimonialsIntoView() {
      const target = root.querySelector(".t-viewport") || root;
      const headerEl = document.querySelector(".header");
      const headerH = headerEl ? headerEl.getBoundingClientRect().height : 0;
      const y =
        target.getBoundingClientRect().top + window.scrollY - headerH - 8;
      const reduce =
        window.matchMedia &&
        window.matchMedia("(prefers-reduced-motion: reduce)").matches;
      window.scrollTo({
        top: Math.max(0, y),
        behavior: reduce ? "auto" : "smooth",
      });
    }

    if (!track || !dots) return;

    // Reset DOM
    track.innerHTML = "";
    dots.innerHTML = "";

    // Build slides + dots
    const slideEls = items.map((t, i) => {
      const li = document.createElement("li");
      li.className = "t-slide";
      li.id = `t-slide-${i}`;
      li.setAttribute("role", "group");
      li.setAttribute("aria-roledescription", "slide");
      li.setAttribute("aria-label", `${i + 1} of ${items.length}`);

