    <!-- Important: keep 404s out of the index -->
    <meta name="robots" content="noindex, nofollow, noarchive" />

    <!-- Early theme boot (mirrors index.html) to avoid FOUC -->
    <script>
      (function () {
        try {
          var saved = localStorage.getItem("theme"); // 'light' | 'dark' | null
          var prefersDark =
            window.matchMedia &&
            window.matchMedia("(prefers-color-scheme: dark)").matches;
          var theme = saved || (prefersDark ? "dark" : "light");
          if (theme === "dark")
            document.documentElement.setAttribute("data-theme", "dark");
          else document.documentElement.setAttribute("data-theme", "light");
        } catch (e) {}
      })();
    </script>

    <!-- Site CSS (inherits tokens + components for both light/dark) -->
    <link rel="stylesheet" href="assets/css/tuition-css-1.0.1.css?v=1.0.1.E" />

    <!-- Page-scoped styles (small, theme-token based) -->
    <style>
      html { color-scheme: light dark; }
      .nf-wrap {
        display: grid;
        place-items: center;
        min-height: calc(100dvh - 56px - 60px); /* viewport minus header/footer */
        padding: clamp(24px, 4vw, 48px) 16px;
        background: var(--bg);
      }
      .nf-card {
        width: min(980px, 100%);
        background: var(--bg-alt);
        color: var(--text);
        border-radius: 16px;
        box-shadow: var(--dropdown-shadow);
        padding: clamp(20px, 3vw, 36px);
      }
      .nf-title { font-size: clamp(2rem, 4vw, 3rem); margin: 0 0 8px; }
      .nf-sub   { margin: 0 0 20px; opacity: .9; }

      .nf-links { 
        display: grid; 
        grid-template-columns: repeat(auto-fit, minmax(220px,1fr));
        gap: 12px; 
        margin: 16px 0 8px;
      }
      .nf-link {
        display: block;
        background: var(--header-bg);
        border: 1px solid var(--divider);
        border-radius: 12px;
        padding: 14px 16px;
        text-decoration: none;
        color: inherit;
        transition: transform .06s ease-out;
      }
      .nf-link:focus-visible, .nf-link:hover { outline: none; transform: translateY(-1px); }
      .nf-link strong { display: block; font-size: 1.05rem; }
      .nf-link small { opacity: .8; }

      .nf-cta { margin-top: 18px; display: flex; gap: 12px; flex-wrap: wrap; }
      .nf-note { margin-top: 16px; font-size: .95rem; opacity: .85; }

      /* Make the big emoji subtle across themes */
      .nf-emoji { font-size: 2rem; opacity: .8; }
    </style>
  </head>

  <body>
    <a class="skip-link" href="#main-content">Skip to content</a>

