          <span aria-hidden="true"></span><span aria-hidden="true"></span><span aria-hidden="true"></span>
        </button>
        <button class="theme-toggle" aria-label="Toggle light/dark mode" title="Toggle light/dark mode" aria-pressed="false">&#9790;</button>
      </div>
    </header>

    <!-- Mobile menu overlay & drawer (rebuilt by tuition-js) -->
    <div class="overlay" aria-hidden="true"></div>
    <nav class="mobile-menu" id="mobile-menu" aria-label="Mobile navigation" aria-hidden="true"></nav>

