## `/assets/js/blog-post-1.0.0.js`

```js
/*
  Headless blog renderer with Yoast SEO integration
  -------------------------------------------------
  - Fetches WP post by slug (from ?slug= or /blog/{slug} later)
  - Renders article content
  - Injects SEO <head>: <title>, description, canonical, robots,
    Open Graph, Twitter Card, and JSON-LD (preferring Yoast schema)
  - Falls back to synthesized metas/schema if Yoast is missing
  - Idempotent: re-renders cleanly if navigated via client-side router
*/
(function () {
  'use strict';

  const CFG = window.BLOG_CONFIG || {};
  const FRONTEND_ORIGIN = trimTrailingSlash(CFG.FRONTEND_ORIGIN || location.origin);
  const WP_API_BASE = trimTrailingSlash(CFG.WP_API_BASE || (location.origin + '/wp-json'));
  const ROUTE_BASE = trimTrailingSlash(CFG.ROUTE_BASE || '/blog');
  const POST_URL_MODE = CFG.POST_URL_MODE || 'query'; // 'query' | 'pretty'
  const SITE_TITLE_SUFFIX = CFG.SITE_TITLE_SUFFIX || '';
  const CATEGORY_BASE = trimTrailingSlash(CFG.CATEGORY_BASE || '/blog/category');
  const TAG_BASE = trimTrailingSlash(CFG.TAG_BASE || '/blog/tag');
  const PUBLISHER = CFG.PUBLISHER || { name: document.title.replace(/\s*\|.*$/, ''), logoUrl: '' };

  document.addEventListener('DOMContentLoaded', init);

  async function init() {
    const slug = resolveSlug();
    if (!slug) return notFound('Missing slug');

    try {
      const post = await fetchPostBySlug(slug);
      if (!post) return notFound('Post not found');

      const yoast = post.yoast_head_json || null;

      // Render body/content first (for UX), then head
      renderArticle(post);
      applyHead(yoast, post);

    } catch (err) {
      console.error('[blog] Failed to render post:', err);
      notFound('Error loading post');
    }
  }

  // -------------------------
  // Fetching
  // -------------------------
  async function fetchPostBySlug(slug) {
    const fields = [
      'id','slug','link','title','excerpt','content','date','modified',
      'yoast_head_json','_links','_embedded'
    ].join(',');
    const params = new URLSearchParams({ slug, _embed: '1', _fields: fields, per_page: '1' });

    let res = await fetch(`${WP_API_BASE}/wp/v2/posts?${params.toString()}`, { credentials: 'omit' });
    if (res.ok) {
      const arr = await res.json();
      if (arr && arr.length) return arr[0];
    }

    // Fallback to pages if not a post
    const p2 = new URLSearchParams({ slug, _embed: '1', _fields: fields, per_page: '1' });
    res = await fetch(`${WP_API_BASE}/wp/v2/pages?${p2.toString()}`, { credentials: 'omit' });
    if (res.ok) {
      const arr = await res.json();
      if (arr && arr.length) return arr[0];
    }

    return null;
  }

  // -------------------------
  // Rendering (BODY)
  // -------------------------
  function renderArticle(post) {
    const titleHTML = (post.title && post.title.rendered) || 'Untitled';
    const contentHTML = (post.content && post.content.rendered) || '';

    // Title
    const h1 = document.getElementById('post-title');
    if (h1) h1.innerHTML = titleHTML;

    // Author
    const author = extractAuthor(post);
    const authorWrap = document.getElementById('post-author');
    if (authorWrap) {
      const nameEl = authorWrap.querySelector('[itemprop="name"]') || authorWrap;
      nameEl.textContent = author || '';
    }

    // Dates
    const publishedISO = post.date ? new Date(post.date).toISOString() : '';
    const modifiedISO = post.modified ? new Date(post.modified).toISOString() : '';
    const pubEl = document.getElementById('post-date');
    if (pubEl) {
      pubEl.dateTime = publishedISO;
      pubEl.textContent = formatNiceDate(publishedISO);
    }
    const modEl = document.getElementById('post-modified');
    if (modEl && modifiedISO && modifiedISO !== publishedISO) {
      modEl.hidden = false;
      modEl.dateTime = modifiedISO;
      modEl.textContent = 'Updated ' + formatNiceDate(modifiedISO);
    }

    // Hero image (featured media)
    const hero = document.getElementById('post-hero');
    if (hero) {
      clearChildren(hero);
      const fm = extractFeaturedMedia(post);
      if (fm) {
        const img = document.createElement('img');
        img.src = fm.url;
        if (fm.width) img.width = fm.width;
        if (fm.height) img.height = fm.height;
        img.alt = fm.alt || '';
        img.loading = 'eager';
        img.decoding = 'async';
        hero.appendChild(img);
        if (fm.caption) {
          const figcap = document.createElement('figcaption');
          figcap.innerHTML = fm.caption;
          hero.appendChild(figcap);
        }
      }
    }

    // Content
    const body = document.getElementById('post-content');
    if (body) body.innerHTML = contentHTML;

    // Breadcrumbs
    renderBreadcrumbs(post);

    // Tags
    renderTags(post);
  }

  function extractAuthor(post) {
    try {
      const emb = post._embedded || {};
      const a = emb.author && emb.author[0];
      return (a && (a.name || a.slug || a.link)) || '';
    } catch (_) { return ''; }
  }

  function extractFeaturedMedia(post) {
    try {
      const media = post._embedded && post._embedded['wp:featuredmedia'] && post._embedded['wp:featuredmedia'][0];
      if (!media) return null;
      const sizes = (media.media_details && media.media_details.sizes) || {};
      const preferred = sizes['large'] || sizes['medium_large'] || sizes['full'] || null;
      const url = (preferred && preferred.source_url) || media.source_url;
      return {
        url,
        width: preferred && preferred.width,
        height: preferred && preferred.height,
        alt: media.alt_text || media.title?.rendered || '',
        caption: media.caption && media.caption.rendered
      };
    } catch (_) { return null; }
  }

  function renderBreadcrumbs(post) {
    const el = document.getElementById('breadcrumbs');
    if (!el) return;

    const cats = extractTerms(post, 'category');
    const primaryCat = cats[0] || null;
    const slug = post.slug;
    const home = FRONTEND_ORIGIN + '/';
    const blogHome = FRONTEND_ORIGIN + ROUTE_BASE;
    const postURL = buildFrontendPostUrl(slug);

    // Visual breadcrumbs
    clearChildren(el);
    const ol = document.createElement('ol');
    ol.className = 'breadcrumb-list';
    ol.innerHTML = [
      `<li><a href="${home}">Home</a></li>`,
      `<li><a href="${blogHome}">Blog</a></li>`,
      primaryCat ? `<li><a href="${FRONTEND_ORIGIN + CATEGORY_BASE + '/' + primaryCat.slug}">${escapeHTML(primaryCat.name)}</a></li>` : '',
      `<li aria-current="page">${escapeHTML(stripHTML(post.title?.rendered || 'Untitled'))}</li>`
    ].filter(Boolean).join('');
    el.appendChild(ol);

    // JSON-LD BreadcrumbList (appended in HEAD via applyHead())
  }

  function renderTags(post) {
    const el = document.getElementById('post-tags');
    if (!el) return;
    clearChildren(el);
    const tags = extractTerms(post, 'post_tag');
    for (const t of tags) {
      const li = document.createElement('li');
      const a = document.createElement('a');
      a.href = FRONTEND_ORIGIN + TAG_BASE + '/' + t.slug;
      a.textContent = t.name;
      li.appendChild(a);
      el.appendChild(li);
    }
  }

  function extractTerms(post, taxonomy) {
    try {
      const terms = post._embedded && post._embedded['wp:term'];
      if (!Array.isArray(terms)) return [];
      const flat = terms.flat().filter(Boolean);
      return flat.filter(x => x.taxonomy === taxonomy);
    } catch (_) { return []; }
  }

  // -------------------------
  // HEAD injection (SEO)
  // -------------------------
  function applyHead(yoast, post) {
    // Remove previously injected tags (in case of soft navigations)
    pruneManagedHead();

    const slug = post.slug;
    const canonical = normalizeFrontendUrl((yoast && yoast.canonical) || buildFrontendPostUrl(slug));

    // Title & description
    const titleText = (yoast && yoast.title) || stripHTML(post.title?.rendered || '');
    const descText = (yoast && yoast.description) || excerptText(post.excerpt?.rendered || post.content?.rendered || '', 160);

    document.title = joinTitle(titleText, SITE_TITLE_SUFFIX);
    upsertMetaByName('description', descText);
    upsertLinkRel('canonical', canonical);

    // Robots
    const robotsContent = yoast && yoast.robots ? serializeRobots(yoast.robots) : 'index,follow';
    upsertMetaByName('robots', robotsContent);

    // Open Graph
    upsertMetaByProperty('og:type', 'article');
    upsertMetaByProperty('og:url', canonical);
    upsertMetaByProperty('og:title', titleText);
    upsertMetaByProperty('og:description', descText);

    const ogImg = selectOGImage(yoast, post);
    if (ogImg) {
      upsertMetaByProperty('og:image', ogImg.url);
      if (ogImg.width) upsertMetaByProperty('og:image:width', String(ogImg.width));
      if (ogImg.height) upsertMetaByProperty('og:image:height', String(ogImg.height));
      if (ogImg.alt) upsertMetaByProperty('og:image:alt', ogImg.alt);
    }

    // Twitter
    upsertMetaByName('twitter:card', 'summary_large_image');
    upsertMetaByName('twitter:title', titleText);
    upsertMetaByName('twitter:description', descText);
    if (ogImg) upsertMetaByName('twitter:image', ogImg.url);

    // JSON-LD
    const graph = (yoast && yoast.schema) ? rewriteSchemaUrls(yoast.schema) : buildFallbackArticleSchema(post, canonical, ogImg);
    upsertJsonLd(graph);

    // Optional: JSON-LD BreadcrumbList
    const breadcrumbLd = buildBreadcrumbSchema(post, canonical);
    if (breadcrumbLd) upsertJsonLd(breadcrumbLd);
  }

  function selectOGImage(yoast, post) {
    // Prefer Yoast OG image if available; else featured media
    try {
      if (yoast && Array.isArray(yoast.og_image) && yoast.og_image.length) {
        const i = yoast.og_image[0];
        return { url: normalizeFrontendUrl(i.url || i.secure_url), width: i.width, height: i.height, alt: i.alt || '' };
      }
    } catch (_) {}
    const fm = extractFeaturedMedia(post);
    return fm ? { url: normalizeFrontendUrl(fm.url), width: fm.width, height: fm.height, alt: fm.alt } : null;
  }

  function buildFallbackArticleSchema(post, canonical, ogImg) {
    const headline = stripHTML(post.title?.rendered || '');
    const author = extractAuthor(post) || undefined;
    const published = post.date ? new Date(post.date).toISOString() : undefined;
    const modified = post.modified ? new Date(post.modified).toISOString() : undefined;

    const schema = {
      '@context': 'https://schema.org',
      '@type': 'BlogPosting',
      'mainEntityOfPage': canonical,
      'headline': headline,
      'description': excerptText(post.excerpt?.rendered || post.content?.rendered || '', 200),
      'datePublished': published,
      'dateModified': modified,
      'author': author ? { '@type': 'Person', 'name': author } : undefined,
      'publisher': {
        '@type': 'Organization',
        'name': PUBLISHER.name,
        'logo': PUBLISHER.logoUrl ? { '@type': 'ImageObject', url: PUBLISHER.logoUrl } : undefined
      },
      'image': ogImg ? [ogImg.url] : undefined,
      'url': canonical
    };
    return pruneEmpty(schema);
  }

  function buildBreadcrumbSchema(post, canonical) {
    const cats = extractTerms(post, 'category');
    const primaryCat = cats[0] || null;
    const items = [
      { name: 'Home', item: FRONTEND_ORIGIN + '/' },
      { name: 'Blog', item: FRONTEND_ORIGIN + ROUTE_BASE }
    ];
    if (primaryCat) items.push({ name: primaryCat.name, item: FRONTEND_ORIGIN + CATEGORY_BASE + '/' + primaryCat.slug });
    items.push({ name: stripHTML(post.title?.rendered || 'Untitled'), item: canonical });

    return {
      '@context': 'https://schema.org',
      '@type': 'BreadcrumbList',
      'itemListElement': items.map((x, i) => ({ '@type': 'ListItem', position: i + 1, name: x.name, item: x.item }))
    };
  }

  function upsertJsonLd(obj) {
    if (!obj) return;
    const s = document.createElement('script');
    s.type = 'application/ld+json';
    s.setAttribute('data-managed', 'seo');
    s.textContent = JSON.stringify(obj);
    document.head.appendChild(s);
  }

  function serializeRobots(robots) {
    // Yoast robots is an object like { index: 'index', follow: 'follow', max-snippet: -1, ... }
    const map = [];
    for (const [k, v] of Object.entries(robots)) {
      if (v === 'index' || v === 'follow') map.push(v);
      else if (typeof v === 'string' || typeof v === 'number') map.push(`${k}:${v}`);
      else if (v === true) map.push(k);
    }
    return map.join(',');
  }

  function pruneManagedHead() {
    const sel = 'meta[data-managed="seo"], link[rel="canonical"][data-managed="seo"], script[type="application/ld+json"][data-managed="seo"], meta[property^="og:"][data-managed="seo"], meta[name^="twitter:"][data-managed="seo"]';
    document.head.querySelectorAll(sel).forEach(n => n.remove());
  }

  function upsertMetaByName(name, content) {
    if (!content) return;
    let el = document.head.querySelector(`meta[name="${cssEscape(name)}"][data-managed="seo"]`);
    if (!el) {
      el = document.createElement('meta');
      el.setAttribute('name', name);
      el.setAttribute('data-managed', 'seo');
      document.head.appendChild(el);
    }
    el.setAttribute('content', content);
  }

  function upsertMetaByProperty(prop, content) {
    if (!content) return;
    let el = document.head.querySelector(`meta[property="${cssEscape(prop)}"][data-managed="seo"]`);
    if (!el) {
      el = document.createElement('meta');
      el.setAttribute('property', prop);
      el.setAttribute('data-managed', 'seo');
      document.head.appendChild(el);
    }
    el.setAttribute('content', content);
  }

  function upsertLinkRel(rel, href) {
    if (!href) return;
    let el = document.head.querySelector(`link[rel="${cssEscape(rel)}"][data-managed="seo"]`);
    if (!el) {
      el = document.createElement('link');
      el.setAttribute('rel', rel);
      el.setAttribute('data-managed', 'seo');
      document.head.appendChild(el);
    }
    el.setAttribute('href', href);
  }

  // -------------------------
  // Utilities
  // -------------------------
  function resolveSlug() {
    // Priority 1: ?slug=...
    const sp = new URLSearchParams(location.search);
    if (sp.has('slug')) return sp.get('slug').trim();

    // Priority 2 (future‑proof): /blog/{slug}
    const path = location.pathname.replace(/\/+$/, '');
    const base = ROUTE_BASE.replace(/\/+$/, '');
    const m = path.match(new RegExp('^' + escapeRegExp(base) + '/([^/]+)$'));
    if (m) return decodeURIComponent(m[1]);

    return '';
  }

  function buildFrontendPostUrl(slug) {
    if (POST_URL_MODE === 'pretty') {
      return `${FRONTEND_ORIGIN}${ROUTE_BASE}/${encodeURIComponent(slug)}`;
    }
    // default: query mode (this very page)
    const url = new URL(location.href);
    url.searchParams.set('slug', slug);
    url.hash = '';
    return url.toString();
  }

  function normalizeFrontendUrl(url) {
    if (!url) return url;
    try {
      const u = new URL(url, location.href);
      // Force canonical host to FRONTEND_ORIGIN
      const front = new URL(FRONTEND_ORIGIN);
      u.protocol = front.protocol;
      u.host = front.host;
      return u.toString();
    } catch (_) { return url; }
  }

  function rewriteSchemaUrls(schema) {
    // Deep‑clone and rewrite @id/url fields to FRONTEND_ORIGIN host
    const front = new URL(FRONTEND_ORIGIN);
    const clone = JSON.parse(JSON.stringify(schema));
    const visit = (node) => {
      if (!node || typeof node !== 'object') return;
      for (const key of Object.keys(node)) {
        const v = node[key];
        if (key === '@id' || key === 'url' || key === 'mainEntityOfPage') {
          node[key] = normalizeFrontendUrl(String(v));
        } else if (Array.isArray(v)) {
          v.forEach(visit);
        } else if (v && typeof v === 'object') {
          visit(v);
        }
      }
    };
    visit(clone);
    return clone;
  }

  function joinTitle(title, suffix) {
    if (!suffix) return title;
    const t = String(title || '').trim();
    return t.endsWith(suffix) ? t : (t + suffix);
  }

  function excerptText(html, n) {
    const text = stripHTML(html).replace(/\s+/g, ' ').trim();
    return text.length > n ? text.slice(0, n - 1).trimEnd() + '…' : text;
  }

  function stripHTML(html) {
    const tmp = document.createElement('div');
    tmp.innerHTML = html || '';
    return tmp.textContent || tmp.innerText || '';
  }

  function escapeHTML(s) {
    return String(s).replace(/[&<>"]/g, c => ({'&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;'}[c]));
  }

  function cssEscape(s) {
    return String(s).replace(/"/g, '\\"');
  }

  function clearChildren(node) {
    while (node && node.firstChild) node.removeChild(node.firstChild);
  }

  function pruneEmpty(obj) {
    if (!obj || typeof obj !== 'object') return obj;
    for (const k of Object.keys(obj)) {
      const v = obj[k];
      if (v && typeof v === 'object') pruneEmpty(v);
      if (v == null || v === '' || (typeof v === 'object' && !Array.isArray(v) && Object.keys(v).length === 0) || (Array.isArray(v) && v.length === 0)) {
        delete obj[k];
      }
    }
    return obj;
  }

  function formatNiceDate(iso) {
    if (!iso) return '';
    try {
      const d = new Date(iso);
      return d.toLocaleDateString('en-SG', { year: 'numeric', month: 'short', day: '2-digit' });
    } catch (_) { return ''; }
  }

  function trimTrailingSlash(s) {
    return String(s || '').replace(/\/$/, '');
  }

  function escapeRegExp(s) {
    return s.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
  }

  function notFound(message) {
    document.title = joinTitle('Not Found', SITE_TITLE_SUFFIX);
    upsertMetaByName('robots', 'noindex,follow');

    const h1 = document.getElementById('post-title');
    if (h1) h1.textContent = message || 'Not found';
    const body = document.getElementById('post-content');
    if (body) body.innerHTML = '<p>Sorry, the requested post could not be found.</p>';
  }
})();
