  <meta name="robots" content="<?= !empty($yoast['robots']) ? implode(',', array_filter(array_map(
      fn($k,$v)=>($v==='index'||$v==='follow')?$v:(is_bool($v)?($v?'$k':''):($k.':'.$v)),
