    }
}

/* ===== Left Contact Card =====
     Very dark brown for *all* modes to guarantee high contrast */
.contact-card {
    --card-bg: #2b1600;
    /* very dark brown: white text/links have excellent contrast */
    background: var(--card-bg);
    color: #fff;
    border-radius: 18px;
