}

.form-hint {
    font-size: 0.98rem;
    opacity: 0.85;
}

input[type="text"],
input[type="email"],
input[type="tel"],
textarea {
    font: inherit;
    color: var(--text);
    background: var(--bg);
    border: 1px solid var(--divider);
    border-radius: 12px;
