}

input:focus-visible,
textarea:focus-visible {
    outline: 3px solid var(--primary);
    outline-offset: 2px;
    border-color: var(--primary);
}

fieldset {
    border: 0;
    padding: 0;
    margin: 0 0 10px;
}

.form-legend {
    font-weight: 900;
    margin-bottom: 8px;
    font-size: clamp(1.15rem, 1rem + 0.6vw, 1.35rem);
}

.options {
    display: flex;
    flex-wrap: wrap;
    gap: 12px 20px;
}

.option {
    display: inline-flex;
    align-items: center;
    gap: 10px;
    font-size: clamp(1.02rem, 0.9rem + 0.5vw, 1.2rem);
}

.option input[type="radio"] {
    accent-color: var(--primary);
}

.option--other input[type="text"] {
    min-width: min(320px, 60vw);
}

.required-badge {
    font-weight: 700;
    color: #ff0000;
}

.btn-pill {
    display: inline-block;
    padding: 14px 28px;
    border-radius: 30px;
    background: var(--cta-bg);
    color: var(--cta-text);
    font-weight: 800;
    border: 0;
    cursor: pointer;
    font-size: clamp(1.02rem, 0.9rem + 0.5vw, 1.2rem);
    box-shadow: 0 0 0 1px color-mix(in srgb, var(--text) 20%, transparent),
        0 2px 8px rgba(0, 0, 0, 0.08);
    transition: background 0.2s ease, color 0.2s ease, transform 0.06s ease-in-out;
}

.btn-pill:hover,
.btn-pill:focus-visible {
    background: var(--cta-hover-bg);
    color: var(--cta-hover-text);
}

.btn-pill:active {
    transform: translateY(1px);
}

.form-actions {
    margin-top: 12px;
}

.form-alert {
    display: none;
    margin-top: 12px;
    padding: 12px 14px;
    border-radius: 12px;
    font-size: 1rem;
}

.form-alert--success {
    background: #e8f8ee;
    color: #065f46;
    border: 1px solid #10b981;
}

.form-alert--error {
    background: #fde8e8;
    color: #7f1d1d;
    border: 1px solid #ef4444;
}

:root[data-theme="dark"] .form-alert--success {
    background: #0b2b1f;
    color: #b7f3dc;
}

:root[data-theme="dark"] .form-alert--error {
    background: #3a0f0f;
    color: #fecaca;
}

.counter {
    font-size: 1rem;
    opacity: 0.75;
    text-align: right;
}

/* accessible “screen reader only” helper */
.sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    margin: -1px;
    padding: 0;
    border: 0;
    white-space: nowrap;
    clip-path: inset(50%);
    clip: rect(0 0 0 0);
    overflow: hidden;
}

/* =========================
     PATCH: Contact card links
     ========================= */
/* Use dark‑mode link/hover colours on the left card even when the page is in light mode. 
     We out‑specific the global '#main-content .section a' rules. */
:root[data-theme="light"] #main-content .section.section--contact .contact-card a {
    color: #7ec1ff;
    /* same as dark mode link colour */
    text-decoration-color: #7ec1ff;
    /* keep underline consistent */
}

:root[data-theme="light"] #main-content .section.section--contact .contact-card a:hover,
:root[data-theme="light"] #main-content .section.section--contact .contact-card a:focus-visible {
    color: #ff7578;
    /* same as dark mode hover colour */
    text-decoration-color: #ff7578;
}

/* Keep the white focus outline on the dark card for visibility */
.section--contact .contact-card a:focus-visible {
    outline: 3px solid #fff;
    outline-offset: 3px;
    border-radius: 6px;
}

/* ==============================================
     PATCH: "Send Message" pill – thicker theme ring
     ============================================== */
/* Light mode: thicker black perimeter */
:root[data-theme="light"] .section--contact .btn-pill {
    /* was a 1px subtle keyline; make it clearly 2px */
    box-shadow: 0 0 0 2px #000000, 0 2px 8px rgba(0, 0, 0, 0.08);
}

/* Dark mode: analogous 2px white perimeter */
:root[data-theme="dark"] .section--contact .btn-pill {
    box-shadow: 0 0 0 2px #ffffff, 0 2px 8px rgba(0, 0, 0, 0.18);
}

/* ================================
   CONTACT: XS overflow hardening
   ================================= */

/* Let grid children shrink with the viewport (safe globally) */
.contact-grid>* {
    min-width: 0;
    /* overrides grid default min-width:auto */
}

/* Inputs should never exceed their container */
.section--contact input[type="text"],
.section--contact input[type="email"],
.section--contact input[type="tel"],
.section--contact textarea {
    width: 100%;
    max-width: 100%;
}

/* When space is tight, allow the "Other" text box to wrap under its label */
@media (max-width: 359.98px) {
    .option--other {
        flex-wrap: wrap;
        /* allow children to break to a new line */
        align-items: flex-start;
        gap: 8px 10px;
        /* slightly denser on XS */
    }

    .option--other input[type="text"] {
        /* override global min(320px,60vw) so it cannot force the card wider */
        min-width: 0;
        flex: 1 1 100%;
        width: 100%;
    }

    /* Slightly tighter card padding to reclaim a few px on ultra-narrow phones */
    .contact-card,
    .contact-form {
        padding: 12px;
    }
}

/* Optional: give the grid a touch more usable width on very small phones */
@media (max-width: 399.98px) {
    .contact-grid {
        max-width: 96vw;
        /* was 92vw; reduces side margins to cut risk of scrollbars */
    }
}

/* ===== Footer ===== */
.footer {
    background: var(--footer-bg);
    color: var(--footer-text);
    text-align: center;
    padding: 24px 0 16px 0;
    font-size: 1rem;
    letter-spacing: 0.5px;
    transition: background 0.3s, color 0.3s;
}

/* ====== Desktop Styles ====== */
@media (min-width: 1200px) {
    .header {
        height: 72px;
        padding: 0 40px;
    }

    .logo img {
        max-height: 40px;
    }

    .theme-toggle {
        font-size: 1.7em;
    }

    .hamburger {
        display: none;
    }

    /* Hide hamburger on desktop */
    .nav {
        display: flex;
        align-items: center;
        gap: 0;
    }

    .nav ul {
        display: flex;
        align-items: center;
        gap: 0;
        margin: 0;
        padding: 0;
        list-style: none;
    }

    .nav-item {
        position: relative;
    }

    .nav-link {
        text-decoration: none;
        color: var(--text);
        font-weight: 500;
        padding: 12px 16px;
        border-radius: 4px;
        transition: background 0.2s;
        display: flex;
        align-items: center;
        cursor: pointer;
        background: none;
        border: none;
        font-size: 1em;
        line-height: 1.2;
    }

    .nav-link:hover {
        background: var(--nav-hover);
    }

    .arrow {
        margin-left: 6px;
        font-size: 0.8em;
        transition: transform 0.2s;
    }

    .dropdown {
        display: none;
        position: absolute;
        top: 100%;
        right: 0;
        background: var(--header-bg);
        min-width: 180px;
        box-shadow: var(--dropdown-shadow);
        border-radius: 6px;
        z-index: 1001;
        padding: 8px 0;
        flex-direction: column;
    }

    .dropdown a {
        display: block;
        padding: 10px 20px;
        color: var(--text);
        text-decoration: none;
        font-size: 1em;
        transition: background 0.2s;
    }

    .dropdown a:hover {
        background: var(--nav-hover);
    }

    /* Make dropdown keyboard-openable, not just hover */
    .nav-item:hover .dropdown,
    .nav-item:focus-within .dropdown {
        display: flex;
    }

    .nav-item.force-close .dropdown {
        display: none !important;
    }

    .nav-item:focus-within .arrow,
    .nav-item:hover .arrow {
        transform: rotate(180deg);
    }

    .mobile-menu,
    .overlay {
        display: none !important;
    }

    /* Hide mobile UI */
    .hero {
        height: 420px;
    }

    .section {
        padding: 64px 0;
    }

    .section-title {
        font-size: 2rem;
    }

    .section-content {
        max-width: 700px;
        font-size: 1.1rem;
    }
}

/* ===== WhatsApp floating button ===== */
.whatsapp-button {
    position: fixed;
    bottom: 20px;
    right: 20px;
    background-color: transparent;
    border-radius: 50%;
    cursor: pointer;
    transition: transform 0.2s ease-in-out;
    z-index: 1002;
    width: 60px;
    height: 60px;
}

.whatsapp-button img {
    width: 100%;
    height: 100%;
    object-fit: contain;
}

.whatsapp-button:hover,
.whatsapp-button:active {
    transform: scale(1.1);
    animation: pulse 1s infinite;
}

@media (min-width: 768px) {
    .whatsapp-button {
        width: 80px;
        height: 80px;
    }

    .whatsapp-button:hover,
    .whatsapp-button:active {
        transform: scale(1.15);
    }
}

@keyframes pulse {
    0% {
        transform: scale(1);
    }

    50% {
        transform: scale(1.05);
    }

    100% {
        transform: scale(1);
    }
}

/* Respect users who prefer reduced motion */
@media (prefers-reduced-motion: reduce) {
    * {
        animation: none !important;
        transition: none !important;
    }

    .whatsapp-button:hover,
    .whatsapp-button:active {
        transform: none;
        animation: none;
    }
}

/* --- Fix descender clipping in hero titles on non-blog pages --- */
.hero .hero-title {
    /* give glyphs with descenders (g,j,p,q,y) headroom */
    line-height: 1.15;
    /* was effectively ~1.00; bump slightly */
    padding-bottom: 0.12em;
    /* tiny breathing room, visually neutral */
}

/* If a container is clipping, ensure the text can render fully */
.hero .hero-content {
    overflow: visible;
    /* safe for text; does not affect background */
}


/* 700–1199px: compact hero + tighter vertical rhythm */
@media (min-width: 700px) and (max-width: 1199.98px) {

    /* shorter overall hero */
    .hero {
        /* was clamp(420px, 52vw, 640px) */
        min-height: clamp(320px, 40vw, 480px);
        /* lift content toward the sticky header */
        align-items: flex-start;
        /* overrides place-items:center vertically */
    }

    /* reduce the gap above/below the hero text block */
    .hero-content {
        padding-block-start: 10px;
        /* less space under header */
        padding-block-end: 12px;
        /* less space above next section */
        gap: clamp(6px, 1vw, 12px);
        /* slightly tighter internal spacing */
    }

    /* CTAs stack tighter on tablets */
    .hero-actions {
        gap: clamp(8px, 1.8vh, 24px);
    }

    /* first content section starts closer to the hero */
    #main-content>.section:first-of-type {
        padding-top: 18px;
    }
}


/* Make the hero image always fill the hero’s height */
.hero {
    position: relative;
    /* already present; needed for absolute positioning */
    overflow: hidden;
    /* hide the crop */
}

/* Pin the <picture> to the hero’s edges so it defines the backdrop */
.hero-media {
    position: absolute;
    inset: 0;
    /* top:0 right:0 bottom:0 left:0 */
    z-index: 1;
    /* stays behind the text (hero-content has z-index:2) */
    height: 100%;
    pointer-events: none;
    /* image never blocks clicks on CTAs */
}

/* Force the <img> to fill the box and crop as needed */
.hero .hero-media img {
    width: 100%;
    height: 100% !important;
    /* override inline height:auto */
    object-fit: cover;
    /* fill + maintain aspect ratio, crop overflow */
    object-position: center;
    /* adjust focal point as needed */
}

(() => {
    "use strict";

    const UNIVERSITY_REDIRECT_CARD = Object.freeze({
        href: "/university-maths.html",
        title: "Looking for University Maths?",
        message: "Our University Maths programme page now lives at /university-maths.html.",
        ctaLabel: "Go to University Maths",
        ariaLabel: "Visit the University Maths page",
    });

    const JUNIOR_COLLEGE_REDIRECT_CARD = Object.freeze({
        href: "/junior-college-maths.html",
        title: "Looking for Junior College Maths?",
        message: "We renamed this page — here is the updated Junior College Maths overview.",
        ctaLabel: "Go to Junior College Maths",
        ariaLabel: "Visit the Junior College Maths page",
    });

    const SECONDARY_REDIRECT_CARD = Object.freeze({
        href: "/secondary-school-maths.html",
        title: "Looking for Upper Secondary Maths?",
        message: "Upper Secondary (E & A Maths) resources are now consolidated here.",
        ctaLabel: "Go to Upper Secondary Maths",
        ariaLabel: "Visit the Upper Secondary Maths page",
    });

    const PROGRAMME_REDIRECTS = new Map([
        ["university", UNIVERSITY_REDIRECT_CARD],
        ["juniorcollege", JUNIOR_COLLEGE_REDIRECT_CARD],
        ["secondary", SECONDARY_REDIRECT_CARD],
        ["secondaryschool", SECONDARY_REDIRECT_CARD],
    ]);

    const SIMPLE_PATH_REDIRECTS = new Map([
        ["blog", {
            href: "/blog.html",
            title: "Trying to reach the blog?",
            message: "Our latest articles now live at /blog.html on the static site.",
            ctaLabel: "Go to Blog",
            ariaLabel: "Open the blog overview page",
        }],
        ["contact", {
            href: "#contact",
            title: "Need to contact us?",
            message: "Use the contact section below to reach Dr Ng directly.",
            ctaLabel: "Jump to contact form",
            ariaLabel: "Jump to the contact form section",
        }],
    ]);

    function decodePathSegments(pathname) {
        let p = String(pathname || "").split("?")[0].split("#")[0];
        p = p.replace(/^\/+|\/+$/g, "");
        if (!p) return [];
        return p
            .split("/")
            .filter(Boolean)
            .map((seg) => {
                try {
                    return decodeURIComponent(seg).toLowerCase();
                } catch (_) {
                    return String(seg).toLowerCase();
                }
            });
    }

    function matchProgrammeRedirect(segments) {
        if (segments.length !== 1) return null;
        const compact = segments[0].replace(/[^a-z0-9]/g, "");
        if (!compact) return null;
        const stripped = compact.replace(/(mathematics|maths|math)$/i, "");
        if (stripped.length === compact.length) return null; // No maths suffix found.
        return PROGRAMME_REDIRECTS.get(stripped) || null;
    }

    function matchSimpleRedirect(segments) {
        if (segments.length !== 1) return null;
        const key = segments[0].replace(/[^a-z0-9]/g, "");
        if (!key) return null;
        return SIMPLE_PATH_REDIRECTS.get(key) || null;
    }

    function renderSuggestionCard(config) {
        if (!config) return;
        const box = document.getElementById("legacy-suggestion");
        if (!box) return;
        const section = box.closest(".section--narrow");
        if (section) section.classList.remove("is-empty");
        box.textContent = "";

        const card = document.createElement("div");
        card.className = "legacy-suggestion-card";

        const titleEl = document.createElement("h2");
        titleEl.className = "legacy-suggestion-title";
        titleEl.textContent = config.title || "Did you mean this page?";
        card.appendChild(titleEl);

        if (config.message) {
            const messageEl = document.createElement("p");
            messageEl.textContent = config.message;
            card.appendChild(messageEl);
        }

        if (config.href) {
            const ctaWrapper = document.createElement("p");
            const link = document.createElement("a");
            link.className = "hero-cta hero-cta--contact";
            link.href = config.href;
            link.textContent = config.ctaLabel || "Visit suggested page";
            if (config.ariaLabel) {
                link.setAttribute("aria-label", config.ariaLabel);
            }
            ctaWrapper.appendChild(link);
            card.appendChild(ctaWrapper);
        }

        box.appendChild(card);
    }

    function firstSegment(pathname) {
        let p = String(pathname || "").split("?")[0].split("#")[0];
        p = p.replace(/^\/+|\/+$/g, "");
        const segs = p.split("/").filter(Boolean).map((s) => {
            try { return decodeURIComponent(s); } catch { return s; }
        });
        return segs.length === 1 ? segs[0].toLowerCase() : "";
    }

    function isLegacyCandidate(slug) {
        if (!slug) return false;
        const reserved = new Set([
            "blog", "blog.htm", "assets", "wp-json", "wp-admin", "wp-login.php", "api",
            "favicon.ico", "index.html", "blog-post.html"
        ]);
        return !reserved.has(slug) && !slug.includes(".");
    }

    async function readStaticRegistry() {
        const candidates = [
            "/assets/data/static-posts.json",
            "/assets/data/blog.json",
        ];
        for (const url of candidates) {
            try {
                const res = await fetch(url, { credentials: "same-origin", cache: "no-store" });
                if (!res.ok) continue;
                const data = await res.json();

                /**
                 * Normalise the various manifest shapes we may encounter:
                 *  • ["slug-a", "slug-b"]
                 *  • [{ slug: "slug-a" }, ...]
                 *  • { posts: { "slug-a": "/blog/slug-a/" } }
                 *  • { posts: ["slug-a"] }
                 */
                let rawSlugs = [];
                if (Array.isArray(data)) {
                    rawSlugs = data;
                } else if (data && typeof data === "object") {
                    const { posts, items } = data;
                    if (Array.isArray(posts)) {
                        rawSlugs = posts;
                    } else if (posts && typeof posts === "object") {
                        rawSlugs = Object.keys(posts);
                    } else if (Array.isArray(items)) {
                        rawSlugs = items;
                    } else {
                        rawSlugs = Object.keys(data).filter((key) => !/^schema/i.test(key));
                    }
                }

                const slugs = new Set(
                    rawSlugs
                        .map((entry) => {
                            if (!entry) return "";
                            if (typeof entry === "string") return entry;
                            if (typeof entry.slug === "string") return entry.slug;
                            return "";
                        })
                        .filter(Boolean)
                        .map((s) => String(s).toLowerCase())
                );

                if (slugs.size) return slugs;
            } catch (_) {
                /* fall through */
            }
        }
        return new Set();
    }

    async function wpPostExists(slug) {
        // Public WP REST only exposes published posts; that's fine for suggestions.
        const url = `/wp-json/wp/v2/posts?slug=${encodeURIComponent(slug)}&_fields=slug,status&per_page=1`;
        const ctl = new AbortController();
        const timer = setTimeout(() => ctl.abort(), 4000);
        try {
            const res = await fetch(url, { signal: ctl.signal, credentials: "same-origin", cache: "no-store" });
            if (!res.ok) return false;
            const arr = await res.json();
            if (!Array.isArray(arr) || arr.length === 0) return false;
            const post = arr[0];
            return !!(post && typeof post.slug === "string" && post.slug.toLowerCase() === slug);
        } catch (_) {
            return false; // Network or timeout: stay silent.
        } finally {
            clearTimeout(timer);
        }
    }

    function buildStaticSuggestion(slug) {
        return {
            href: `/blog/${encodeURIComponent(slug)}/`,
            label: `Open static article: /blog/${slug}/`,
            inStatic: true,
        };
    }

    function buildDynamicSuggestion(slug) {
        return {
            href: `/blog-post.html?slug=${encodeURIComponent(slug)}`,
            label: `Open dynamic article: blog-post.html?slug=${slug}`,
            inStatic: false,
        };
    }

    function showSuggestion({ href, label, inStatic }) {
        renderSuggestionCard({
            href,
            ctaLabel: "Visit suggested page",
            title: "Did you mean this article?",
            ariaLabel: label,
        });
    }

    function collapseSuggestion() {
        const box = document.getElementById("legacy-suggestion");
        if (!box) return;
        const section = box.closest(".section--narrow");
        if (section) section.classList.add("is-empty");
        box.innerHTML = "";
    }

    document.addEventListener("DOMContentLoaded", async () => {
        const segments = decodePathSegments(location.pathname);
        const programmeRedirect = matchProgrammeRedirect(segments);
        if (programmeRedirect) {
            renderSuggestionCard(programmeRedirect);
            return;
        }

        const simpleRedirect = matchSimpleRedirect(segments);
        if (simpleRedirect) {
            renderSuggestionCard(simpleRedirect);
            return;
        }

        const slug = firstSegment(location.pathname);
        if (!isLegacyCandidate(slug)) {
            collapseSuggestion();
            return;
        }

        let suggested = false;

        try {
            const staticSlugs = await readStaticRegistry();
            if (staticSlugs.has(slug)) {
                showSuggestion(buildStaticSuggestion(slug));
                suggested = true;
                return;
            }

            // Not in static registry — verify in WP before suggesting dynamic view
            if (await wpPostExists(slug)) {
                showSuggestion(buildDynamicSuggestion(slug));
                suggested = true;
                return;
            }
            // else: no suggestion; keep the generic 404 content
        } catch { /* soft-fail */ }

        if (!suggested) collapseSuggestion();
    });
})();

