*,
*::before,
*::after {
    box-sizing: border-box;
}

.skip-link {
    position: absolute;
    left: -9999px;
    top: auto;
    width: 1px;
    height: 1px;
    overflow: hidden;
}

.skip-link:focus {
    left: 8px;
    top: 8px;
    width: auto;
    height: auto;
    padding: 8px 12px;
    background: var(--header-bg);
    color: var(--text);
    border-radius: 4px;
    z-index: 2000;
}

body {
    margin: 0;
    font-family: "Segoe UI", Arial, sans-serif;
    background: var(--bg);
    color: var(--text);
}

/* ===== Header ===== */
.header {
    position: sticky;
    top: 0;
    background: var(--header-bg);
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 16px;
    height: 56px;
    z-index: 1000;
    box-shadow: var(--header-shadow);
    transition: background 0.3s;
}

/* default: show light logo, hide dark one */
.logo-dark {
    display: none;
}

:root[data-theme="dark"] .logo-light {
    display: none;
}

:root[data-theme="dark"] .logo-dark {
    display: inline-block;
}

:root[data-theme="dark"] .brand img {
    filter: none;
}

/* Brand area aligns logo + short title on one line */
.brand {
    display: inline-flex;
    align-items: center;
    gap: 0.6rem;
    text-decoration: none;
    color: var(--text);
}

.brand:focus-visible {
    outline: 2px solid var(--primary);
    outline-offset: 2px;
}

/* Short site title: compact, bold, respects theme colors */
.brand-title {
    font-weight: 700;
