    /* Dark palette */
    --dark-bg: #23272f;
    --dark-bg-alt: #2e323a;
    --dark-header: #181a20;
    --dark-footer: #111;
    --dark-footer-text: #eee;
    --dark-primary: #4ea1ff;
    --dark-text: #f5f6fa;
