        display: flex;
        align-items: center;
        gap: 0;
        margin: 0;
        padding: 0;
        list-style: none;
    }

    .nav-item {
        position: relative;
    }

    .nav-link {
        text-decoration: none;
        color: var(--text);
        font-weight: 500;
        padding: 12px 16px;
        border-radius: 4px;
        transition: background 0.2s;
        display: flex;
        align-items: center;
        cursor: pointer;
        background: none;
        border: none;
        font-size: 1em;
        line-height: 1.2;
    }

    .nav-link:hover {
        background: var(--nav-hover);
    }

    .arrow {
        margin-left: 6px;
        font-size: 0.8em;
        transition: transform 0.2s;
    }

    .dropdown {
        display: none;
        position: absolute;
        top: 100%;
        right: 0;
        background: var(--header-bg);
        min-width: 180px;
        box-shadow: var(--dropdown-shadow);
        border-radius: 6px;
        z-index: 1001;
        padding: 8px 0;
        flex-direction: column;
    }

    .dropdown a {
        display: block;
        padding: 10px 20px;
        color: var(--text);
        text-decoration: none;
        font-size: 1em;
        transition: background 0.2s;
    }

    .dropdown a:hover {
        background: var(--nav-hover);
    }

    /* Make dropdown keyboard-openable, not just hover */
    .nav-item:hover .dropdown,
    .nav-item:focus-within .dropdown {
        display: flex;
    }

    .nav-item.force-close .dropdown {
        display: none !important;
    }

    .nav-item:focus-within .arrow,
    .nav-item:hover .arrow {
        transform: rotate(180deg);
    }

    .mobile-menu,
    .overlay {
        display: none !important;
    }

    /* Hide mobile UI */
