}

.section--programs .program-media img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
}

.section--programs .program-text {
    /* left-justified text, but constrained + centered within the card */
    width: 88%;
    max-width: 46ch;
    text-align: left;
}

.section--programs .program-title {
    margin: 2px 0 6px;
    font-weight: 800;
    line-height: 1.2;
    font-size: clamp(1.25rem, 1.05rem + 0.9vw, 1.6rem);
