        background: var(--nav-hover);
    }

    .arrow {
        margin-left: 6px;
        font-size: 0.8em;
        transition: transform 0.2s;
    }

    .dropdown {
        display: none;
        position: absolute;
        top: 100%;
        right: 0;
        background: var(--header-bg);
        min-width: 180px;
        box-shadow: var(--dropdown-shadow);
        border-radius: 6px;
        z-index: 1001;
        padding: 8px 0;
        flex-direction: column;
    }

    .dropdown a {
        display: block;
        padding: 10px 20px;
        color: var(--text);
        text-decoration: none;
        font-size: 1em;
        transition: background 0.2s;
    }

    .dropdown a:hover {
        background: var(--nav-hover);
