# Create the extended HTML file based on the user's uploaded tuition.html, 
# adding additional sections inspired by the microsite.
html = """<!doctype html>
<html lang="en">
<head>
  <meta charset="utf-8" />
  <title>SG Maths Tuition — Personalised Secondary & JC Math Tutoring</title>
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <meta name="description" content="Ace Secondary & JC Math with personalised tutoring by Dr. Ng. One-on-one and small group classes for E-Math, A-Math, H1, H2, H3.">
  <link rel="icon" href="data:image/svg+xml,%3Csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 100 100'%3E%3Ctext y='0.9em' font-size='90'%3E%F0%9F%93%9A%3C/text%3E%3C/svg%3E">
  <style>
    :root{
      --bg:#ffffff;
      --ink:#111827;
      --muted:#4b5563;
      --accent:#0ea5e9;       /* brand blue (matches existing file) */
      --accent-ink:#0b1520;
      --card:#f9fafb;
      --ring:rgba(14,165,233,0.30);
      --radius:14px;
      --gap:18px;
      --maxw:1120px;
      --shadow:0 1px 0 rgba(0,0,0,.03),0 10px 20px rgba(0,0,0,.04);
    }
    /* base */
    *{box-sizing:border-box}
    html:focus-within{scroll-behavior:smooth}
    body{
      margin:0;
      font:18px/1.65 system-ui,-apple-system,Segoe UI,Roboto,Arial,sans-serif;
      color:var(--ink);
      background:var(--bg);
      text-rendering:optimizeLegibility;
    }
    img{max-width:100%;height:auto;display:block}
    a{color:var(--accent);text-decoration:underline;text-underline-offset:2px}
    .wrap{max-width:var(--maxw);margin:0 auto;padding:48px 20px}
    .eyebrow{
      text-transform:uppercase;letter-spacing:.06em;font-weight:700;
      color:var(--accent);margin-bottom:6px;font-size:clamp(15px,1.4vw,20px)
    }
    h1{font-size:clamp(30px,6vw,48px);line-height:1.15;margin:0 0 8px}
    h2{margin:8px 0 10px;font-size:clamp(24px,4vw,36px);line-height:1.2}
    h3{margin:0 0 6px;font-size:clamp(18px,2.6vw,22px);line-height:1.3}
    h4{margin:0 0 6px;font-size:clamp(16px,2.2vw,20px)}
    p.lead{color:var(--muted);margin:8px 0 22px;font-size:1.15em}
    .muted{color:var(--muted)}
    .divider{height:1px;background:#e5e7eb;margin:28px 0}
    .grid{display:grid;gap:var(--gap)}
    .grid-2{grid-template-columns:repeat(2,minmax(0,1fr))}
    .grid-3{grid-template-columns:repeat(3,minmax(0,1fr))}
    .grid-4{grid-template-columns:repeat(4,minmax(0,1fr))}
    @media (max-width:980px){.grid-3,.grid-4{grid-template-columns:1fr 1fr}}
    @media (max-width:760px){.grid-2{grid-template-columns:1fr}.grid-3,.grid-4{grid-template-columns:1fr}}

    /* reusable UI */
    .card,.step{background:var(--card);border-radius:var(--radius);padding:18px 16px;border:1px solid #e5e7eb;box-shadow:0 1px 0 rgba(0,0,0,.03)}
    .card p,.step p{margin:6px 0 0;color:var(--muted);line-height:1.65}
    .btn{display:inline-flex;align-items:center;gap:10px;padding:12px 16px;border-radius:12px;border:1px solid transparent;font-weight:700;text-decoration:none}
    .btn:focus-visible{outline:2px solid var(--accent);outline-offset:2px}
    .btn-primary{background:var(--accent);color:#fff;box-shadow:0 0 0 6px var(--ring)}
    .btn-ghost{background:transparent;border-color:#cbd5e1;color:var(--ink)}
    .badge{display:inline-block;padding:4px 8px;border-radius:999px;background:#e0f2fe;color:#075985;font-weight:700;font-size:12px;letter-spacing:.04em}

    /* header + nav */
    .topbar{
      position:sticky;top:0;z-index:50;background:rgba(255,255,255,.86);
      backdrop-filter:blur(10px);border-bottom:1px solid #e5e7eb
    }
    .topbar-inner{display:flex;align-items:center;justify-content:space-between;gap:16px;padding:12px 20px;max-width:var(--maxw);margin:0 auto}
    .brand{display:flex;align-items:center;gap:12px}
    .brand-logo{width:36px;height:36px;border-radius:9px;background:linear-gradient(135deg,#bae6fd,#38bdf8);display:grid;place-items:center;color:#075985;font-weight:900}
    .brand b{font-size:18px}
    .contact-links{display:flex;align-items:center;gap:16px;flex-wrap:wrap}
    .contact-links a{display:inline-flex;align-items:center;gap:8px;text-decoration:none}
    .contact-links a .icon{width:18px;height:18px}
    .nav{display:flex;gap:14px;flex-wrap:wrap}
    .nav a{padding:8px 10px;border-radius:10px;text-decoration:none;color:var(--ink)}
    .nav a:hover{background:#f1f5f9}
    .menu{display:flex;align-items:center;gap:14px}
    @media (max-width:860px){
      .nav{display:none}
      .menu-toggle{display:inline-flex}
    }
    @media (min-width:861px){.menu-toggle{display:none}}
    .mobile-menu{display:none;padding:10px 20px;border-top:1px solid #e5e7eb}
    .mobile-menu a{display:block;padding:10px 0;color:var(--ink);text-decoration:none}
    .mobile-menu a + a{border-top:1px dashed #e5e7eb}

    /* hero */
    .hero{background:
      radial-gradient(900px 300px at 10% -10%, #e0f2fe 0%, transparent 60%),
      radial-gradient(900px 300px at 90% 120%, #e0f2fe 0%, transparent 60%);
      border-bottom:1px solid #e5e7eb
    }
    .hero-inner{max-width:var(--maxw);margin:0 auto;display:grid;gap:28px;grid-template-columns:1.1fr .9fr;align-items:center;padding:38px 20px}
    .hero ul{margin:12px 0 0;padding:0;list-style:none;display:grid;gap:8px}
    .hero li{display:flex;align-items:center;gap:10px}
    .hero .check{width:20px;height:20px;border-radius:6px;background:#059669;display:inline-grid;place-items:center;color:#fff;font-size:14px}
    .hero-visual{aspect-ratio:4/3;border-radius:18px;background:linear-gradient(180deg,#f8fafc,#f1f5f9);border:1px solid #e5e7eb;box-shadow:var(--shadow);display:grid;place-items:center}
    .hero-cta{display:flex;gap:12px;flex-wrap:wrap;margin-top:16px}

    /* subject tiles */
    .tiles{margin-top:12px}
    .tile{padding:14px;border-radius:12px;border:1px solid #e5e7eb;background:#fff}
    .tile h4{margin:0 0 4px}
    .tile small{color:var(--muted)}

    /* steps */
    .steps{counter-reset:step}
    .step .kicker{
      display:inline-flex;align-items:center;justify-content:center;
      width:34px;height:34px;border-radius:10px;font-weight:700;background:var(--accent);
      color:#fff;margin-bottom:8px;box-shadow:0 0 0 6px var(--ring)
    }

    /* testimonials */
    .testimonials{overflow:hidden}
    .quote{display:flex;gap:14px}
    .quote .avatar{width:46px;height:46px;border-radius:50%;background:#e2e8f0;flex:0 0 46px;display:grid;place-items:center;font-weight:800;color:#0f172a}
    .quote .bubble{background:#fff;border:1px solid #e5e7eb;border-radius:16px;padding:12px 14px;box-shadow:0 1px 0 rgba(0,0,0,.03)}
    .quote footer{margin-top:6px;color:var(--muted);font-size:.95em}

    /* forms */
    form{display:grid;gap:12px}
    .field{display:grid;gap:6px}
    .field label{font-weight:700}
    .field input,.field select,.field textarea{
      width:100%;padding:12px 12px;border-radius:12px;border:1px solid #cbd5e1;background:#fff;
      font:inherit;line-height:1.5
    }
    .field input:focus,.field select:focus,.field textarea:focus{outline:2px solid var(--accent);outline-offset:2px}
    .help{font-size:.92em;color:var(--muted)}

    /* sticky actions (mobile) */
    .sticky-cta{
      position:fixed;inset:auto 0 12px;display:flex;justify-content:center;z-index:60;
      pointer-events:none
    }
    .sticky-cta .tray{
      pointer-events:auto;display:flex;gap:10px;padding:10px;background:rgba(255,255,255,.92);
      border:1px solid #e5e7eb;border-radius:999px;backdrop-filter:blur(8px);box-shadow:var(--shadow)
    }
    .fab{display:inline-flex;align-items:center;justify-content:center;width:44px;height:44px;border-radius:50%;border:1px solid #cbd5e1;background:#fff;text-decoration:none}
    .fab:focus-visible{outline:2px solid var(--accent);outline-offset:2px}
    .sr-only{position:absolute;width:1px;height:1px;padding:0;margin:-1px;overflow:hidden;clip:rect(0,0,0,0);white-space:nowrap;border:0}

    /* footer */
    footer{background:#0b1520;color:#cbd5e1;margin-top:36px}
    footer a{color:#93c5fd}
    .footer-inner{max-width:var(--maxw);margin:0 auto;padding:24px 20px}
  </style>
</head>
<body>
  <!-- ====== Sticky / translucent header ====== -->
  <header class="topbar" role="banner">
    <div class="topbar-inner">
      <div class="brand">
        <div class="brand-logo" aria-hidden="true">Σ</div>
        <div>
          <b>SG MATHS TUITION</b><br>
          <span class="badge">Secondary &amp; JC</span>
        </div>
      </div>
      <nav class="nav" aria-label="Primary">
        <a href="#home">Home</a>
        <a href="#subjects">Subjects</a>
        <a href="#about">About</a>
        <a href="#credentials">Credentials</a>
        <a href="#challenges">Challenges</a>
        <a href="#solutions">Solutions</a>
        <a href="#process">Process</a>
        <a href="#testimonials">Testimonials</a>
        <a href="#contact">Contact</a>
      </nav>
      <div class="menu">
        <div class="contact-links" aria-label="Direct contact">
          <a href="tel:+6592997306" aria-label="Call +65 9299 7306">
            <svg class="icon" viewBox="0 0 24 24" fill="none" aria-hidden="true"><path d="M3 5.5A2.5 2.5 0 0 1 5.5 3h1.1a1.5 1.5 0 0 1 1.48 1.25l.5 2.99a1.5 1.5 0 0 1-.86 1.63l-1.2.54a13 13 0 0 0 7.47 7.47l.54-1.2a1.5 1.5 0 0 1 1.63-.86l2.99.5A1.5 1.5 0 0 1 21 17.4v1.1A2.5 2.5 0 0 1 18.5 21h-.75C9.46 21 3 14.54 3 6.25V5.5Z" stroke="currentColor" stroke-width="1.6"/></svg>
            +65&nbsp;9299&nbsp;7306
          </a>
          <a href="mailto:ngejaysuss@gmail.com" aria-label="Email ngejaysuss@gmail.com">
            <svg class="icon" viewBox="0 0 24 24" fill="none" aria-hidden="true"><path d="M3 6.75A1.75 1.75 0 0 1 4.75 5h14.5A1.75 1.75 0 0 1 21 6.75v10.5A1.75 1.75 0 0 1 19.25 19H4.75A1.75 1.75 0 0 1 3 17.25V6.75Z" stroke="currentColor" stroke-width="1.6"/><path d="m4 7 8 6 8-6" stroke="currentColor" stroke-width="1.6"/></svg>
            Email
          </a>
          <a class="btn btn-primary" href="#contact">Enrol now</a>
        </div>
        <button class="btn btn-ghost menu-toggle" aria-controls="mobile-menu" aria-expanded="false">Menu</button>
      </div>
    </div>
    <div id="mobile-menu" class="mobile-menu" hidden>
      <a href="#home">Home</a>
      <a href="#subjects">Subjects</a>
      <a href="#about">About</a>
      <a href="#credentials">Credentials</a>
      <a href="#challenges">Challenges</a>
      <a href="#solutions">Solutions</a>
      <a href="#process">Process</a>
      <a href="#testimonials">Testimonials</a>
      <a href="#contact">Contact</a>
    </div>
  </header>

  <!-- ====== Hero ====== -->
  <section id="home" class="hero" aria-labelledby="hero-title">
    <div class="hero-inner">
      <div>
        <div class="eyebrow">Ace Secondary &amp; JC Math with Personalised Tutoring</div>
        <h1 id="hero-title">Excel in Math with Dr. Ng — 16+ Years of Teaching</h1>
        <p class="lead">One-on-one and small group tuition built for clarity, confidence, and exam performance.</p>
        <ul>
          <li><span class="check" aria-hidden="true">✓</span> Secondary: E-Math &amp; A-Math (including IP)</li>
          <li><span class="check" aria-hidden="true">✓</span> Junior College: H1, H2, and H3 Mathematics</li>
        </ul>
        <div class="hero-cta">
          <a class="btn btn-primary" href="#contact">Secure your slot</a>
          <a class="btn btn-ghost" href="#subjects">View subjects</a>
        </div>
      </div>
      <div class="hero-visual" aria-hidden="true">
        <div class="wrap-small" style="text-align:center;padding:20px">
          <svg viewBox="0 0 200 120" width="90%" aria-hidden="true">
            <rect x="1" y="1" width="198" height="118" rx="14" fill="white" stroke="#cbd5e1"/>
            <g font-family="ui-monospace, SFMono-Regular, Menlo, monospace" font-size="10" fill="#0f172a">
              <text x="12" y="24">f(x)=x²+2x+1</text>
              <text x="12" y="42">∫₀¹ (3x²+5) dx</text>
              <text x="12" y="60">limₓ→0 sin x / x</text>
              <text x="12" y="78">P(A|B)=P(A∩B)/P(B)</text>
              <text x="12" y="96">e^{iπ}+1=0</text>
            </g>
          </svg>
        </div>
      </div>
    </div>
  </section>

  <!-- ====== Subjects ====== -->
  <section id="subjects" class="wrap" aria-labelledby="subjects-title">
    <div class="eyebrow">Subjects we teach</div>
    <h2 id="subjects-title">Secondary &amp; Junior College</h2>
    <div class="grid grid-2 tiles" role="list">
      <article class="tile" role="listitem">
        <h4>Secondary School</h4>
        <small>E-Math, A-Math for Secondary &amp; IP Levels</small>
      </article>
      <article class="tile" role="listitem">
        <h4>Junior College</h4>
        <small>H1, H2, and H3 Mathematics</small>
      </article>
    </div>
  </section>

  <!-- ====== About / Tutor Bio ====== -->
  <section id="about" class="wrap" aria-labelledby="about-title">
    <div class="eyebrow">Meet your tutor</div>
    <h2 id="about-title">Dr. Ng</h2>
    <p class="lead">Full-time private tutor with a passion for teaching Mathematics. Ph.D. in Mathematics (NUS) and First Class Honours graduate. Tutoring since 2002, helping students from Secondary to JC build durable skills.</p>
    <p class="muted">This combination of deep academic knowledge and long-term, hands-on experience allows me to pinpoint needs quickly and guide students to genuine understanding.</p>
  </section>

  <!-- ====== Credentials ====== -->
  <section id="credentials" class="wrap" aria-labelledby="cred-title">
    <div class="eyebrow">My academic credentials</div>
    <h2 id="cred-title">Qualified, ethical, student-first</h2>
    <div class="grid grid-2">
      <div class="card">
        <ul>
          <li>Ph.D. in Mathematics, National University of Singapore (NUS)</li>
          <li>B.Sc. First Class Honours in Mathematics, NUS</li>
          <li>GPA 4.9 / 5.0 (Dean's List 4 semesters)</li>
          <li>Associate Faculty, Singapore University of Social Sciences (SUSS)</li>
        </ul>
      </div>
      <div class="card">
        <h4>Professional integrity</h4>
        <p>I do not accept my own SUSS students for paid private tuition to avoid any conflict of interest. My commitment is to ethical, effective teaching.</p>
      </div>
    </div>
  </section>

  <!-- ====== Challenges (kept from original file) ====== -->
  <section class="wrap" aria-labelledby="challenges">
    <div class="eyebrow">Why students plateau (15+ years' observations)</div>
    <h2 id="challenges">3 reasons why students in Upper Sec and JC are held back</h2>
    <div class="grid grid-3" role="list">
      <article class="card" role="listitem">
        <h3>Challenge 1: Mastering the basics</h3>
        <p>Gaps in core concepts make it hard to keep up with new (and harder) material.</p>
      </article>
      <article class="card" role="listitem">
        <h3>Challenge 2: Large classroom learning</h3>
        <p>In big groups, personalisation is limited and individual learning styles get lost.</p>
      </article>
      <article class="card" role="listitem">
        <h3>Challenge 3: Exam application under pressure</h3>
        <p>Students understand after revision but drop marks under pressure or on novel questions.</p>
      </article>
    </div>
  </section>

  <!-- ====== Solutions (kept / refined) ====== -->
  <section id="solutions" class="wrap" aria-labelledby="solutions-title">
    <div class="eyebrow">What we do differently</div>
    <h2 id="solutions-title">An approach that directly addresses these challenges</h2>
    <p class="lead">More than exam prep: we build the foundations and habits that support future learning.</p>
    <div class="grid grid-3" role="list">
      <article class="card" role="listitem">
        <h3>Solution 1: Expert-led clarity</h3>
        <p>Concepts are explained in plain language, broken down and scaffolded with step-by-step examples.</p>
      </article>
      <article class="card" role="listitem">
        <h3>Solution 2: Personalised &amp; guided</h3>
        <p>Small classes with individual attention. We identify and close gaps early with a tailored plan.</p>
      </article>
      <article class="card" role="listitem">
        <h3>Solution 3: Supportive space</h3>
        <p>We emphasise progress and confidence. Students feel safe to ask questions and engage deeply.</p>
      </article>
    </div>
  </section>

  <!-- ====== Process (4 steps) ====== -->
  <section id="process" class="wrap" aria-labelledby="process-title">
    <div class="eyebrow">Your path to mastery</div>
    <h2 id="process-title">A proven 4-step process</h2>
    <div class="grid grid-4 steps" role="list">
      <article class="step" role="listitem">
        <div class="kicker" aria-hidden="true">1</div>
        <h4>Discovery</h4>
        <p>We map strengths, current knowledge, and the exact gaps holding you back.</p>
      </article>
      <article class="step" role="listitem">
        <div class="kicker" aria-hidden="true">2</div>
        <h4>Custom-built lessons</h4>
        <p>Lessons target weak points and create small, compounding wins each week.</p>
      </article>
      <article class="step" role="listitem">
        <div class="kicker" aria-hidden="true">3</div>
        <h4>Guided mastery</h4>
        <p>We master difficult topics together, ensuring understanding before moving on.</p>
      </article>
      <article class="step" role="listitem">
        <div class="kicker" aria-hidden="true">4</div>
        <h4>Lock it in</h4>
        <p>We review and practise consistently so gains stick for exam day.</p>
      </article>
    </div>
  </section>

  <!-- ====== Testimonials ====== -->
  <section id="testimonials" class="wrap" aria-labelledby="t-title">
    <div class="eyebrow">What students say</div>
    <h2 id="t-title">Real improvements in grades and confidence</h2>
    <div class="grid grid-3">
      <article class="quote">
        <div class="avatar" aria-hidden="true">CS</div>
        <div class="bubble">
          <p>Math went from a constant struggle to consistent B/A-range performances after targeted tutoring.</p>
          <footer>— <b>Chui Si Min</b></footer>
        </div>
      </article>
      <article class="quote">
        <div class="avatar" aria-hidden="true">VT</div>
        <div class="bubble">
          <p>Clear explanations and focus on fundamentals made complex topics finally make sense.</p>
          <footer>— <b>Vivienne Tay</b></footer>
        </div>
      </article>
      <article class="quote">
        <div class="avatar" aria-hidden="true">DL</div>
        <div class="bubble">
          <p>Lessons are objective-driven and calm. Problems are unpacked and solved step by step.</p>
          <footer>— <b>Dalon Lee</b></footer>
        </div>
      </article>
    </div>
  </section>

  <!-- ====== Contact / Enrol ====== -->
  <section id="contact" class="wrap" aria-labelledby="contact-title">
    <div class="eyebrow">Secure your slot</div>
    <h2 id="contact-title">Limited intake each semester</h2>
    <p class="lead">Due to the personalised nature of tuition, I take on a limited number of students. Enquire now.</p>
    <form aria-label="Enrolment form" onsubmit="enrol(event)">
      <div class="grid grid-2">
        <div class="field">
          <label for="name">Full name</label>
          <input id="name" name="name" autocomplete="name" required placeholder="e.g., Tan Wei Ming" />
        </div>
        <div class="field">
          <label for="phone">Contact number</label>
          <input id="phone" name="phone" inputmode="tel" autocomplete="tel" placeholder="+65 ..." />
        </div>
        <div class="field">
          <label for="email">Email</label>
          <input id="email" name="email" type="email" autocomplete="email" required placeholder="you@example.com" />
        </div>
        <div class="field">
          <label for="subject">Subject / Level</label>
          <select id="subject" name="subject" required>
            <option value="" selected disabled>Select subject</option>
            <option>H1 Math</option>
            <option>H2 Math</option>
            <option>H3 Math</option>
            <option>Sec 3 E Math</option>
            <option>Sec 3 A Math</option>
            <option>Sec 4 E Math</option>
            <option>Sec 4 A Math</option>
          </select>
        </div>
      </div>
      <div class="field">
        <label for="message">Your message</label>
        <textarea id="message" name="message" rows="5" placeholder="Tell me a bit about your goals and current challenges..."></textarea>
      </div>
      <div style="display:flex;gap:10px;flex-wrap:wrap;align-items:center">
        <button class="btn btn-primary" type="submit">Secure my slot</button>
        <a class="btn btn-ghost" href="https://wa.me/6592997306" target="_blank" rel="noopener">Chat on WhatsApp</a>
        <span class="help">Prefer email? <a href="mailto:ngejaysuss@gmail.com">Send an email</a>.</span>
      </div>
    </form>
  </section>

  <!-- ====== Sticky mobile actions ====== -->
  <div class="sticky-cta" aria-hidden="false">
    <div class="tray" role="group" aria-label="Quick actions">
      <a class="fab" href="tel:+6592997306" title="Call">
        <span class="sr-only">Call</span>
        <svg viewBox="0 0 24 24" fill="none" aria-hidden="true" width="22" height="22"><path d="M3 5.5A2.5 2.5 0 0 1 5.5 3h1.1a1.5 1.5 0 0 1 1.48 1.25l.5 2.99a1.5 1.5 0 0 1-.86 1.63l-1.2.54a13 13 0 0 0 7.47 7.47l.54-1.2a1.5 1.5 0 0 1 1.63-.86l2.99.5A1.5 1.5 0 0 1 21 17.4v1.1A2.5 2.5 0 0 1 18.5 21h-.75C9.46 21 3 14.54 3 6.25V5.5Z" stroke="currentColor" stroke-width="1.6"/></svg>
      </a>
      <a class="fab" href="https://wa.me/6592997306" title="WhatsApp" target="_blank" rel="noopener">
        <span class="sr-only">WhatsApp</span>
        <svg viewBox="0 0 24 24" fill="none" aria-hidden="true" width="22" height="22"><path d="M12 2a10 10 0 0 0-8.7 14.9L2 22l5.3-1.3A10 10 0 1 0 12 2Z" stroke="currentColor" stroke-width="1.6"/></svg>
      </a>
      <a class="fab" href="#contact" title="Enrol form">
