{"name":"Electroweak-Symmetric-Universe.html","type":"code/html","content":"<!doctype html>
<html lang="en">
<head>
  <meta charset="utf-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <title>When the Universe Had No Rest Mass — The Electroweak‑Symmetric Era</title>
  <meta name="description" content="A teaching‑style guide to the early universe when the Higgs vev was zero and all Standard Model particles had vanishing rest mass. Includes definitions, key equations, pitfalls, and an interactive calculator." />
  <meta name="color-scheme" content="light dark" />
  <style>
    :root {
      --bg: #0b0c10;
      --fg: #e6e6e6;
      --muted: #9aa3ad;
      --ink: #0f172a;
      --paper: #ffffff;
      --accent: #6ee7b7;
      --accent-2: #60a5fa;
      --ring: rgba(94, 234, 212, 0.35);
      --callout: #1f2937;
      --border: #2b2f36;
      --code: #111827;
      --shadow: 0 10px 30px rgba(0,0,0,.25);
    }
    @media (prefers-color-scheme: light) {
      :root {
        --bg: #f6f7fb;
        --fg: #111827;
        --muted: #4b5563;
        --ink: #0b1020;
        --paper: #ffffff;
        --accent: #0ea5e9;
        --accent-2: #22c55e;
        --ring: rgba(14,165,233,0.25);
        --callout: #eef2ff;
        --border: #e5e7eb;
        --code: #0b1020;
      }
    }

    * { box-sizing: border-box; }
    html, body { height: 100%; }
    body {
      margin: 0;
      font-family: ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Ubuntu, Cantarell, Noto Sans, Helvetica, Arial, "Apple Color Emoji", "Segoe UI Emoji";
      color: var(--fg);
      background: radial-gradient(1200px 600px at 80% -20%, rgba(96,165,250,.08), transparent 60%),
                  radial-gradient(800px 400px at 10% -10%, rgba(34,197,94,.08), transparent 60%),
                  var(--bg);
      line-height: 1.6;
    }

    .container { max-width: 1100px; margin: 0 auto; padding: 2rem 1.25rem 6rem; }
    header.hero {
      display: grid;
      grid-template-columns: 1.2fr 1fr;
      gap: 2rem;
      align-items: center;
    }
    header.hero h1 { font-size: clamp(1.8rem, 3.6vw, 3rem); line-height: 1.1; margin: 0 0 .6rem; }
    header.hero p.lede { font-size: clamp(1rem, 2.2vw, 1.2rem); color: var(--muted); margin: 0; }

    .card {
      background: var(--paper);
      border: 1px solid var(--border);
      border-radius: 16px;
      box-shadow: var(--shadow);
      padding: 1.2rem 1.4rem;
    }

    nav.toc { margin: 2rem 0 1rem; }
    nav.toc .card { padding: 1rem 1.2rem; }
    nav.toc ol { margin: .4rem 0 0; padding-left: 1.2rem; }
    nav.toc li { margin: .2rem 0; }
    nav.toc a { color: var(--accent); text-decoration: none; }
    nav.toc a:hover { text-decoration: underline; }

    section { margin-top: 2.6rem; }
    section h2 { font-size: clamp(1.3rem, 2.4vw, 1.8rem); line-height: 1.2; margin: 0 0 .8rem; }
    section h3 { font-size: 1.1rem; margin: 1rem 0 .4rem; }
    section p { margin: .5rem 0; }

    .grid-2 { display: grid; grid-template-columns: 1fr 1fr; gap: 1rem; }
    @media (max-width: 880px) { .grid-2 { grid-template-columns: 1fr; } header.hero { grid-template-columns: 1fr; } }

    .callout { border-left: 4px solid var(--accent); background: var(--callout); padding: .9rem 1rem; border-radius: 10px; }
    .callout h4 { margin: 0 0 .3rem; font-size: 1rem; }

    .pill { display: inline-block; padding: .15rem .6rem; border-radius: 999px; font-size: .85rem; border: 1px solid var(--border); background: rgba(148,163,184,.12); }

    .eq { font-family: ui-monospace, SFMono-Regular, Menlo, Consolas, "Liberation Mono", monospace; background: rgba(148,163,184,.12); padding: .12rem .35rem; border-radius: 6px; }

    .kbd { font-family: ui-monospace, SFMono-Regular, Menlo, Consolas, monospace; background: rgba(148,163,184,.18); padding: .15rem .4rem; border: 1px solid var(--border); border-bottom-width: 2px; border-radius: 6px; }

    .note { color: var(--muted); font-size: .95rem; }

    .list-check { list-style: none; padding-left: 0; }
    .list-check li { margin: .25rem 0; padding-left: 1.6rem; position: relative; }
    .list-check li:before { content: "✔"; position: absolute; left: 0; top: 0; color: var(--accent-2); }

    details { border: 1px solid var(--border); border-radius: 10px; padding: .6rem .8rem; background: var(--paper); }
    details + details { margin-top: .6rem; }
    details summary { cursor: pointer; font-weight: 600; }

    /* Interactive panel */
    .panel { display: grid; gap: 1rem; grid-template-columns: 1.2fr 1fr; }
    .panel .inputs .row { display: grid; grid-template-columns: 1fr auto; gap: .6rem; align-items: center; }
    .panel .inputs label { font-size: .95rem; }
    .panel .inputs input[type="range"] { width: 100%; }
    .panel .inputs input[type="number"] { width: 120px; padding: .35rem .5rem; border-radius: 8px; border: 1px solid var(--border); background: var(--paper); color: var(--fg); }
    .panel .outputs { display: grid; gap: .55rem; }
    .kv { display: grid; grid-template-columns: 1.2fr 1fr; gap: .5rem; align-items: baseline; }
    .kv code { user-select: all; }

    footer { margin-top: 3rem; padding-top: 1.2rem; border-top: 1px solid var(--border); font-size: .9rem; color: var(--muted); }
  </style>
  <script>
    // Simple interactive physics panel — no external libraries
    addEventListener('DOMContentLoaded', () => {
      const elT = document.getElementById('T');
      const elTn = document.getElementById('Tn');
      const elG = document.getElementById('gstar');
      const elGg = document.getElementById('gauge');

      const out = {
        Tk: document.getElementById('Tk'),
        rhoSI: document.getElementById('rhoSI'),
        rhoNat: document.getElementById('rhoNat'),
        H: document.getElementById('H'),
        t: document.getElementById('t'),
        horizon: document.getElementById('horizon'),
        horizon_mm: document.getElementById('horizon_mm'),
        mthW: document.getElementById('mthW'),
        deb: document.getElementById('deb')
      };

      const C = 299792458;                 // m/s
      const KB = 1.380649e-23;             // J/K
      const HBAR = 1.054571817e-34;        // J*s
      const G = 6.67430e-11;               // m^3 kg^-1 s^-2
      const J_PER_GeV = 1.602176634e-10;   // J/GeV
      const GeV_TO_K = 1.160451812e13;     // K per GeV

      function fmt(x, unit="") {
        if (!isFinite(x)) return '—';
        let v = x, suffix = '';
        const abs = Math.abs(v);
        if (abs >= 1e12 || (abs > 0 && abs < 1e-3)) {
          // scientific
          return v.toExponential(3) + (unit ? ' ' + unit : '');
        } else if (abs >= 1e9) { v/=1e9; suffix='G'; }
        else if (abs >= 1e6) { v/=1e6; suffix='M'; }
        else if (abs >= 1e3) { v/=1e3; suffix='k'; }
        return v.toLocaleString(undefined, {maximumFractionDigits: 3}) + ' ' + suffix + (unit?unit:'');
      }

      function compute() {
        const TGeV = parseFloat(elT.value);
        elTn.value = TGeV;
        const gstar = parseFloat(elG.value);
        const g = parseFloat(elGg.value);

        const TK = TGeV * GeV_TO_K; // Kelvin

        // Energy density (SI): rho = (pi^2/30) g* (k_B T)^4 / (ħ^3 c^5)
        const rho = (Math.PI*Math.PI/30) * gstar * Math.pow(KB*TK, 4) / (Math.pow(HBAR,3)*Math.pow(C,5)); // J/m^3

        // Hubble rate: H^2 = (8πG/3) rho / c^2  (since rho is energy density)
        const H = Math.sqrt( (8*Math.PI*G/3) * (rho / (C*C)) ); // s^-1

        const t = 1/(2*H);           // s (radiation era)
        const horizon = C * t;       // m

        // Natural units outputs
        const rhoNat = (Math.PI*Math.PI/30) * gstar * Math.pow(TGeV, 4); // GeV^4

        // Thermal (screening) mass scale ~ g T (in GeV)
        const mth = g * TGeV; // GeV

        // Debye length ~ ħ c / (g k_B T)
        const deb = (HBAR * C) / (g * KB * TK); // meters

        out.Tk.textContent = fmt(TK, 'K');
        out.rhoSI.textContent = fmt(rho, 'J/m³');
        out.rhoNat.textContent = fmt(rhoNat, 'GeV⁴');
        out.H.textContent = fmt(H, 's⁻¹');
        out.t.textContent = fmt(t, 's');
        out.horizon.textContent = fmt(horizon, 'm');
        out.horizon_mm.textContent = fmt(horizon*1e3, 'mm');
        out.mthW.textContent = fmt(mth, 'GeV');
        out.deb.textContent = fmt(deb, 'm');
      }

      elT.addEventListener('input', compute);
      elTn.addEventListener('change', e => { elT.value = e.target.value; compute(); });
      elG.addEventListener('input', compute);
      elGg.addEventListener('input', compute);

      compute();
    });
  </script>
