      radial-gradient(1200px 500px at 80% -10%, rgba(255,190,120,.16), transparent 70%),
      radial-gradient(900px 420px at -10% 0%, rgba(255,205,160,.10), transparent 65%),
      linear-gradient(180deg, color-mix(in oklab, var(--bg-warm) 92%, black 8%), var(--bg-warm-dark));
    isolation:isolate;
  }
  .credentials::before{
    content:""; position:absolute; inset:0;
    background: linear-gradient(180deg, rgba(35,18,10,.18), rgba(35,18,10,.42));
    mix-blend-mode: multiply; pointer-events:none; z-index:0;
  }
  .cred-inner{
    position:relative; z-index:1;
    padding: clamp(16px, 2.4vw, 26px) clamp(16px, 3vw, 28px);
  }

  /* Headline + subline use only system fonts */
  .cred-title{
    font-family: Georgia, "Times New Roman", Times, serif;
    font-weight:700;
    letter-spacing:.3px;
    font-size: clamp(var(--title-min), 2.4vw + 20px, var(--title-max));
    line-height:1.06;
    text-align:center;
    color: var(--text);
    text-shadow: 0 1px 0 rgba(0,0,0,.15);
    margin: 0 0 .25rem 0;
  }
  .cred-sub{
    text-align:center;
    color: var(--text-soft);
    font-size: clamp(14px, 1.3vw + 10px, 17px);
    max-width: 60ch;
    margin: 0 auto clamp(12px, 2vw, 18px);
    text-shadow: 0 1px 0 rgba(0,0,0,.12);
  }

  /* Grid: desktop 4, tablet 2, mobile 1 */
  .cred-grid{
    display:grid;
    grid-template-columns: repeat(4, 1fr); /* desktop */
    gap: clamp(12px, 1.6vw, 18px);
  }
  @media (max-width: 1024px){           /* tablet */
    .cred-grid{ grid-template-columns: repeat(2, 1fr); }
  }
  @media (max-width: 640px){            /* mobile */
    .cred-grid{ grid-template-columns: 1fr; }
  }

  .cred-item{
    display:flex; flex-direction:column; align-items:center; text-align:center;
    padding: clamp(10px, 1.6vw, 16px);
    border-radius: 16px;
    background: linear-gradient(180deg, rgba(255,255,255,.045), rgba(255,255,255,.02));
    outline: 1px solid rgba(255,255,255,.08);
    backdrop-filter: blur(1.5px);
  }

  .icon{ width: clamp(44px, 5.5vw, 64px); margin-bottom: clamp(6px, 1vw, 10px); }
  .icon svg{ width:100%; height:auto; display:block; }
  .award-stroke{ stroke:#fff; opacity:.95; }
  .award-fill{ fill:#fff; opacity:.96; }

  .cred-text{
    font-family: Georgia, "Times New Roman", Times, serif;
    font-weight:700;
    color: var(--text);
    /* only mild downscaling across breakpoints */
    font-size: clamp(var(--body-min), 1.2vw + 16px, var(--body-max));
    line-height:1.18;
    letter-spacing:.15px;
    text-shadow: 0 1px 0 rgba(0,0,0,.2);
    hyphens: manual;
  }
  /* Desktop & tablet: intentional line breaks */
  .cred-text .line{ display:block; }
  /* Mobile: collapse lines into a single sentence (normal word wrap) */
  @media (max-width: 640px){
    .cred-text{ line-height:1.25; hyphens: none; word-break: normal; overflow-wrap: normal; }
    .cred-text .line{ display:inline; }
    .cred-text .line + .line::before{ content:" "; } /* add spaces between former lines */
  }
</style>
