  <script>
    (function(){
      "use strict";

      const root = document.documentElement;

      // Theme init
      const THEME_KEY = "borrowed-tomorrows-theme";
      function setTheme(t){
        root.setAttribute("data-theme", t);
        try { localStorage.setItem(THEME_KEY, t); } catch(_) {}
      }
      function getPreferredTheme(){
        try {
          const saved = localStorage.getItem(THEME_KEY);
          if (saved === "light" || saved === "dark") return saved;
        } catch(_) {}
        const prefersDark = window.matchMedia && window.matchMedia("(prefers-color-scheme: dark)").matches;
        return prefersDark ? "dark" : "light";
      }
      setTheme(getPreferredTheme());

      // Minimal Markdown renderer for this page (headings, paragraphs, emphasis, blockquotes, ordered lists, hr, hard line breaks)
      function escapeHtml(s){
        return s
          .replace(/&/g, "&amp;")
          .replace(/</g, "&lt;")
          .replace(/>/g, "&gt;")
          .replace(/"/g, "&quot;");
      }

      function inline(md){
        let s = escapeHtml(md);

        // bold first
        s = s.replace(/\*\*(.+?)\*\*/g, "<strong>$1</strong>");

        // italics (avoid **)
        s = s.replace(/(^|[^*])\*(?!\*)([^*]+?)\*(?!\*)/g, "$1<em>$2</em>");

        // curly quotes are already in the text; leave them alone
        return s;
      }

      function renderMarkdown(md, stripFirstHeading){
        const text = md.replace(/\r\n?/g, "\n");
        let src = text;

        if (stripFirstHeading){
          // Remove a single leading H1 line and any following blank lines.
          src = src.replace(/^\s*#\s+.*\n+/, "");
        }

        const lines = src.split("\n");
        let out = "";
        let para = [];

        function flushPara(){
          if (!para.length) return;
          const raw = para.join("\n").trimEnd();
          const html = inline(raw).replace(/\n/g, "<br>");
          // Heuristic: multi-line bold/status blocks render better as a console card.
          const isStatusBlock =
            para.length > 1 &&
            para.every(l => l.trim().startsWith("**") || l.trim().startsWith("> **") || l.trim().startsWith(">**"));

          if (isStatusBlock){
            out += `<div class="console" role="note">${html}</div>`;
          } else {
            out += `<p>${html}</p>`;
          }
          para = [];
        }

        function renderBlocks(blockMd){
          // Nested render without stripping headings
          return renderMarkdown(blockMd, false);
        }

        for (let i = 0; i < lines.length; i++){
          const line = lines[i];

          // blank line => paragraph break
          if (line.trim() === ""){
            flushPara();
            continue;
          }

          // horizontal rule
          if (/^---\s*$/.test(line.trim())){
            flushPara();
            out += "<hr>";
            continue;
          }

          // blockquote
          if (/^>\s?/.test(line)){
            flushPara();
            const q = [];
            while (i < lines.length && /^>\s?/.test(lines[i])){
              q.push(lines[i].replace(/^>\s?/, ""));
              i++;
            }
            i--; // compensate for for-loop increment
            const inner = q.join("\n");
            out += `<blockquote class="callout">${renderBlocks(inner)}</blockquote>`;
            continue;
          }

          // ordered list
          if (/^\d+\.\s+/.test(line)){
            flushPara();
            const items = [];
            while (i < lines.length && /^\d+\.\s+/.test(lines[i])){
              items.push(lines[i].replace(/^\d+\.\s+/, ""));
              i++;
            }
            i--;
            out += "<ol>";
            for (const it of items){
              out += `<li>${inline(it)}</li>`;
            }
            out += "</ol>";
            continue;
          }

          // headings (rare here; safe)
          const h = line.match(/^(#{1,6})\s+(.+)$/);
          if (h){
            flushPara();
            const level = Math.min(6, h[1].length + 1); // keep page title in header; story headings start at h2
            out += `<h${level}>${inline(h[2])}</h${level}>`;
            continue;
          }

          // normal paragraph line
          para.push(line);
        }

        flushPara();
        return out;
      }

      // Render story
      const sourceEl = document.getElementById("storySource");
      const targetEl = document.getElementById("read");

      if (sourceEl && targetEl){
        const md = sourceEl.textContent || "";
        const html = renderMarkdown(md, true);
        targetEl.innerHTML = html;

        // Hide the raw source now that we have a formatted version
        sourceEl.hidden = true;

        // Reading meta
        const text = targetEl.textContent || "";
        const words = (text.trim().match(/\S+/g) || []).length;
        const minutes = Math.max(1, Math.round(words / 220));
        const meta = document.getElementById("readingMeta");
        if (meta){
          meta.textContent = `${minutes} min read • ${words.toLocaleString()} words`;
        }
      }

      // Progress bar
      const bar = document.getElementById("progressBar");
      function updateProgress(){
        if (!bar) return;
        const doc = document.documentElement;
        const max = Math.max(1, doc.scrollHeight - window.innerHeight);
        const p = Math.min(1, Math.max(0, window.scrollY / max));
        bar.style.transform = `scaleX(${p})`;
      }
      updateProgress();
      window.addEventListener("scroll", updateProgress, { passive: true });
      window.addEventListener("resize", updateProgress);

      // Controls
      const themeBtn = document.getElementById("themeToggle");
      const printBtn = document.getElementById("printBtn");
      const topBtn = document.getElementById("toTopBtn");

      if (themeBtn){
        themeBtn.addEventListener("click", function(){
          const current = root.getAttribute("data-theme") === "dark" ? "dark" : "light";
          setTheme(current === "dark" ? "light" : "dark");
        });
      }
      if (printBtn){
        printBtn.addEventListener("click", function(){ window.print(); });
      }
      if (topBtn){
        topBtn.addEventListener("click", function(){
          window.scrollTo({ top: 0, behavior: "smooth" });
        });
      }
    })();
  </script>
